css:
@-moz-document domain("gammal.rollspel.nu") {
:root {
    --color-white: #fff;
    --color-black: #121212;
    --color-dark-blue: #033145;
    --color-light-blue: rgb(211, 236, 252);
    --color-light-blue-transparency: rgba(40, 171, 255, 0.2);
    --color-primary: rgb(28, 97, 145);
    --color-link: var(--color-primary);
    --color-hover: #2379B5;
    --color-dark-grey: #656A6D;
    --color-grey: rgb(189, 189, 189);
    --color-light-grey: #f5f5f5;
    --color-warning: #a20000;
    --color-notice: #FCF7D7;
    --color-text: #fff;
    
    --spacing: 1rem;
    --spacing-quarter: 0.25rem;
    --spacing-half: 0.5rem;
    --spacing-double: 2rem;
    --spacing-quadruple: 4rem;
    
    --right-sidebar-width: 300px;
    --header-height: 36px;
    
    --font-size-small: 12px;
    --font-size-smallest: 10px;
    
    --border-radius: 5px;
    
    --transition-duration-fast: 100ms;
    --transition-duration-slow: 300ms;
}

*:focus {   /* removes selection marker from all elements */
    outline: none;
}


html > body#vb-page-body {      /* To no affect the input for creating a new post, which is in an iframe */
    padding: 0px;
    margin: 0px;
    letter-spacing: 0.1px;
    background: var(--color-dark-blue);    
}

#outer-wrapper {
    background: var(--color-white);
    padding: 0px;
    margin: 0px;
}

.category-header,       /* Forum categories */
.category-header > td,  /* Forum categories */
.widget-header,         /* Right top sidebar title */
.search-widget          /* Right bottom sidebar div */
{
    background: none !important;
    border: none !important;
}

#breadcrumbs {          /* URL path to where the user is at */
    margin: initial;
    padding: var(--spacing);
}

#wrapper > #content {   /* Forum list container */
    margin: 0px;
    padding: 0px var(--spacing-double);
    max-width: initial;
}


/* ::: HEADER -- ALL PAGES */

#channel-tabbar,
#header,
#channel-subtabbar,
#footer {
    max-width: initial;
}

.b-top-background__header-mainnav > #channel-tabbar {   /* Top menu */
    height: var(--header-height);
    background: var(--color-dark-blue);
    border-bottom-color: var(--color-dark-blue);
}

.b-top-background__header-mainnav-subnav {
    z-index: 100;
}

#channel-tabbar,
#channel-subtabbar {
    border: none;
}

/* Submenu */

#channel-subtabbar > ul > li.js-shrink-event-child {
    margin: 0px;    
}

#channel-subtabbar .navbar_mark_channels_read {
 	visibility: hidden;
    position: relative;
}

#channel-subtabbar .navbar_mark_channels_read::before {
    visibility: visible;
    position: absolute;
	display: block;
    padding: 0px var(--spacing);
	font-size: 12px;
	color: var(--color-black);
	font-weight: bold;
}

#channel-subtabbar .navbar_mark_channels_read::before {
	content: 'Markera som läst';
}

#channel-subtabbar .navbar_mark_channels_read:hover::before
{
    background-color: var(--color-light-blue);
}


#channel-subtabbar .navbar_newtopics,
#channel-subtabbar .navbar_whos_online,
#channel-subtabbar .navbar_member_list,
#channel-subtabbar .navbar_calendar,
body.logged-out #channel-subtabbar
{
    display: none !important;
}


/* FIXED USER HEADER AT TOP RIGHT */

.b-top-menu__background {
    z-index: 1000;
    position: absolute;
    width: 0px;
    height: 0px;
    right: 0px;
}

.b-top-menu__container {
 	position: fixed !important;
    z-index: 100;
	right: 0;
    width: auto;
    float: none;
    background: var(--color-dark-blue);
}

.b-top-menu__container > ul > li {
    border: none;
}

.b-top-menu__container .notifications-count {
    font-size: 11px;
}

#lnkUsernameMenu img {      /* profile image */
    border-radius: 43%;
    height: 23px;
}

.b-top-menu__container > ul > li > a.b-top-menu__item-link,         /* Notifications */
.b-top-menu__container > ul > li .b-comp-menu-dropdown--headerbar,  /* Messages, Profile */
.b-top-menu__container > ul > li .b-comp-menu-dropdown__trigger,    /* Messages, Profile */
.b-top-menu__container > ul > li.b-top-menu__item,
#channel-tabbar > ul.channel-tabbar-list > li > a       /* Items in main left menu */
{
    height: var(--header-height) !important;
    vertical-align: baseline;
    color: var(--color-text);
    font-size: 12px !important;
    font-weight: bold;
    text-transform: uppercase;
    border: none !important;
}

.b-top-menu__container > ul > li .b-comp-menu-dropdown__trigger,    /* Messages, Profile */
#channel-tabbar > ul.channel-tabbar-list > li > a                   /* Items in main left menu */
{
    padding: 0px var(--spacing);    
}



.b-top-menu__container > ul > li > a.b-top-menu__item-link:hover {
    color: var(--color-text);
}

.b-top-menu__container > ul > li:hover
{
    background-color: var(--color-hover);
}


.b-top-menu__container > ul > li .b-comp-menu-dropdown--open .b-comp-menu-dropdown__trigger     /* Messages, Profile */
{  
    background-color: var(--color-hover);
}

.b-top-menu__container .b-comp-menu-dropdown__content {
    background: rgba(255, 255, 255, 0.95);
}

.b-top-menu__container .b-comp-menu-dropdown__content li.b-comp-menu-dropdown__content-item /* Menu options */
{       
    padding: var(--spacing);
    font-weight: normal;
    text-transform: none;
}


body:not(.logged-out) .b-top-menu__container .b-comp-menu-dropdown__content li.b-comp-menu-dropdown__content-item:hover   /* Menu options */
{
    background-color: var(--color-light-blue-transparency);    
}

.b-top-menu__container .b-comp-menu-dropdown__content li.b-comp-menu-dropdown__content-item--divider,   /* Extra menu options */
.b-top-menu__container .b-comp-menu-dropdown__content li.b-comp-menu-dropdown__content-item--divider ~ li {
    background-color: rgba(0, 0, 0, 0.03);
}


/* LOGO AND SEARCHBAR SECTION */

#header {
    position: absolute;
    height: 0px !important;
    overflow: hidden;
    z-index: 100;
}

#header .search-container {
    width: 0px;
    height: 0px !important;
    margin: 0px !important
}

#header .search-box {
    position: absolute;
    top: calc(var(--header-height) / 2);
    transform: translateY(-50%);
    display: flex;
    right: 37rem;
}

#header .search-box > .search-term-container > input {
    width: 140px;
    transition: width 400ms ease-out;
}

#header .search-box > .search-term-container input:focus {
    width: 320px;
}


/* ::: FOOTER -- ALL PAGES */

#footer-tabbar {
    background: var(--color-dark-blue);
    margin-top: 10rem;
    height: 100%;
}

#footer > #footer-tabbar ul.js-footer-chooser-list > li {  /* footer items */
    height: calc(var(--header-height) * 3);
    line-height: var(--header-height);
    padding-top: var(--spacing);
}

#footer-copyright {
    display: none;
}
}

@-moz-document domain("gammal.rollspel.nu") {
/* ::: RIGHT SIDE BAR -- ALL PAGES */

.topic-list-container .cell-topic-image,                 /* The container for the avatar in front of each topic */
.topic-list-container .cell-icons,                      /* Icons for thread */
.topic-list-container .js-post-icon,                    /* Icons for thread title */
.topic-list-container .views-count                      /* View count */
{
    display: none !important;
}

.js-sectiontype-secondary {
    box-sizing: border-box;
    float: none !important;
}

.js-sectiontype-secondary .widget-header {  /* Header containers for latest posts and latests threads */
    margin-top: 0px !important;
}





/* New Posts */

.topic-list-container,     /* Table */
.topic-list-container .cell-topic
{
    border: none !important;
}

.topic-list-container .topic-item {
    min-height: initial !important;
}

.topic-list-container .topic-wrapper,         /* Topic container */
.topic-list-container .cell-count,            /* Topic information */
.topic-list-container .cell-topic,            /* Topic title */
.topic-list-container .cell-lastpost,
.js-sectiontype-secondary > .canvas-widget    /* Container for sidebar items (latest posts & discussions) */
{
    padding: 0px !important;
}

.topic-list-container .topic-wrapper > span  /* Creator of thread */
{
    font-size: var(--font-size-small);
    font-weight: normal;
}


.topic-list-container .topic-title {
    font-size: 14px !important;
}

.topic-list-container .topic-wrapper { /* Topic container */
    margin-bottom: 0px !important;
}


.module-title {     /* Container for title section */
    margin-bottom: 0px;
}


.js-sectiontype-secondary .module-title {       /* Container for section title */
    width: initial;
}

.js-sectiontype-secondary .module-title h1 {    /* Title for section */
    padding-left: var(--spacing);
    padding-right: 0px;
}


/* Specific forum sections */

.topic-list-container tr     /* Each container for each latest posts */
{
    position: relative;
    cursor: pointer;
    border-radius: var(--border-radius);
    
    transition: background-color var(--transition-duration-slow);
}

.js-sectiontype-secondary.section-1 .topic-list-container tr {
    padding: 0.75rem var(--spacing);    
}

.topic-list-container tr:hover {
    background-color: var(--color-light-blue);
    transition: background-color var(--transition-duration-fast);
}

.topic-list-container .topic-list .topic-item .cell-topic     /* Container for topic and author for latest post; in both side column and forum list */
{
    position: static;
}

.js-sectiontype-secondary > .canvas-widget .topic-list-container .cell-topic .topic-wrapper     /* Topic and author for latest post */
{
    float: none !important;
    font-size: 0;
}

.js-sectiontype-secondary > .canvas-widget .topic-list-container .cell-count        /* Comments and likes container for latest posts */
{
    display: flex;
    justify-content: space-between;
}

.topic-list-container .go-to-first-unread       /* Icon with link to first unread post; in both side column and forum list */
{
    visibility: hidden !important;
    height: 0px;
    width: 0px;
    overflow: hidden;
    /* Remove row breaks that creates space to the next element */
    float: right !important;
}

.topic-list-container .go-to-first-unread::before
{
    visibility: visible;
    content: '';
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    z-index: 100;
}

.topic-list-container .go-to-last-post    /* Icon with link to last post; in both side column and forum list */
{
    visibility: hidden;
}

.topic-list-container .go-to-last-post::before
{
    visibility: visible;
    content: '';
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    z-index: 10;
}


.js-sectiontype-secondary > .canvas-widget .topic-list-container .cell-lastpost /* Last update in last post */
{
    display: flex;
}

.js-sectiontype-secondary > .canvas-widget .topic-list-container .cell-lastpost > *
{
    margin-right: 3px;    
}

.js-sectiontype-secondary > .canvas-widget .topic-list-container .cell-lastpost > span:first-child::after {     /* Date for last post */
    content: ':';
}


/* Latest thread starts */

.js-sectiontype-secondary .conversation-list > li {
    padding: 0.35rem var(--spacing);
}

.js-sectiontype-secondary .conversation-list > li .b-post-sm__title {
    font-weight: normal;
}


/* Show all buttons */

.js-sectiontype-secondary .widget-content .widget-footer {
    margin: 0px;
    padding: var(--spacing);
    width: 100%;
    box-sizing: border-box;
}

.js-sectiontype-secondary .widget-footer > a {
    width: 100%;
}
}

@-moz-document domain("rollspel.nu") {
/* ::: MAIN PAGE -- FORUM SECTION */

#sitebuilder-wrapper,                           /* empty clear-fix container */
#header-axd,                                    /* empty clear-fix container */
#header > .header-cell,                         /* site logo */
.canvas-widget-list.section-2,                  /* static information to user about the forum */
#widget_3 > .widget-header,                     /* site activity above forum list */
.b-top-menu__container .b-icon__arrow-down-s,   /* down arrow in right top user menu */
.forum-list-container .forum-list-header,       /* headline for forums */
.widget-header-divider,                         /* All HR elements */
#content .list-footer,                          /* Mark all forums as read beneath forum categories */
.forum-item > .topics-count,                    /* Number of threads in forum on forum list */
.forum-item > .posts-count,                     /* Number of posts in forum on forum list */
.forum-item > .lastpost,                        /* Information about last post in forum list */
.forum-item .forum-wrapper > .icon,             /* Folder icon in forum list */
.subforum-list .subforum-header,                /* Header for subforums in forum list */
.subforum-list .subforum-info .icon:not(.new),
.subforum-list .subforum-info .counts
{
    display: none !important;
}

#notices {
    margin-top: var(--spacing-double);
}

#notices > li {
    padding: var(--spacing);
}



#canvas-layout-wide-narrow > .canvas-layout-row {   /* Container for forum list and right side bar */
    display: flex;
    height: initial;
}

#canvas-layout-wide-narrow > .canvas-layout-row > .canvas-widget-list {     /* Container for forum list */
    margin-right: 0px;
}

#canvas-layout-wide-narrow > .canvas-layout-row > .canvas-widget-list > div {
    width: 100%;
}

#canvas-layout-wide-narrow > .canvas-layout-row .forum-list-container,
#canvas-layout-wide-narrow > .canvas-layout-row td
{
    border: none;
}

#canvas-layout-wide-narrow > .canvas-layout-row .forum-list-container > tbody {     /* Forum list */
    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
    align-items: stretch;
    width: 100%;
}

#canvas-layout-wide-narrow .category-header {       /* Headline container for each forum section */
    width: 100%;
    height: initial;
}

#canvas-layout-wide-narrow .category-header .category {     /* Headline for each forum section */
    font-size: 18px;
    margin: var(--spacing-quadruple) 0rem var(--spacing-half);
}

/* Forum Categories */

.forum-list-container .forum-item {     /* Container for forum category */
    display: flex;                      /* To get all forum categories to get the same height */
}

.forum-list-container .forum-item > .cell-forum {     /* Each forum category */
    position: relative;
	display: inline-block !important;
    border-width: 0px !important;
    
    width: var(--right-sidebar-width) !important;
    box-sizing: border-box;
    margin: var(--spacing);
    padding: var(--spacing-double) !important;
    border-radius: var(--border-radius);
    cursor: pointer;
    
    transition: background-color var(--transition-duration-slow);
}

.forum-list-container .forum-item.new > .cell-forum {   /* New posts in forum */
    background-color: var(--color-light-grey);
}

.forum-list-container .forum-item > .cell-forum:hover {
    background-color: var(--color-light-blue);
    transition: background-color var(--transition-duration-fast);
}

.forum-list-container .forum-item > .cell-forum .forum-wrapper > .forum-info {   /* Title for forum */
    padding-left: 0px;
}

.forum-list-container .forum-item > .cell-forum > .forum-desc                  /* Description of forum */
{
    margin-left: 0px !important;
    line-height: 120%;
}

.forum-list-container .forum-item > .cell-forum .forum-wrapper > .forum-info > a.forum-title {    /* Link to forum */
    margin-bottom: var(--spacing-half);
}

.forum-list-container .forum-item > .cell-forum .forum-wrapper > .forum-info > a.forum-title::before {
    content: '';
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
}



/* SUB FORUM LIST */

tr.subforum-list > td { /* Container for subforum */
    display: block;
    position: relative;
    
    width: var(--right-sidebar-width) !important;
    box-sizing: border-box;
    margin: var(--spacing);
    padding: 0px !important;

}

.subforum-list > td::before {           /* arrow for curly bracket */
    content: '';
    top: 50%;
	left: -17px;
	position: absolute;
    border-width: 6px;
    border-style: solid;
    border-color: transparent #D3ECFC transparent transparent;
    transform: translateY(-50%);
}

.subforum-list > td > table::before {       /* "body" for curly bracket */
 	content: '';
    display: block;
    position: absolute;
    height: 100%;
    width: 0.5rem;
    border-radius: 10px;
    border-left: 2px solid #D3ECFC;
    left: -5px;
}

.subforum-item {            /* Container for subforum */
    position: relative;
 	display: block;
    height: 24px;
}

.subforum-list .subforum-info a {       /* Title for subforum */
 	position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    line-height: 24px;
    vertical-align: baseline;
    box-sizing: border-box;
    padding: 0 0.5rem;
    border-radius: 3px;

    transition: background-color var(--transition-duration-slow);
}

.subforum-list .subforum-info .b-icon__speech-single2--blue ~ a {   /* New posts in subforum */
    background-color: var(--color-light-grey);
}

.subforum-list .subforum-info a:hover {
    background-color: var(--color-light-blue);
    transition: background-color var(--transition-duration-fast);    
}


/* User Activity */

#widget_159, #widget_4 {           /* Container */
    border: none;
    background: none;
    margin-top: var(--spacing-double);
}

#widget_159 .widget-content,   /* Item container */
#widget_4 .widget-content {
    display: flex;
    flex-direction: column-reverse;
}

#widget_159 .canvas-widget-list .canvas-layout-row,
#widget_4 .canvas-widget-list .canvas-layout-row {
    display: flex;
}

#widget_159 .widget-content > *,
#widget_4 .widget-content > * {
    margin: var(--spacing-half) 0px;
}

.onlineuserlist > li {      /* Each user name that's online */ 
    height: 18px;
}
}

@-moz-document domain("gammal.rollspel.nu") {
/* THREADS IN FORUM CATEGORY */

#topic-tab > .scrolltofixed-filler,
#thread-view-tab > .scrolltofixed-filler {   /* Filler for sticky element */
    width: initial !important;
}

.topic-list-header,
.topic-list > .topic-item .vb-icon
{
    display: none !important;
}

tbody.topic-list,
.topic-list > .topic-item {
    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
    align-content: flex-start;
}

.topic-list > .topic-item {     /* Post in forum view */
    width: var(--right-sidebar-width);
    padding: var(--spacing-double);
    box-sizing: border-box;
    position: relative;
}

.topic-list .closed:not(.sticky) {
    background-color: #fcf4f4 !important;
    position: relative;
}

.topic-list .closed::before {
    position: absolute;
    content: '🔒';
    bottom: 0px;
    right: 0px;
    font-size: 22px;
}

.topic-list-container .topic-list .topic-item .cell-topic .topic-wrapper {      /* Container for post headline */
    width: 100%;
    margin: 0px;
}

.topic-list-container .topic-list .topic-item .cell-topic {
    width: 100%;
}

.topic-list-container .topic-list .topic-item .cell-topic .topic-info {     /* Post starter */
    font-size: var(--font-size-smallest);
    margin-left: 0px;
}

.topic-list-container .topic-list .topic-item .cell-lastpost,           /* Container for last post and date; in both forum lists and search list */
.topic-list-container .topic-list .topic-item .cell-count {             /* Container for number of likes and responses; in both forum lists and search list */
    width: 100%;
    display: flex;
    align-items: flex-end;
    border-top: none;
}

.topic-list-container .topic-list .topic-item .cell-count > div {       /* Container for number of likes and responses */
    display: inline-block;   
}

.topic-list-container .topic-list .topic-item .cell-count > .posts-count {      /* Number of responses */
    flex: 1 1 auto;
}


.topic-list-container .topic-list .topic-item .cell-count > .votes-count {      /* Number of likes */
    color: var(--color-dark-grey);    
}

.topic-list-container .topic-list .topic-item .cell-lastpost {                  /* Container for last post and date */
    margin-top: var(--spacing-quarter);
}

.topic-list-container .topic-list .topic-item .cell-lastpost .lastpost-by::after {      /* Adding comma behind last poster's name */
    content: ',';
    position: relative;
    left: -4px;
}






}

@-moz-document url-prefix("http://www.nonsens.nu") {
/* OLD STUFF */


/* MAIN PAGE, FORUM LIST LAYOUT */

#forum-tab, table {
 	border: 0px none !important;
}

#thread-view-tab {
    padding-bottom: 13rem;
}

#breadcrumbs {
    margin: 0.5rem;
}


/* FORUM CATEGORIES */
.category-header {  /* CHANGED */
	width: 100%;
    height: auto !important;
    background: none !important;
    margin-top: 2rem;
    margin-bottom: 0.5rem;
    font-size: 18px !important;
}

.subchannel-widget tbody > tr:nth-child(2) {
    margin-top: 0px !important;
}


.conversation-content-widget .widget-header {
    display: none;
}

.category-header > td {
 	border-width: 0px !important;
}

.category-header > td > .category {
    font-size: 18px !important;
}

tbody.topic-list {
    position: relative;
}

tbody.topic-list .topic-item {
    position: relative;
}

tbody.topic-list .cell-topic {
    position: static !important;
}

.topic-list  .go-to-first-unread::before {
    position: absolute;
    content: '';
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
}

.forum-list-container > tbody,
.topic-list-container > tbody {
    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
    border-width: 0px !important;
}

.subforum-list {
    position: relative;
 	padding-left: 0.5rem;
}


.subforum-list > td {
    display: block;
    padding: 0px !important;
}

.subforum-list table {
 	width: 100%;
}

.subforum-list > td::before {
    content: '';
    top: 50%;
	left: -7px;
	position: absolute;
    border-width: 6px;
    border-style: solid;
    border-color: transparent #D3ECFC transparent transparent;
    transform: translateY(-50%);
}

.subforum-list > td > table::before {
 	content: '';
    display: block;
    position: absolute;
    height: 100%;
    width: 0.5rem;
    border-radius: 10px;
    border-left: 2px solid #D3ECFC;
    left: 0.3rem;
}

.subforum-item {
    position: relative;
 	display: block;
    height: 24px;
}

.subforum-list .subforum-info {
 	float: none !important;
}

.subforum-list .subforum-info a {
 	position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    line-height: 24px;
    vertical-align: baseline;
    box-sizing: border-box;
    padding: 0 0.5rem;
}

.subforum-list .subforum-info .icon:not(.new),
.subforum-list .subforum-info .counts {
 	display: none !important;
}


.forum-list-container .subforum-list .subforum-item .icon.new {
	position: absolute;
    width: 100%;
    height: 24px;
    margin: 0;
    background: none;
