css:
@-moz-document domain("halowaypoint.com") {
/* Background colors */
.region,
.social-- {
    background-color: #191921;
}
.light,
.social--large {
    background-color: #2c2c35!important;
    color: white!important;
}
.footer,
.footer .site-info .links > li a {
    color: slategray;
    background-color: #2c2c35;
}

.footer .site-brands {
    background-color: #1b1922;
    margin: 0px 0px;
    padding: 15px 10px 15px 10px;
    /*border-style: solid;
    border-width: 2px;
    border-color: #1e1e1e; */
    border-radius: 0px;
    transition: box-shadow .3s;
}
.footer .site-brands .brands .brand-ms-studios {
    filter: invert(255);
}
.footer .site-info .brand-343 {
    filter: invert(255) contrast(0) brightness(1) hue-rotate(90deg);
}
.footer .site-brands .brands .brand-xbox {
    filter: brightness(1.4);
}

.footer:hover .site-brands:hover {
    border-width: 1px;
}



/* Font Colors */
.content {
    color: white;
}




/* For page number text */
ol.paging li,
ol.paging li.current a {
    color: #777;
    border-color: #444;
}

ol.paging li.current a:hover {
    color: #000;
}
}

@-moz-document domain("halowaypoint.com") {
/* Forum text style */
.avatar,
.rank-watermark {
    box-shadow: 0px 0px 10px #111;
    border-style: none none none solid;
}

.spoiler-content {
    background-color: rgba(62, 62, 62, .17)!important;
    border: 1px solid rgba(0, 0, 0, .3)!important;
    border-radius: 4px;
}

blockquote {
    background-color: rgba(255, 255, 255, .02) !important;
    border: 1.4px solid rgba(0, 0, 0, .4)!important;
    border-radius: 4px;
}
}

@-moz-document domain("halowaypoint.com") {
/* Icon Colors */
.icon--heart,
.icon--heart span {
    color: #44baee;
}
.icon--heart {
    transition: background-color .3s;
    background-color: #474c4f;
}

.icon--heart:hover {
    background-color: #911243;
}
}
