css:
@-moz-document url-prefix("chrome-extension://bldnjpcciemkbmbbfpehdmdhiaemidce") {
body
  {
    background-color: #a7a7a7;
  }

  .CodeMirror-scroll
  {
    background-color: #90d06e;
  }

  .CodeMirror-lint-markers
  {
    background-color: #8f86e0;
  }

  .CodeMirror-foldgutter, input, select
  {
    background-color: #c9c9f1;
  }

  button
  {
    background-color: #90d06e;
  }

  .CodeMirror-gutters, .CodeMirror-linenumber
  {
    background-color: #50a2da;
    color: #000;
  }
}

@-moz-document domain("userstyles.org") {
body
  {
    background-color: #4c6ea0;
  }

  #nav-view
  {
    background-color: #bed0ec;
  }

  install_style_button
  {
    background-color: #e5f100;
    color: #ff0101;
  }

  style_installed_button
  {
    background-color: #2cce18;
    border: solid 1px #462ecc;
    color: #000000;
  }

  .logo
  {
    background-color: #2e3e49;
  }

  navigation, nav-view, footer
  {
    background-color: #cbcbce;
  }

  .css_button, .no-select, send_feedback_button
  {
    background-color: #61a1ff;
    color: #000;
  }

  .loggedIn, .header_button a
  {
    color: #2c3e50;
  }

  search_input input
  {
    background-color: rgb(96, 108, 117);
  }

  .CodeMirror .CodeMirror-matchingbracket
  {
    color: #f00;
  }

  #create_new_style a
  {
    color: #000000 !important;
  }

  #footer
  {
    background-color: #141921;
  }

  #navigation
  {
    background-color: #4f78b9;
  }

  *:not(.notes_textarea):not(.text_style_page):not(.style_description_long):not(.NotificationLine-Content) > a
  {
    color: rgb(255, 255, 255);
  }
}
