css:
@-moz-document domain("vuejs.org"), domain("vuejobs.com"), domain("vuemeetups.org"), domain("simulatedgreg.gitbooks.io")
{
  body,
body > div,
.book-summary,
.book-body,
#book-search-input , .module-header:hover, .bg-gray{
/* 	color: #4A7099 !important; */
    color: white !important;
	background-color: #2D2D2D !important;
}

.custom-block, .sidebar__box, div .job__skills .tag{
    color: #923034 !important;
    background: #d0c5ac !important;
}
.btn,#tawkchat-status-icon, .button,.position .text-muted{
    color: white !important;
}

.custom-block a{
    color: #5b7f33 !important;
}

p.tip, .main-pane, .position, .card, footer, th, td,.plan  {
	background-color: #3C3C3C !important;
	color: #A1A9B7 !important;
}

a.sidebar-link.active, .text-capitalize,.nav-link{
    color: white !important;
}

/* vue jop site */
.position--featured ,.bg-white,.style-example,details
{
    background-color: #4e4b33 !important;
    color: white !important;
}
/* ////// */


h1,
h2,
h3,
h4,
h5,
h6, th {
	color: #FF8100 !important;
}
a {
	color: #8DBB6E !important;
}
li.active>a,
li.active,
.section-link.active,
a.current {
	color: #DC616A !important;
}
strong {
	color: #A1A9B7 !important;
}
p code, code {
    color: #fbb851 !important;
	background-color: #3C3C3C !important;
	text-shadow: none !important;
}
code[class*="language-"],
pre[class*="language-"] {
	text-shadow: none !important;
}
figure {
	background-color: #3C3C3C !important;
}
pre {
	background-color: #3C3C3C !important;
	color: #A1A9B7 !important;
}
pre .line span.tag {
	color: #DC616A !important;
}
#ad {
	background-color: #424242 !important;
}
.sidebar .menu-sub a:hover {
    color: #4CAF50 !important;
    font-weight: bold !important;
    text-decoration: underline !important;
}
.content a:hover {
	text-decoration: underline !important;
}
#nav .nav-dropdown {
	background-color: #424242 !important;
}
#nav .nav-dropdown a:hover {
	background-color: #616161 !important;
}
#banner {
	background-color: #424242 !important;
    color: #E0E0E0 !important;
}
#banner blockquote {
	border-left: 5px solid #FF5722 !important;
	background-color: #757575 !important;
	color: #fcfcfc !important;
}
/* blockquote, .expanded-quote {
	color: #fdfdfd !important; 
	background-color: 
} */
code, aside.quote .title, blockquote {
	background-color: #424242 !important;
}
element {
	background-color: #424242 !important;
}
select {
    color: #0277BD !important;
}
#hero .button {
	background-color: #424242 !important;
}
.d-header {
	background-color: #424242 !important;
}
.menu-panel {
    background-color: #424242 !important;
}
.badge-wrapper.bullet span.badge-category {
	color: #E0E0E0 !important;
}
.menu-panel ul.menu-links li a:hover, .menu-panel ul li.heading a:hover {
	background-color: #757575 !important;
}
.list-controls .category-breadcrumb li.bullet > .dropdown-header {
	background-color: #616161 !important;
}
.list-controls .category-dropdown-menu {
	background-color: #616161 !important;
}
.nav-pills > li > a:hover {
	background-color: #616161 !important;
}
.topic-map ul {
	background-color: #424242 !important;
    color: #EEEEEE !important;
}
.topic-map .buttons .btn {
	background-color: #424242 !important;
    color: #EEEEEE !important;
}
.topic-map section {
	background-color: #424242 !important;
    color: #EEEEEE !important;
}
.alert.alert-info {
	background-color: #424242 !important;
    color: #EEEEEE !important;
}
.page-wrapper p {
	color: #fff !important;
}
.page-wrapper ol > li {
	color: white;
}
.page-wrapper ol > li code {
	background-color: #3C3C3C !important;
	color: #FF8100 !important;
}
.markdown-section ul > li {
	color: #DFDFDF;
}
.token.operator {
	background: none;
}
.sidebar, .navbar, .site-name, .nav-bar__outside,.dropdown-wrapper .nav-dropdown{
    background: #3C3C3C !important;
    color: white !important;
}

p{
    color: white !important;
}

/* Cancel background */
.navbar .links{
    background:none;
}
}
