css:
@-moz-document regexp("http://localhost/phpMyAdmin/.*") {
/*
bepis
background: #2d2d2d
mid: #3b3b3b
focus: #4c4c4c
white: #e0e0e0
color: #f89c0e
*/
body {
    color: #e0e0e0 !important;
    background: #2d2d2d !important;
}
#serverinfo {
    background-color: #3b3b3b !important;
}
#page_nav_icons > * {
    background-color: #3b3b3b !important;
}
#pma_navigation {
    background: #2d2d2d !important;
}
#pma_navigation a {
    color: #e0e0e0 !important;
}
#pma_navigation li.table.selected,
#pma_navigation_tree li.selected,
#pma_navigation li.activePointer {
    background-color: #4c4c4c !important;
}
#pma_navigation_resizer {
    background-color: #4c4c4c !important;
}
#pma_recent_list {
    color: #e0e0e0 !important;
    background-color: #4c4c4c !important;
}
#topmenucontainer {
    background: #2d2d2d !important;
    border-top-color: #4c4c4c !important;
}
#topmenu a {
    text-shadow: 0 1px 0 #000 !important;
}
#topmenu > li {
    border: solid 1px #4c4c4c !important;
}
.tabactive {
    background-color: #4c4c4c !important;
}
li:hover {
    background-color: #3b3b3b !important;
    background-image: none !important;
}
a,
div.tools a,
label,
button.mult_submit {
    color: #f89c0e !important;
}
.notice {
    color: #2d2d2d !important;
    background-color: #bd904a !important;
    border: none !important;
    box-shadow: none !important;
}
.notice a {
    color: black !important;
}
.success {
    color: #e0e0e0 !important;
    background: #65865b !important;
    border: none !important;
    box-shadow: none !important;
}
.error {
    color: #2d2d2d !important;
    background-color: #d88795 !important;
    border: none !important;
    box-shadow: none !important;
}
code.sql {
    background-color: #4c4c4c !important;
}
.tools {
    background-color: #3b3b3b !important;
}
th {
    color: #e0e0e0 !important;
    background-image: none !important;
    background-color: #3b3b3b !important;
    text-shadow: 0 1px 0 black !important;
}
td {
    text-shadow: 0 1px 0 black !important;
}
tr {
    background-color: #1d1d1d !important;
}
tr:hover {
    color: #e0e0e0 !important;
    background-color: #3b3b3b !important;
    background-image: none !important;
}
td.marked:not(.nomarker),
table tr.marked:not(.nomarker) td,
table tbody:first-of-type tr.marked:not(.nomarker) th,
table tr.marked:not(.nomarker) {
    color: #e0e0e0 !important;
    background-color: #4c4c4c !important;
    background-image: none !important;
}
td.hover {
    color: #e0e0e0 !important;
    background-color: #3b3b3b !important;
    background-image: none !important;
}
.navigation {
    background-image: none !important;
}
.navigation tr {
    background-color: #3b3b3b !important;
}
.navigation tr:hover {
    color: #e0e0e0 !important;
    background-image: none !important;
}
.tooltip {
    box-shadow: 0px 0px 15px -5px #000 !important;
}
legend {
    color: #e0e0e0 !important;
    background-color: #3b3b3b !important;
    border: none !important;
    box-shadow: none !important;
    margin-top: 25px;
}
fieldset {
    background-color: #3b3b3b !important;
    border: none !important;
    box-shadow: none !important;
    margin-top: -25px;
}
.toolbar {
    background-color: #3b3b3b !important;
    border: solid 1px #4c4c4c !important;
}
.switch_button:hover {
    background-color: #4c4c4c !important;
}
.content {
    background-color: #2d2d2d !important;
    border-top: none !important;
}
#pma_console .message {
    border-bottom: none !important;
}
#selflink {
    border: none !important;
}
input,
select {
    color: #e0e0e0 !important;
    background-color: #4c4c4c !important;
    border: solid 1px #3b3b3b !important;
    background-image: none !important;
    text-shadow: 0 1px 0 black !important;
}
input:hover {
    box-shadow: none !important;
}
.tab {
    background-color: #2d2d2d !important;
    border: none !important;
}
.tabactive {
    background-color: #4c4c4c !important;
    border: none !important;
}
.group {
    background-color: #3b3b3b !important;
    border: solid 1px #4c4c4c !important;
    box-shadow: none !important;
}
.group h2 {
    background-color: #4c4c4c !important;
    box-shadow: none !important;
}
.cEdit .edit_area {
    background-color: #3b3b3b !important;
    border: solid 1px #2d2d2d !important;
}
.ui-widget-content {
    color: #e0e0e0 !important;
    background-color: #3b3b3b !important;
    border: solid 1px #2d2d2d !important;
}
.ui-widget-header {
    color: #e0e0e0 !important;
    background-color: #4c4c4c !important;
    background-image: none !important;
    border: solid 1px #2d2d2d !important;
}
.ui-state-default {
    background-color: #3b3b3b !important;
    background-image: none !important;
    border: 1px solid #4c4c4c !important;
}
.ui-state-hover {
    background-color: #4c4c4c !important;
}
.ui-state-active {
    color: #2d2d2d !important;
    background-color: #f89c0e !important;
    background-image: none !important;
    border: solid 1px #2d2d2d !important;
    text-shadow: none !important;
}

/* code*/
.CodeMirror {
    background: #1d1d1d !important;
    caret-color: #e0e0e0 !important;
}
.CodeMirror-line {
    color: #e0e0e0 !important;
}
.CodeMirror-gutters {
    background-color: #4c4c4c !important;
    border-right: solid 1px #3b3b3b !important;
}
.cm-keyword {
    color: #ff86ff !important;
}
.cm-variable-2 {
    color: #71e6ff !important;
}
.cm-number {
    color: #61d278 !important;
}
}
