css:
@-moz-document domain("facebook.com") {
:root {
    --bg1: #eee !important;
    --bg2: #fff !important;
    --bg3: #f9f9f9 !important;
    --text1: #111 !important;
    --text2: #111 !important;
    --accent: #111 !important;
}

/* Basics */
* {
    transition: ease-in-out .3s;
}
a,
a:link,
span a,
._517h,
._59pe:focus,
._59pe:hover {
    color: var(--accent) !important;
    text-decoration: none !important;
}

/* bg */
body,
._5vb_,
._5vb_ #contentCol {
    background: var(--bg1) !important;
    color: var(--first-color) !important;
}

/* Header */
._2t-a,
._2s1x ._2s1y,
._19eb:hover {
    background: var(--bg2) !important;
    border: none;
    box-shadow: none;
}
#u_0_b > div:nth-child(1) > div:nth-child(1),
#u_0_b > div:nth-child(1) > div:nth-child(3),
#u_0_b > div:nth-child(1) > div:nth-child(4),
#u_0_m {
    display: none;
}

/* Logo */
._2md {
    margin-left: 100px;
    filter: invert(100%);
    opacity: 0.6;
}

/* Search */
._585- {
    background-color: var(--bg1) !important;
    max-width: 300px;
    margin: 0 auto;
    text-align: center;
    border: none;
    height: 25px;
    top: 9px;
    padding-left: 15px;
}
._585-,
._585- ._4w98 {
