}
._585- ._4w98._4w97,
._585- ._4w98._4w97:hover,
._585- ._4w98 {
    background-color: var(--bg3) !important;
}

/* Making A Post Section */
._m_1 ._2aha,
._5qtp {
    font-size: 0px;
}
._m_1 ._2aha:hover,
._5qtp:hover {
    font-size: 13px;
}
._m_1 {
    width: 38px;
}
._2yq ._4-u2 {
