@-moz-document domain("youtube.com") {
/*----------------------------Made-by-yours-truly-Marshy----------------------------*/
/*----------------------------------THE-ROOT-ACCESS---------------------------------*/
/*---------------Change-this-baby-and-you-can-have-any-color-you-want---------------*/

:root {
    --main-color-theme: #3ea6ff; /*Default: #3ea6ff*/
    --main-color-theme2: rgba(62,166,255,.2); /*Default: rgba(62,166,255,.2)*/
    --main-anime: bounce 1500ms linear both;
    --main-anime-reverse: bounce-reverse 1500ms linear both;
}

/*------and-the-rest-of-the-code------*/
ytd-searchbox[mode="legacy"] #container.ytd-searchbox, ytd-searchbox[mode="legacy-centered"] #container.ytd-searchbox {
	background-color: rgba(0,0,0,.05);
	border: none;
	box-shadow: 0 3px rgba(0,0,0,.1);
	padding: 2px 6px;
	border-radius: 2px 0 0 2px;
    outline: none !important;
} 
ytd-searchbox[mode="legacy"][has-focus] #container.ytd-searchbox, ytd-searchbox[mode="legacy-centered"][has-focus] #container.ytd-searchbox {
    border: none;
    box-shadow: 0 3px rgba(0,0,0,.1);
}

#search-icon-legacy.ytd-searchbox {
	cursor: pointer;
	width: 65px;
	border: none;
    box-shadow: 0 3px rgba(0,0,0,.08);
	background-color: var(--ytd-searchbox-legacy-button-color);
	border-radius: 0 2px 2px 0;
	margin: 0;
	--yt-icon-button-icon-width: 24px;
	--yt-icon-button-icon-height: 24px;
}

ytd-searchbox[mode="legacy"] #search-icon-legacy.ytd-searchbox:hover, ytd-searchbox[mode="legacy-centered"] #search-icon-legacy.ytd-searchbox:hover {
	border: none;
	background-color: var(--ytd-searchbox-legacy-button-hover-color);
	box-shadow: 0 3px rgba(0,0,0,.13);
}

#search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox {
	width: 20px;
	height: 20px;
	color: var(--ytd-searchbox-legacy-button-icon-color);
	opacity: 0.6;
    transition: .4s;
    animation: var(--main-anime-reverse);
}

#search-icon-legacy.ytd-searchbox:hover yt-icon.ytd-searchbox {
	opacity: 1;
    /*transform: scale(1.05,1.05);*/
    animation: var(--main-anime);
}

img.yt-img-shadow {
	display: block;
	margin-left: auto;
	margin-right: auto;
	max-height: var(--yt-img-max-height, none);
	max-width: var(--yt-img-max-width, 100%);
    transition: .4s;
}

img.yt-img-shadow:hover {
	display: block;
	margin-left: auto;
	margin-right: auto;
	max-height: var(--yt-img-max-height, none);
	max-width: var(--yt-img-max-width, 100%);
    transform: scale(1.05,1.05);
}

button.yt-icon-button {
	vertical-align: middle;
	color: inherit;
	outline: none;
	background: none;
	margin: 0;
	border: none;
	padding: 0;
	width: 100%;
	height: 100%;
	line-height: 0;
	cursor: pointer;
	/* -webkit-tap-highlight-color: rgba(0, 0, 0, 0); */
	-webkit-tap-highlight-color: transparent;
    transition: .4s;
    bottom: 0px;
    position: relative;
    backface-visibility: hidden;
    transform: translateZ(0) scale(1.0, 1.0);
}

button.yt-icon-button:hover {
	vertical-align: middle;
	color: inherit;
	outline: none;
	background: none;
	margin: 0;
	border: none;
	padding: 0;
	width: 100%;
	height: 100%;
	line-height: 0;
	cursor: pointer;
	/* -webkit-tap-highlight-color: rgba(0, 0, 0, 0); */
	-webkit-tap-highlight-color: transparent;
    /*transform: translateZ(0) scale(1.1, 1.1);*/
    animation: var(--main-anime);
}

button.yt-icon-button:not(:hover) {
	vertical-align: middle;
	color: inherit;
	outline: none;
	background: none;
	margin: 0;
	border: none;
	padding: 0;
	width: 100%;
	height: 100%;
	line-height: 0;
	cursor: pointer;
	/* -webkit-tap-highlight-color: rgba(0, 0, 0, 0); */
	-webkit-tap-highlight-color: transparent;
    /*transform: translateZ(0) scale(1.1, 1.1);*/
    animation: var(--main-anime-reverse);
}

#logo-icon-container.ytd-topbar-logo-renderer {
	width: 80px;
	height: 24px;
	-ms-flex: none;
	-webkit-flex: none;
	flex: none;
}

#logo-icon-container.ytd-topbar-logo-renderer:hover {
	width: 80px;
	height: 24px;
	-ms-flex: none;
	-webkit-flex: none;
	flex: none;
    backface-visibility: hidden;
    animation: var(--main-anime);
}

#logo-icon-container.ytd-topbar-logo-renderer:not(:hover) {
	width: 80px;
	height: 24px;
	-ms-flex: none;
	-webkit-flex: none;
	flex: none;
    backface-visibility: hidden;
    animation: var(--main-anime-reverse);
}

