paper-button[subscribed].ytd-subscribe-button-renderer {
	/* background-color: hsl(0, 0%, 93.3%); */
	color: #717171;
	background-color: #e9e9e9;
}

.deemphasize.yt-formatted-string {
	/* color: var(--yt-formatted-string-deemphasize-color); */
	margin-left: var(--yt-formatted-string-deemphasize_-_margin-left);
	display: var(--yt-formatted-string-deemphasize_-_display);
	/* color: var(--yt-formatted-string-deemphasize_-_color, var(--yt-formatted-string-deemphasize-color)); */
	color: #717171 !important;
}

.ytp-button:not([aria-disabled="true"]):not([disabled]):not([aria-hidden="true"]) {
	cursor: pointer;
    animation: var(--main-anime-reverse);
}

.ytp-chrome-top .ytp-button:hover, .ytp-chrome-controls .ytp-button:not([aria-disabled="true"]):not([disabled]):hover, .ytp-chrome-controls .ytp-button[aria-expanded="true"], .ytp-chrome-controls .ytp-button[aria-pressed="true"], .ytp-replay-button:hover {
	opacity: 1;
	-moz-transition: opacity .1s cubic-bezier(0.0,0.0,0.2,1);
	-webkit-transition: opacity .1s cubic-bezier(0.0,0.0,0.2,1);
	transition: opacity .1s cubic-bezier(0.0,0.0,0.2,1);
    animation: var(--main-anime);
}

button.ytd-topbar-menu-button-renderer {
	background: none;
	border: none;
	outline: none;
	display: flex;
    cursor: pointer;
    animation: var(--main-anime-reverse);
}

button.ytd-topbar-menu-button-renderer:hover {
	background: none;
	border: none;
	outline: none;
	display: flex;
    animation: var(--main-anime);
}

/*----------------------Here-is-where-you-can-change-all-the-colors----------------------*/

/*------Below-is-for-the-progress-bar-on-the-vid------*/

.ytp-swatch-background-color {
    background-color: var(--main-color-theme);
}

/*------Below-is-for-the-HD-tag-ON-the-settings-icon------*/

.ytp-settings-button.ytp-hd-quality-badge::after, .ytp-settings-button.ytp-4k-quality-badge::after, .ytp-settings-button.ytp-5k-quality-badge::after, .ytp-settings-button.ytp-8k-quality-badge::after {
	content: '';
	position: absolute;
	top: 10px;
	right: 5px;
	height: 9px;
	width: 13px;
    background-color: var(--main-color-theme);
	border-radius: 1px;
	line-height: normal;
}

/*------Below-is-for-the-HD-tag-IN-the-settings-icon------*/

.ytp-swatch-color {
	color: var(--main-color-theme);
}

/*------Below-is-for-the-toggles------*/

.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
    background-color: var(--main-color-theme);
}

/*-----Below-is-for-the-tiny-circle-next-to-the-notification-bell-icon-at-the-top-right-----*/

#notification-count.ytd-notification-topbar-button-renderer {
	position: absolute;
	top: 4px;
	right: 0;
	color: var(--yt-notification-button-bubble_-_color);
    background-color: var(--main-color-theme) !important;
	width: 18px;
	height: 18px;
	border-radius: 50%;
	line-height: 18px;
	font-size: 10px;
	text-align: center;
	cursor: pointer;
}

/*------------below-is-for-the-sub-button-well,-before-you-sub-to-someone------------*/

paper-button.ytd-subscribe-button-renderer {
    background-color: var(--main-color-theme);
	border-radius: 2px;
	color: var(--yt-subscribe-button-text-color);
	padding: 10px 16px;
	margin: auto 4px;
	white-space: nowrap;
	font-size: 1.4rem;
	font-weight: 500;
	letter-spacing: .007px;
	text-transform: uppercase;
	display: flex;
	-ms-flex-direction: row;
	-webkit-flex-direction: row;
	flex-direction: row;
}

/*-------Below-is-for-the-LIVE-NOW-tag-under-the-video-thumbnails-that-are-live-------*/

.badge-style-type-live-now.ytd-badge-supported-renderer {
	background: transparent !important;
	color: var(--main-color-theme) !important;
	border: 0px solid var(--main-color-theme) !important;
    border-radius: 2px !important;
    padding: 5px !important;
}

.badge-style-type-live-now.ytd-badge-supported-renderer:hover {
	background: var(--main-color-theme2) !important;
}

/*-------Below-is-for-the-bars-at-the-bottom-of-thumbnails-for-the-vids-you-have-watched-------*/

#progress.ytd-thumbnail-overlay-resume-playback-renderer {
    background-color: var(--main-color-theme) !important;
	height: 100%;
}

/*-----------Below-is-for-the-volume-bar-color------------*/

.ytp-volume-slider-handle {
	position: absolute;
	top: 50%;
	width: 12px;
	height: 12px;
	border-radius: 6px;
	margin-top: -6px;
    background-color: var(--main-color-theme);
}

.ytp-volume-slider-handle::before {
	left: -58px;
    background-color: var(--main-color-theme);
}

.ytp-volume-slider-handle::after {
	left: 6px;
	background: var(--main-color-theme2) !important;
}

/*------Below-is-for-the-side-bar-icons------*/

ytd-guide-entry-renderer[active] .guide-icon.ytd-guide-entry-renderer, ytd-guide-entry-renderer[is-guide-refresh][active] .guide-icon.ytd-guide-entry-renderer {
	color: var(--main-color-theme);
}

/*----Below-is-for-the-side-bar-LIVE-icons-for-the-channels-you-subbed-to-that-are-live----*/

.guide-entry-badge.ytd-guide-entry-renderer {
	width: 16px;
	height: 16px;
	color: var(--main-color-theme);
}

/*----Below-is-for-the-toggle-switch-for-the-autoplay----*/

paper-toggle-button[checked]:not([disabled]) .toggle-button.paper-toggle-button {
    background-color: var(--main-color-theme);
}

/*----Below-is-for-the-like-button-and-the-like/dislike-bar----*/

ytd-sentiment-bar-renderer[activated_] #like-bar.ytd-sentiment-bar-renderer {
	background-color: var(--main-color-theme);
}

ytd-toggle-button-renderer.style-default-active[is-icon-button] {
	color: var(--main-color-theme);
}

/*----Below-is-for-the-logo-svg-so-it-can-change-color-with-the-rest-of-it's-friends----*/

#logo-icon-container.ytd-topbar-logo-renderer svg g g path{
	fill:  var(--main-color-theme) !important;
}

#logo-icon-container.ytd-topbar-logo-renderer svg g g g path {
	fill:  #717171 !important;
}

/*----Below-is-for-the-JOIN-button----*/

ytd-button-renderer.style-suggestive[is-paper-button] {
	background-color: var(--main-color-theme2) !important;
	border: 1px solid var(--main-color-theme) !important;
	color: var(--main-color-theme);
}

/*----Below-is-for-the-tags-under-some-videos----*/

.super-title.ytd-video-primary-info-renderer {
	--yt-endpoint-color: var(--main-color-theme);
	--yt-endpoint-visited-color: var(--main-color-theme);
	--yt-endpoint-hover-color: var(--main-color-theme);
	font-size: 1.2rem;
	font-weight: 400;
	line-height: 1.5rem;
}

/*----Below-is-for-the-video-time-stamp----*/

ytd-thumbnail-overlay-time-status-renderer {
	display: inline-block;
	position: relative;
	top: 0px;
	right: 0px;
	margin: 0px;
	color: #717171 !important;
	background-color: hsl(0, 0%, 100%) !important;
	opacity: .9;
	padding: 2px 0px;
	border-radius: 0px;
	letter-spacing: .5px;
	font-size: 1.2rem;
	font-weight: 500;
	line-height: 1.2rem;
	display: flex;
	-ms-flex-direction: row;
	-webkit-flex-direction: row;
	flex-direction: row;
	align-items: center;
	display: inline-flex;
	width: 100%;
	text-align: center;
	padding-left: 43%;
}

/*----Below-is-for-the-tickets-buttons----*/

.link.ytd-ticket-shelf-renderer {
	color: var(--main-color-theme);
	font-weight: 700;
	text-decoration: none;
	margin-right: 8px;
	float: right;
	font-size: 1.4rem;
	font-weight: 500;
	line-height: 1.6rem;
}

#primary-event.ytd-ticket-shelf-renderer #meta.ytd-ticket-shelf-renderer #tickets-button.ytd-ticket-shelf-renderer {
	background-color: var(--main-color-theme2);
	border-radius: 2px;
	color: var(--main-color-theme);
	display: inline-block;
	height: 16px;
	margin-top: 8px;
	padding: 8px 16px;
	text-decoration: none;
	font-size: 1.4rem;
	font-weight: 500;
	line-height: 1.6rem;
}

#primary-event.ytd-ticket-shelf-renderer #meta.ytd-ticket-shelf-renderer #tickets-button.ytd-ticket-shelf-renderer:hover {
	background-color: var(--main-color-theme2);
	border-radius: 2px;
	color: var(--main-color-theme);
	display: inline-block;
	height: 16px;
	margin-top: 8px;
	padding: 8px 16px;
	text-decoration: none;
	font-size: 1.4rem;
	font-weight: 500;
	line-height: 1.6rem;
}
  
/*----Below-is-for-the-used-to-be-red-circle-next-to-the-LIVE-tag----*/

.ytp-live-badge[disabled]::before {
	background: var(--main-color-theme);
}
  
/*----Below-is-for-the-sub-button-ON-the-video----*/

.ytp-sb-subscribe, a.ytp-sb-subscribe {
	background-color: var(--main-color-theme) !important;
	color: #fff;
}

.ytp-sb-unsubscribe {
	background-color: #232323 !important;
	color: #fff;
}
  
/*----Below-is-for-the-logo-ON-the-video----*/

.ytp-large-play-button-bg {
	-moz-transition: fill .1s cubic-bezier(0.4,0.0,1,1),fill-opacity .1s cubic-bezier(0.4,0.0,1,1);
	-webkit-transition: fill .1s cubic-bezier(0.4,0.0,1,1),fill-opacity .1s cubic-bezier(0.4,0.0,1,1);
	transition: fill .1s cubic-bezier(0.4,0.0,1,1),fill-opacity .1s cubic-bezier(0.4,0.0,1,1);
	fill: var(--main-color-theme) !important;
	fill-opacity: .8;
}
  
/*----Below-is-for-the-Captions-button----*/

.ytp-chrome-controls .ytp-button[aria-pressed]::after {
	content: '';
	display: block;
	position: absolute;
	height: 2px;
	border-radius: 2px;
	width: 0;
	left: 18px;
	bottom: 6px;
	background-color: var(--main-color-theme);
	-moz-transition: left .1s cubic-bezier(0.4,0.0,1,1),width .1s cubic-bezier(0.4,0.0,1,1);
	-webkit-transition: left .1s cubic-bezier(0.4,0.0,1,1),width .1s cubic-bezier(0.4,0.0,1,1);
	transition: left .1s cubic-bezier(0.4,0.0,1,1),width .1s cubic-bezier(0.4,0.0,1,1);
}
}
