css:
@-moz-document url-prefix("https://v3rmillion.net")
{
  #logo img, .logo
  {
    content: url(https://vgy.me/unTtzc.png) !important;
  }

  .pm_new, .alert_new, ul.menu li a.active
  {
    background: #00ffe1 !important;
  }

  #bridge
  {
    border-bottom: 5px solid #00AAFF;
  }

  ul.menu li ul, .panel_buttons
  {
    border-top: 5px solid #00ffe1;
  }

  .pagination .pagination_current
  {
    background: #00dbb6;
    border: 1px solid #00dbb6;
    border-radius: 2px;
  }

  .pagination a
  {
    background: #00ffe1;
    border: 1px solid #00ffe1;
  }

  .pagination a:hover
  {
    transition: background 0.2s ease, border 0.2s ease;
    background: #0088DD;
    border: 1px solid #00ffe1;
  }

  .thead
  {
    background: #00ffe1;
  }

  .popup_menu .popup_item:hover
  {
    color: 00dbb6;
  }

  .buttons:hover
  {
    transition: border 0.2s ease;
    background: 00dbb6;
    cursor: pointer;
  }

  .buttons
  {
    border: 00dbb6;
  }

  a.postbit_qdelete, a.postbit_website, a.postbit_reply_pm, a.postbit_forward_pm, a.postbit_delete_pm, a.postbit_email, a.postbit_pm, a.postbit_find, a.postbit_edit, a.postbit_quote, a.postbit_multiquote, a.postbit_multiquote_on, a.postbit_report
  {
    background: #0088DD !important;
    border: 1px solid #00dbb6 !important;
  }

  a.button.new_thread_button, button, input.button, a.button:link, a.button:visited, a.button:active
  {
    background: #0088DD;
    border: 1px solid #00dbb6;
  }

  a.button.new_thread_button:hover, button, input.button:hover, a.button:hover
  {
    transition: background 0.2s ease, border 0.2s ease;
    background: #202244;
    border: 1px solid #202244;
  }

  .tfoot
  {
    background: #00dbb6;
  }

  .usercp_nav_item:before
  {
    color: #00dbb6;
  }

  .thead input.textbox, .thead select
  {
    border: #00dbb6;
  }

  ul.menu li a:hover, .panel_buttons a:hover, ul.menu li ul li a:hover
  {
    transition: background 0.5s ease;
    background: #202244;
  }

  .thread_head
  {
    border-bottom: 5px solid #00dbb6 !important;
  }

  #footer
  {
    border-top: 5px solid #00AAFF;
  }

  .forum_on
  {
    color: #00dbb6;
  }

  .forum_off
  {
    color: #202244;
  }

  .cRem a:visited, .cRem a:link
  {
    color: #00dbb6 !important;
  }

  .inline_row a:visited, .inline_row a
  {
    color: #00dbb6 !important;
  }

  input.textbox:focus
  {
    transition: border 0.2s ease, box-shadow 0.2s ease;
    border: 1px solid #00dbb6;
    box-shadow: 0 0 10px #00dbb6;
  }

  form #message:focus
  {
    transition: border 0.2s ease, box-shadow 0.2s ease;
    border: 1px solid #00dbb6;
    box-shadow: 0 0 10px #00dbb6;
  }

  div#container span.smalltext a span strong
  {
    color: #0088DD !important;
  }

  .red_alert
  {
    background: #00dbb6;
  }

  .red_alert:hover
  {
    transition: background 0.5s ease;
    background: #00dbb6;
  }
}

@-moz-document url("https://v3rmillion.net/siterules.php"), url("https://v3rmillion.net/legal.html"), url("https://v3rmillion.net/removals/")
{
  img
  {
    content: url(http://image.prntscr.com/image/22ae9fd9c67e404097b1f747d248583d.png) !important;
  }

  .wrap
  {
    border: 1px solid #00dbb6;
  }

  .wrap a
  {
    color: #00dbb6;
  }

  .footer
  {
    background-color: #00dbb6;
  }

  .footer a
  {
    color: #000000;
  }

  .footer a:hover
  {
    transition: color 0.1s ease;
    color: #00dbb6;
  }

  a:link, a:visited
  {
    color: #00dbb6;
  }

  a:link:hover, a:visited:hover
  {
    color: #DDDDFF;
  }
}
