css:
@-moz-document url-prefix("https://www.pantheonmmo.com/content/forums"), url-prefix("http://www.pantheonmmo.com/content/forums") {
/* v1.2 */

p,
td,
li,
ul,
.forum_topic_posts,
.forum_nth_0,
#tinymce,
#forum_post_create > div,
input#title,
.layout_core_content,
.layout_page_forum_topic_view,
ul.forum_topic_posts > li.forum_nth_0,
div.navbar-collapse,
#forum_post_quick > div,
.layout_page_forum_index_index .layout_middle,
#global_page_forum-forum-view .layout_middle,
.layout_page_forum_topic_view .layout_core_content,
ul.forum_topic_posts li div.forum_topic_posts_info_body blockquote {
    color: #dfdfdf;
    background-color: #444;
}
.forum_topic_posts > .forum_nth_0,
.forum_topic_posts > .forum_nth_1
{
    border-top: 3px solid #333;
}

ul.forum_topics > li.forum_nth_1
{
    background-color: #444;
}
ul.forum_topics > li.forum_nth_0
{
    color: #dfdfdf;
    background-color: #333;
}
body,
div {
    color: #dfdfdf;
}
}
