css:
@-moz-document domain("soundcloud.com") {
/*----------------------------Made-by-yours-truly-Marshy----------------------------*/

:root {
    --soundcloud-anime: bounce 1500ms linear both;
    --soundcloud-anime-reverse: bounce-reverse 1500ms linear both;
    --soundcloud-color: #f50;
}

.sc-artwork.sc-artwork-placeholder-9 {
	border-radius: 50%;
}

.image__full, .image {
	text-align: center;
	position: relative;
	background-repeat: no-repeat;
	border-radius: 50%;
}

.image__lightOutline .image__full {
	box-shadow: 0 0 0px 0px rgba(0,0,0,.1);
}

.playableTile__actions {
	position: absolute;
	bottom: 0;
	left: 0;
	width: 100%;
	height: 100%;
	padding: 5px;
	/*background: linear-gradient(to top,rgba(0,0,0,.4),rgba(0,0,0,0));*/
    background: rgba(0,0,0,.1) !important;
	display: -webkit-flex;
	display: -ms-flexbox;
	display: flex;
	-webkit-justify-content: flex-end;
	-ms-flex-pack: end;
	justify-content: flex-end;
	-webkit-align-items: flex-end;
	-ms-flex-align: end;
	align-items: flex-end;
	box-sizing: border-box;
    border-radius: 50% !important;
}

.sc-button-small.sc-button-icon:not(.sc-button-stretch-icon)::before {
	left: 0px !important;
}

.sc-button-small.sc-button-icon, .sc-button-medium.sc-button-icon {
	text-align: left;
	text-indent: -100000px;
	left: -58px;
	background-color: transparent;
}

.sc-button-small.sc-button-icon:hover, .sc-button-medium.sc-button-icon:hover {
	background-color: transparent;
    transform: scale(1,1);
}

.sc-button {
	display: inline-block;
	position: relative;
	height: 26px;
	margin: 0;
	padding: 2px 11px 2px 10px;
	border: none;
	border-radius: 3px;
	background-color: rgba(0,0,0,.05);
	cursor: pointer;
	color: #717171;
	font-size: 14px;
	line-height: 20px;
	white-space: nowrap;
	font-family: "Interstate","Lucida Grande","Lucida Sans Unicode","Lucida Sans",Garuda,Verdana,Tahoma,sans-serif;
	font-weight: 100;
	text-align: center;
	vertical-align: baseline;
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	box-sizing: border-box;
    /*transition: .4s;
    transform: scale(1,1);*/
    animation: var(--soundcloud-anime-reverse);
}

.sc-button:hover {
    /*transform: scale(1.05,1.05);*/
    animation: var(--soundcloud-anime);
    color: var(--soundcloud-color);
    background-color: rgba(255,85,0,.2);
}

.sc-button-active:hover, .sc-button-active:focus, .sc-button-active.sc-button-focus, .sc-button-selected:hover, .sc-button-selected:focus, .sc-button-selected.sc-button-focus {
	color: var(--soundcloud-color);
    background-color: rgba(255,85,0,.2);
	border: 0px solid var(--soundcloud-color);
}

.sc-button-xlarge.sc-button-play, .sc-button-xlarge.sc-button-pause {
	width: 36px;
	height: 36px;
	border-radius: 50%;
	background-color: var(--soundcloud-color);
}

.sc-button-large.sc-button-play, .sc-button-large.sc-button-pause {
	width: 28px;
	height: 28px;
	margin: 1px 0 1px 1px;
	line-height: 42px;
	background-color: var(--soundcloud-color);
	border-radius: 50%;
}

.sc-button-large.sc-button-play:hover, .sc-button-large.sc-button-pause:hover {
	width: 28px;
	height: 28px;
	margin: 1px 0 1px 1px;
	line-height: 42px;
	background-color: var(--soundcloud-color);
	border-radius: 50%;
}

.playbackSoundBadge__lightLink {
	font-size: 0px;
	line-height: 16px;
	top: 9px;
	position: relative;
}

.playbackSoundBadge__titleLink, .playbackSoundBadge__titleLink:visited {
	font-size: 11px;
	color: #717171;
	position: relative;
	bottom: 9px;
}

.playbackSoundBadge__titleLink, .playbackSoundBadge__titleLink:visited {
	font-size: 11px;
	color: #717171;
	width: 196px !important;
}

a.sc-link-light {
	color: #717171;
}

svg:not(:root) {
	overflow: hidden;
	left: 0px;
	position: relative;
}

.playbackSoundBadge__showQueue {
	width: 24px;
	height: 24px;
	position: relative;
	display: inline-block;
	overflow: hidden;
	cursor: pointer;
	left: -53px;
    animation: var(--soundcloud-anime-reverse);
}

.playbackSoundBadge__showQueue:hover {
	width: 24px;
	height: 24px;
	position: relative;
	display: inline-block;
	overflow: hidden;
	cursor: pointer;
	left: -53px;
    animation: var(--soundcloud-anime);
}

.volume[data-level="6"] .volume__button, .volume[data-level="7"] .volume__button, .volume[data-level="8"] .volume__button, .volume[data-level="9"] .volume__button, .volume[data-level="10"] .volume__button {
	background-image: url(data:image/svg+xml;base64,);
    animation: var(--soundcloud-anime-reverse);
}

.volume[data-level="6"] .volume__button:hover, .volume[data-level="7"] .volume__button:hover, .volume[data-level="8"] .volume__button:hover, .volume[data-level="9"] .volume__button:hover, .volume[data-level="10"] .volume__button:hover {
	animation: var(--soundcloud-anime);
}

.volume[data-level="1"] .volume__button, .volume[data-level="2"] .volume__button, .volume[data-level="3"] .volume__button, .volume[data-level="4"] .volume__button, .volume[data-level="5"] .volume__button {
	background-image: url(data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSIyNCIgaGVpZ2h0PSIyNCI+PHBhdGggZmlsbD0iIzMzMyIgZD0iTTQgOWg0LjAwMkwxMiA1djE0Yy0yLjQ0Ni0yLjY2Ny0zLjc3OC00LTMuOTk4LTRINFY5em0xMCA0YTEgMSAwIDAgMCAwLTJWOWEzIDMgMCAwIDEgMCA2di0yeiIvPjwvc3ZnPgo=);
    animation: var(--soundcloud-anime-reverse);
}

.volume[data-level="1"] .volume__button:hover, .volume[data-level="2"] .volume__button:hover, .volume[data-level="3"] .volume__button:hover, .volume[data-level="4"] .volume__button:hover, .volume[data-level="5"] .volume__button:hover {
	animation: var(--soundcloud-anime);
}

.volume.muted .volume__button {
	background-image: url(data:image/svg+xml;base64,);
    animation: var(--soundcloud-anime-reverse);
}

.volume.muted .volume__button:hover {
	animation: var(--soundcloud-anime);
}

.playbackTimeline__snippetIndicator, .playbackTimeline__timePassed, .playbackTimeline__duration {
	width: 50px;
	height: 46px;
	cursor: default;
}

.volume__sliderWrapper {
	position: absolute;
	left: -5px;
	bottom: 40px;
	z-index: 1;
	width: 30px;
	height: 0;
	transition: height 100ms;
	-webkit-transform: translateZ(0);
	transform: translateZ(0);
	overflow: hidden;
	background-color: #f2f2f2;
	border: 1px solid transparent;
	outline: 0;
	cursor: pointer;
}

.volume.expanded .volume__sliderWrapper {
    border: none !important;
    box-shadow: 0 3px var(--soundcloud-color) !important;
}

.volume__sliderWrapper::before {
	z-index: 0;
	bottom: -10px;
	border: 0px solid #000 !important;
	left: 8px;
	border-color: transparent !important;
	box-shadow: -3px 3px 4px rgba(0,0,0,.1);
}

.volume__sliderWrapper::after {
	bottom: -8px;
	left: 9px;
	z-index: 1;
	border: 0px solid #000 !important;
	border-color: transparent !important;
}

.volume__sliderHandle {
	opacity: 0;
	transition: opacity 100ms linear;
	transition-delay: 100ms;
    animation: var(--soundcloud-anime-reverse);
}

.volume__sliderHandle:hover {
	opacity: 0;
	transition: opacity 100ms linear;
	transition-delay: 100ms;
    animation: var(--soundcloud-anime);
}

.sc-input {
	display: inline-block;
	font-family: "Interstate","Lucida Grande","Lucida Sans Unicode","Lucida Sans",Garuda,Verdana,Tahoma,sans-serif;
	font-weight: 100;
	font-size: 14px;
	line-height: 20px;
	padding: 2px 7px;
	border-radius: 4px;
	border: none;
	background-color: rgba(0,0,0,.05);
    box-shadow: 0px 3px rgba(0,0,0,.08);
}

.g-dark textarea, .g-dark select, .g-dark input[type="text"], .g-dark input[type="password"], .g-dark input[type="search"] {
	color: #666;
	outline: 0;
	border: 0;
	padding: 5px 7px;
	background-color: #fff;
	box-shadow: 0 3px #d8d8d8;
}

.header__navMenu > li > a {
	display: block;
	padding: 12px 0;
	width: 104px;
	height: 46px;
	border-right: 0px solid #111;
	box-sizing: border-box;
	text-align: center;
}

.header__navMenu > li > a.selected, .header > li > a:focus {
	color: #fff !important;
	background-color: transparent;
	outline: 0;
}

.uploadButton.selected {
	background: transparent;
}

.show .header__logo {
	background: rgba(255,255,255,.1) !important;
	background: linear-gradient(rgba(255,255,255,.1)) !important;
    transition: .4s;
}

.show .header__logo:hover {
	background: rgba(255,255,255,.2) !important;
	background: linear-gradient(rgba(255,255,255,.3)) !important;
}

.header__logoLink:focus {
 background-color:rgba(255,255,255,0) !important;
 outline:0
}

.commentForm__input {
	width: 100%;
	background: rgba(0,0,0,.05);
    box-shadow: 0 3px rgba(0,0,0,.1);
	padding: 0 9px;
	font-size: 12px;
	outline: none;
	cursor: pointer;
	border: 0px solid #e5e5e5;
    border-radius: 0px;
}

.commentForm__wrapper {
	height: 0;
	padding: 5px;
	background: transparent;
	border: 0px solid #e5e5e5;
	overflow: hidden;
}

.commentForm.m-active .commentForm__wrapper {
 background: transparent;
 border-color:#ccc
}

.listDisplayToggle__listToggle {
	background-image: url(https://a-v2.sndcdn.com/assets/images/list-toggle-aff6a24.png);
    animation: var(--soundcloud-anime-reverse);
}

.listDisplayToggle__listToggle:hover {
	animation: var(--soundcloud-anime);
}

.listDisplayToggle__badgeToggle {
	background-image: url(https://a-v2.sndcdn.com/assets/images/badge-toggle-5e3bdc3.png);
    animation: var(--soundcloud-anime-reverse);
}

.listDisplayToggle__badgeToggle:hover {
	animation: var(--soundcloud-anime);
}

.header__logoLink {
	background: url(https://a-v2.sndcdn.com/assets/images/header/cloud-e365a47.png) no-repeat 12px 11px;
	background-size: 48px 22px;
	height: 46px;
	width: 69px;
    animation: var(--soundcloud-anime-reverse);
}

.header__logoLink:hover {
	animation: var(--soundcloud-anime);
}

.headerSearch__submit {
	border: 0;
	padding: 0;
	width: 15px;
	height: 15px;
	position: absolute;
	right: 20px;
	top: 15px;
    animation: var(--soundcloud-anime-reverse);
}

.headerSearch__submit:hover {
	animation: var(--soundcloud-anime);
}

.repeatControl.m-none {
    animation: var(--soundcloud-anime-reverse);
}

.repeatControl.m-none:hover {
	animation: var(--soundcloud-anime);
}

.shuffleControl {
	position: relative;
	border: 0;
	padding: 0;
	margin: 0;
	height: 100%;
	width: 100%;
	outline: none;
	background: 0 0;
    animation: var(--soundcloud-anime-reverse);
}

.shuffleControl:hover {
	animation: var(--soundcloud-anime);
}

.skipControl__next {
    animation: var(--soundcloud-anime-reverse);
}

.skipControl__next:hover {
	animation: var(--soundcloud-anime);
}

.playControl {
    animation: var(--soundcloud-anime-reverse);
}

.playControl:hover {
	animation: var(--soundcloud-anime);
}

.skipControl__previous {
    animation: var(--soundcloud-anime-reverse);
}

.skipControl__previous:hover {
	animation: var(--soundcloud-anime);
}

.notificationIcon {
	width: 100%;
	height: 100%;
	position: relative;
    animation: var(--soundcloud-anime-reverse);
}

.notificationIcon:hover {
	animation: var(--soundcloud-anime);
}

.header__navMenu > li > a.header__moreButton {
	width: 46px;
	height: 46px;
	opacity: .75;
	border: 0;
    animation: var(--soundcloud-anime-reverse);
}

.header__navMenu > li > a.header__moreButton:hover {
	animation: var(--soundcloud-anime);
}

.stationTile__actions {
	position: absolute;
	bottom: 0;
	left: 0;
	width: 100%;
	height: 100%;
	padding: 5px;
	background: rgba(0,0,0,.1) !important;
	display: -webkit-flex;
	display: -ms-flexbox;
	display: flex;
	justify-content: flex-end;
	align-items: flex-end;
	box-sizing: border-box;
	opacity: 0;
	visibility: hidden;
	transition: opacity .1s,visibility .1s;
    border-radius: 50% !important;
}

