css:
@-moz-document url-prefix("https://textfac.es") {
/*----------------------------Made-by-yours-truly-Marshy----------------------------*/

body {
	background-color: #202225 !important;
    color: #fff !important;
    background-image: url("https://imgur.com/a/AvOmEkw");
}

.btn-material:not([class^="btn-material btn-material-"]):hover, .btn-material-default:hover {
	background-color: #36393F !important;
    transform: scale(1.05,1.05);
}

.btn-material-link:hover, .btn-material:not([class^="btn-material btn-material-"]):hover, .btn-material-default:hover {
	color: #ffffff;
}

.btn-material:hover:not(.btn-material-link) {
	/*box-shadow: 0 3px 6px rgba(0,0,0,.2),0 3px 6px rgba(0,0,0,.28);*/
    box-shadow: 0 3px #2F3136 !important;
}

ul#facesnav {
	background: transparent;
}

.nav-tabs {
	border: 0px solid #ddd;
}

a {
    color: #fff !important;
}

a:hover {
    color: #fff !important;
    cursor: pointer;
    text-decoration: none;
}

div.shirt-preview-outer {
	display: none !important;
	text-align: center;
	margin: 0 auto;
	width: 100%;
	padding-top: 15px;
}

hr {
	margin-top: 20px;
	margin-bottom: 20px;
	border: 0;
	border-top: 1px solid transparent;
}

.btn-material-link, .btn-material:not([class^="btn-material btn-material-"]), .btn-material-default {
	color: #ffffff;
}

.btn-material {
	position: relative;
	padding: 8px 30px;
	border: 0;
	margin: 10px 1px;
	cursor: pointer;
	border-radius: 4px;
	text-transform: uppercase;
	text-decoration: none;
	color: rgba(255,255,255,.84);
	transition: box-shadow 0s cubic-bezier(.4,0,.2,1);
	outline: 0 !important;
}

.muted {
	color: #ffffff !important;
	font-size: 15px;
}

.nav-tabs > li > a {
	color: #fff !important;
    background-color: rgba(54,57,63,.5) !important;
    box-shadow: 0 3px rgba(47,49,54,.5) !important;
    outline: none !important;
    margin-right: 10px;
    margin-bottom: 10px;
    transition: .4s;
}

.nav-tabs > li > a:hover {
	background: #36393F !important;
	border: 0;
    border-radius: 2px !important;
    box-shadow: 0 3px #2F3136 !important;
}

.nav-tabs > li.active > a, .nav-tabs > li.open > a {
	color: #fff !important;
    background-color: rgba(54,57,63,.5) !important;
    box-shadow: 0 3px rgba(47,49,54,.5) !important;
    border-radius: 2px !important;
    margin-right: 10px;
    cursor: pointer;
    outline: none !important;
}

.nav-tabs > li.active > a:hover, .nav-tabs > li.open > a:hover {
    color: #fff !important;
    background-color: #36393F !important;
    cursor: pointer;
}

textarea.gag-text {
	display: block;
	margin-left: 25%;
	width: 50%;
    resize: none;
    border: none;
    border-radius: 3px !important;
    background: #36393F !important;
    padding: 10px !important;
    margin-bottom: 30px;
}

.btn-material-raised {
	/*box-shadow: 0 3px rgba(47,49,54,.5) !important;*/
	transition: box-shadow 0s cubic-bezier(.4,0,.2,1);
}

.btn-material:not(.btn-material-link), .btn-material-default:not(.btn-material-link) {
	background-color: transparent;
    border-radius: 2px;
    transition: .4s;
}

button, input, select, textarea {
	font-family: inherit;
	font-size: inherit;
	line-height: inherit;
	border: none;
}

select {
    background-color: #36393F !important;
}

option {
    background-color: #36393F !important;
    
}

.btn-material-raised {
	box-shadow: 0 0px 0px rgba(0,0,0,.16),0 0px 0px rgba(0,0,0,.23);
	transition: box-shadow 0s cubic-bezier(.4,0,.2,1);
}
}
