css:
@-moz-document domain("www.amazon.com") {
@media only screen
and (max-width: 1024px) {
    BODY, #nav-upnav, #navbar, #navFooter.navLeftFooter {
        min-width: 0px !important;
    }    
    #nav-upnav, #dv-episode-list .dv-el-packshot, .dp-rating-box,.dv-dp-meta-content, 
    .dv-purchase-options, .dv-dp-packshot, .dv-action-logo,
    #nav-AssociateStripe, #nav-main, DIV>.nav-left, DIV>.nav-right,
    .dp-main-bottom.aiv-container-limited, .tancaccept,
    .dv-el-synopsis-content>SPAN, .dv-el-more-offers,
    #dv-center-features, #dv-customer-reviews,
    .navFooterMoreOnAmazon, #navFooter .navAccessibility.navFooterVerticalColumn,
    .navFooterLinkLine span, .navFooterLinkLine ul,
    .navFooterLine.navFooterLinkLine, #leftNav, #raw-sitewide-rhf, .a-span-last .a-fixed-left-grid,
	#centerBelowExtra {
        display: none;
    }
    #nav-belt .nav-fill {
        height: 55px;
    }
    #navFooter, .av-retail-m-nav-container label {
        margin-top: 0px;
    }
    #dv-dp-title-content {
        margin-right: 0px !important;
        padding-right: 0px;
    }
    #aiv-content-title {
        display: block;
    }
    .num-of-seasons, .release-year {
        display: none;
    }
    #dv-action-box-wrapper {
        float: none;
        display: block;
        margin: auto;
    }
    #dv-action-box-wrapper .a-declarative {
        display: none;
    }
    #dv-action-box-wrapper .a-declarative[data-action="dv-record-reftag"] {
        display: block;
    }
    .dv-dp-top-wrapper {
        padding-bottom: 0px;
    }
    #dv-episode-list {
        width: 100%;
    }    
    #dv-episode-list .dv-el-title-data, #rightResultsATF {
        margin-left: 0px;
    }
    #nav-search {
        display: block;
    }
    #navbar #nav-search {
        padding: 10px;
    }
	#rightResultsATF, .s-span-page, html:not(.a-mobile) .av-retail-m-nav-container {
		min-width: 0px;
	}
	#rightContainerATF {		
		float: left;
		margin-left: 0px;
	}
	.cfMarker {
		width: 50%;
		height: 50%;
	}
	.av-retail-m-nav-container .av-retail-m-nav-lists .av-retail-m-nav-main-item a {
		font-size: 100%;
	}
	.av-retail-m-nav-container .av-retail-m-nav-text-logo, .av-retail-m-nav-container .av-retail-m-nav-lists {
		height: 30px;
		background-position: 0 0px;
	}
	.av-retail-m-nav-container .av-retail-m-nav-lists .av-retail-m-nav-main-item {
		padding-top: 0px;
	}
}
DIV::selection {
	background: transparent !important;
}
}
