  .formerror.is-inline::after {
    background-color: transparent !important;
  }
  body,
  .mainnav-sub-header,
  .flexlist--header,
  .aditem-addon,
  .savedsearchitem-addon,
  #viewad-cntr-num,
  .itemtile-body,
  .dropdown-list > li,
  .textdropdown-options > li,
  .savedsearchitem,
  .followuseritem,
  .modal-dialog > header,
  .modal-dialog-content,
  .text-light,
  .textcounter,
  .headline,
  .headline-huge,
  .headline-big,
  .headline-large,
  .headline-mediumlarge,
  .headline-medium,
  .headline-small,
  .sectionheadline,
  .sectionheadline-underlined,
  .icon-headline,
  .icon-headline-small {
    color: #ccc !important;
  }
  h1:not(#viewad-price),
  h2:not(#viewad-price),
  h3:not(#viewad-price),
  h4:not(#viewad-price),
  h5:not(#viewad-price),
  h6:not(#viewad-price) {
    color: #ccc !important;
  }
