    padding-top: 5px !important;
    margin-bottom: 2px !important;
  }
  #site-header {
    margin-bottom: 10px !important;
  }
  #site-signin.is-centered-button {
    top: 18px !important;
  }
  #site-signin > .login-overlay {
    display: none !important;
  }
  #vip-atf-billboard,
  #store-gallery,
  #home-gallery,
  #vap_adsense-middle,
  .adsenseplacement,
  #brws_banner-supersize,
