css:
@-moz-document url-prefix("https://crafta.ua") {
::-webkit-scrollbar:hover,
::-webkit-scrollbar-track:hover {
background-color: rgba(50, 50, 50, 0.7);
}
::-webkit-scrollbar-track {
overflow: visible!important;
background-color: rgba(50, 50, 50, 0);
}
::-webkit-scrollbar {
width: 5px;
overflow: visible!important;
background-color: rgba(50, 50, 50, 0);
}
::-webkit-scrollbar:hover {
width: 100%!important;
background-color: rgba(50, 50, 50, 0.2);
}
::-webkit-scrollbar-thumb {
box-shadow: 0px 0px 60px 15px rgba(0,107,179,0.73);
background-color: rgb(0,107,179);
}
.cft-lot-item__title:visited{
color: #f50
}

body,
.cft-product-gallery,
[class="cft-header-layout__bottom-row"]{
background: #222
}
.cft-tags,
[class="cft-header-layout__cell cft-header-layout__cart"],
[class="cft-container__title cft-container__title_type_catalog"],
[class="cft-product__ask-seller cft-product__ask-seller_state_fixed js-ask-seller"],
.cft-separator-line,
[class="cft-lot-item__control cft-lot-item__control_type_favourite"],
[class="cft-lot-item__buy-now"],
[class="cft-header-layout__cell cft-header-layout__logo"],
[class="cft-header-layout__top-row"],
.cft-footer,
[class="cft-user-content cft-user-content_margin-top_30"]{
display: none!important;
}
[class="cft-page cft-page_width_1300"]{
min-width: 90%!important;
margin-left:0%;
padding:0;
}
.js-cft-catalog-content{
margin-left:8%;
}
.cft-container__sidebar{
font-size: 0px;
width: 1px;
padding:0;
margin:0;
border: 1px solid #333;
top: 0;
left: -30px;

display: none!important;

}
/*.cft-container__sidebar:after{
content: '';
font-size: 0;
position: absolute;
top: 0;
left: 0;
width: 100%!important;
height: 100%!important;
background: #f50;
}
*/
.cft-container__sidebar:hover{
font-size: 30px;
width: 50%;
}
.cft-content-block{
margin-bottom: 140px;
}
[class="cft-products-list__item"],
.cft-pagination__link,
.cft-lot-item__container{
font-size: 25px;
border: none!important;
border-radius: 0px; 
box-shadow: none!important;
}
.cft-search__input{
font-size: 25px;
border: none!important;
border-radius:0; 
background: #222
}
.cft-search__input:hover{
background: rgba(0,107,179,1)
}
.cft-lot-item__bottom-label,
.cft-lot-item__info{
border: none!important;
color: #c4c4c4;
background: #333;
}
.cft-lot-item:hover .cft-lot-item__bottom-label{
background: none!important;
}
.cft-lot-item:hover .cft-image-holder__image {
border-radius: 10px; 
transform: scale(2);
transition: all 5s;
}
.cft-lot-item__title{
font-size: 30px;
line-height: 28px;
min-height: 65px;
text-align: center!important;
}
.cft-lot-item__price{
color: #f50;
position:absolute;
right: -30px;
font-size: 20px;
bottom: -20px;
text-align: center!important;
}
.cft-lot-item__bids-count{
color: #42f45c;
position:absolute;
left: -140px;
font-size: 15px;
bottom: 0px;
text-align: center!important;
}
.cft-lot-item:hover .cft-lot-item__bids-count{
color: #f50;
}
.cft-lot-item:hover .cft-lot-item__price{
font-size: 30px;
text-shadow: 1px 1px #333;
right: -50px;
bottom: 20px;
}
.cft-lot-item:hover .cft-lot-item__info{
color: white;
background: linear-gradient(to bottom, rgba(125,185,232,0) 0%,rgba(100,100,100,1) 74%,rgba(100,100,100,1) 98%);
text-align: center;
bottom: -30px;
}
.cft-lot-item:hover .cft-lot-item__title{
font-size: 30px;
line-height: 30px;
text-decoration:none;
vertical-align:auto;
}
.cft-lot-item__container{
  -webkit-transition-property: transform;
  transition-property: transform;
  -webkit-transition-duration: 0.5s;
  transition-duration: 0.5s;
  -webkit-transition-timing-function: cubic-bezier(0.52, 1.64, 0.37, 0.66);
  transition-timing-function: cubic-bezier(0.52, 1.64, 0.37, 0.66);
}
.cft-lot-item__container:hover{
background-color: rgba(0,107,179,0.3);
  -webkit-transform: scale(1.5);
  transform: scale(1.5);
  -webkit-transition-timing-function: cubic-bezier(0.52, 1.64, 0.37, 0.66);
  transition-timing-function: cubic-bezier(0.52, 1.64, 0.37, 0.66);
animation: pulse 2s infinite;
}
@keyframes pulse {
0% {-moz-box-shadow: 0 0 0 0 rgba(100,100,100,0.8);
box-shadow: 0 0 0 0 rgba(125,185,232,0.8);}
70% {-moz-box-shadow: 0 0 0 20px rgba(100,100,100,0);
box-shadow: 0 0 0 20px rgba(0,107,179,0);}
100% {-moz-box-shadow: 0 0 0 0 rgba(0,107,179,0);
box-shadow: 0 0 0 0 rgba(0,107,179,0);}
}

.cft-pagination__link {
margin-bottom: 140px;
width: 50px;
height: 50px;
}
.cft-pagination__link:hover {
border-radius: 50px; 
animation: squish 2s ease-in infinite!important;
}
@keyframes squish{
5% {transform: scale(1.2, 0.9);}
10% {transform: scale(0.9, 1.2) translate(0, -4px);}
15% {transform: scale(1);}
}
.cft-pagination__link:hover {
color: white;
font-size: 25px;
background-color: rgba(255,85,0,1)!important;
box-shadow:0px 11px 28px -10px rgba(255,85,0,0.9), 0px -11px 28px -10px rgba(255,85,0,0.9)!important;
}
[class="cft-pagination__link cft-pagination__link_state_active active disabled"] {
color: white;
font-size: 25px;
background-color: rgba(255,85,0,1)!important;
box-shadow:0px 11px 28px -10px rgba(255,85,0,0.9), 0px -11px 28px -10px rgba(255,85,0,0.9)!important;
}
}

@-moz-document url-prefix("https://crafta.ua") {
.cft-product.cft-product_type_collecting,
.cft-product__info{
font-size: 25px;
color: rgba(125,185,232,1);
}
*{
border: none!important;
box-shadow: none!important;

}
.cft-price__value{
color: #f50;
font-size: 40px;
text-align: center!important;
}

.cft-tabs__caption{
font-size: 25px;
color: #f7932b!important;
}
.cft-tabs__item.active{
height: 45px;
background: #333
}
.cft-balance-transactions__row:nth-child(2n+1) {
    background: #333;
color: grey!important;
}
.cft-container{
margin-bottom: 140px;
}
}
