css:
@-moz-document domain("peeper.alternate-life.de") {
body,
.ui.menu,
.borderless,
.ui.segment,
.ui.secondary.segment {
    color: #888888 !important;
    background: #1b1c1d;
}

.ui.secondary.menu a {
    background: #3c3c3c !important;
}

a:hover {
    color: #60cbff;
}

.ui.menu.borderless.stackable {
    background: #282828;
    border-bottom: 1px solid rgb(19, 19, 19);
    box-shadow: 0 1px 2px 0 rgb(19, 19, 19) !important;
}

.ui.menu .item,
.t-muted {
    color: #888888;
}

.ui.menu a.item:hover,
.ui.header {
    color: #fff;
}

/*.ui.pagination.menu.mini,
#sidebarLeft .ui.segment,
#sidebarRight .ui.segment,
.ui.segments {
    border: 1px solid #3c3c3c !important;
    box-shadow: 0 1px 2px 0 rgb(10, 10, 10) !important;
}*/

.ui.pagination.menu .active.item {
    color: #fff;
}

.ui.warning.message {
    color: #888888;
    background: #484e54;
    border: 1px solid rgb(60, 60, 60);
    box-shadow: 0 1px 2px 0 rgb(60, 60, 60) !important;
}

.ui.inverted.segment,
.ui.primary.inverted.segment {
    background: #131313;
}

.ui.statistic > .label,
.ui.statistics .statistic > .label {
    color: #888888;
}

a.active.item {
    color: #fff !important;
}

.ui.menu .item.disabled,
.ui.menu .item.disabled:hover {
    color: #454545 !important;
}

.ui.gray.horizontal.label.tiny {
    color: #fff;
    background: #45474f;
}

.ui.menu .item:before {
    background: #3c3c3c
}

.footer {
    margin-top: 30px !important;
    border-top: 1px solid #3c3c3c !important;
}



/*Places*/
.sub.header {
    color: #cdcdcd !important;
}

.place .ui.label {
    color: #fff;
    background: #45474f;
}



/*Events*/
.ui.dividing.header {
    border-bottom: 1px solid #101010;
}

.column .ui.segment {
    border: 1px solid #3c3c3c !important;
    box-shadow: 0 1px 2px 0 rgb(10, 10, 10) !important;
}

.row .ui.label {
    color: #fff;
    background: #45474f;
}

.ui.yellow.button {
    color: #000
}


/*Kleinanzeigen*/
.ui.segment.teal label {
    color: #fff !important;
}

.ui.gray.large.horizontal.label {
    color: #fff;
    background: #45474f;
}


/*Popup Box*/

#composeModal {
    border-radius: 1rem;
}

.ui.modal > .header {
    background: #1b1c1d;
    color: #fff;
}

.ui.modal > .content {
    background: #1b1c1d;
}


/*Bilder*/
.ui.card, .ui.cards > .card {
    background: #1b1c1d;
    box-shadow: 0 1px 3px 0 #000,0 0 0 1px #4e4e4e;
}

.ui.card .meta, .ui.cards > .card .meta {
    color: #888888 ;
}
}
