css:
@-moz-document regexp("https://view\\.ost\\.com.*") {
body {
    background: #252525;
}

.card-body {
    background: transparent;
}
.card {
    border: none;
    background: transparent;
}
rect {
    fill: transparent!important;
}
.container-summary {
    background: transparent;
}
th,
td,
tr {
    background: transparent!important;
}
tbody {
    background-color: #333!important;
}
table.token-table th,
table.token-table td {
    border-top: 1rem solid #252525;
}
table.token-table th .rank,
table.token-table td .rank {
    border-right: none;
    background: #4f5f5d;
}
table.token-table td {
    color: #bbb;
}
dataTables_wrapper .dataTables_paginate .paginate_button {
    color: #9cc5c1!important;
    background: #333;
    border: 1px solid #9cc5c1;
}
table.token-table thead th {
    background-color: #252525!important;
    border: none;
}
table.token-table tbody,
table.token-table tr {
    background-color: #222!important;
}
.container-summary .card .card-body {
    background: #222;
    margin-top: -20px;
}
header.container-fluid {
    box-shadow: none
}
.container-page-title {
    background: #333;
    border-top: solid 1px #666;
}
.badge-primary {
    background: #222;
    color: #ededed;
}
.container-token-details .info-card .card-body {
    background-color: transparent;
}
.container-token-details .details-card .card-body:nth-of-type(even) {
    background-color: #222;
    border-radius: 10px;
}

.card-graph .card-body .graph-header {
    border: none
}
.container-datatable .nav-tab-ost .nav-pills {
    border-bottom: none;
    box-shadow: none;
}
.container-datatable .nav-tab-ost .nav-pills .nav-link.active:after {
    border: 6px solid #666;
}
.dataTables_wrapper .dataTables_paginate .paginate_button {
    background: #222;
    border-radius: 5px;
}
.dataTables_wrapper .dataTables_paginate .paginate_button.disabled,
.dataTables_wrapper .dataTables_paginate .paginate_button.disabled:hover {
    color: #555!important;
    background: #222;
    border: 1px solid #555;
    border-radius: 5px;
}
table.token-table th a,
table.token-table th a:visited,
table.token-table th a:active,
table.token-table th a:hover,
table.token-table td a,
table.token-table td a:visited,
table.token-table td a:active,
table.token-table td a:hover {
    color: #dcb24d;
}
input {
    background: transparent!important;
    border: none!important;
}
.navbar-ost .input-group .input-group-append .input-group-text {
    background: #252525;
    border: none;
}
.navbar-light .navbar-nav .nav-link {
    color: #666;
}
.value-color {
    color: #7ba9b7;
}
}
