css:
@-moz-document domain("bitchute.com") {
#nav-top-menu .logo {
    white-space: nowrap;
    height: 56px;
}
#nav-top-menu .logo img {
    margin: 17px 0;
    height: 28px;
}
#nav-top-menu .search-box {
    position: absolute;
    top: 0;
    left: 300px;
    margin: 18px 0 27px 20px;
    width: 300px;
}
.svg-inline--fa.fa-w-16 {
    width: 16px;
}
.svg-inline--fa.fa-w-18 {
    width: 17px;
}
.help-us-grow span {
    position: absolute;
    bottom: -16px;
    left: 50%;
    transform: translate(-50%, 0);
    font-size: 12px;
    font-weight: bolder;
    color: #ef4136;
    cursor: default;
    display: none;
}
.svg-inline--fa.fa-w-14 {
    width: 15px;
}
#nav-top-menu .login>ul>li img {
    display: inline-block;
    width: 50px;
    height: 50px;
    vertical-align: middle;
    border: none;
}
#nav-top-menu .login .notify-count {
    position: absolute;
    top: -11px;
    right: -9px;
    font-size: 8px;
    font-weight: 700;
    padding: 1px 4px 0;
    border-radius: 10px;
    z-index: 1;
}
#nav-top-menu .login {
    position: absolute;
    top: 0;
    right: 10px;
    padding: 8px 10px 6px 0;
}

#nav-top-menu .dropdown-toggle img {
    border-radius: 50%;
    display: inline-block;
    width: 36px;
    height: 36px;
    vertical-align: middle;
    border: thin solid transparent;
}

#nav-top-menu .user-menu .user-menu-icon {
    position: absolute;
    top: 4px;
    left: -1px;
    display: none;
}

#nav-top-menu {
 background-image:none !important;   
}

#nav-top-menu .logo img {
    margin: 17px 0;
    height: 28px;
    visibility: hidden;
}

#nav-top-menu .logo {
    white-space: nowrap;
    height: 56px;
    background-image: url(https://s3.us-east-2.amazonaws.com/x929147wdklad/bitchute-logo.svg);
    background-repeat: no-repeat;
    background-position: center;
}

img.logo-icon.hidden-sm.hidden-md.hidden-lg.hidden-xl {
    width: 130px;
}

#nav-top-menu .search-box {
    border: thin solid #373538;
    background: #121212;
}

#nav-top-menu .search-box {
    position: absolute;
    top: 0;
    left: 188px;
    margin: 15px 0 27px 20px;
    width: 300px;
}

#nav-top-menu .search-box .form-control {
    position: relative;
    padding: 6px 22px;
    height: 30px;
    background: 0 0;
    border: none;
    box-shadow: none;
    transition: all .3s ease-in-out .05s;
}


.faa-vertical.animated, .faa-vertical.animated-hover:hover, .faa-parent.animated-hover:hover>.faa-vertical {
    -webkit-animation: none;
    animation: none;
}
}
