@-moz-document domain("resizemybrowser.com") {
/*----------------------------Made-by-yours-truly-Marshy----------------------------*/

#div1 ul {
	margin: 6px 6px 6px 0;
	padding: 6px;
	width: 10.1em;
	color: #fff;
	border: 0px solid #d3d8ed !important;
	cursor: pointer;
    background-color: #36393e !important;   
    border-radius: 3px !important;
    margin-bottom: 10px !important;
    box-shadow: 0 5px 0 #2f3136 !important;
    animation: bounce-reverse 1500ms linear both;
}

#div1 ul:hover {
    box-shadow: 0 4px 0 #68c06e !important;
    background-color: #6ce679 !important;
    animation: bounce 1500ms linear both;
} 

