} 

body {
    background-color: #1e2124 !important; 
}

#core {
	background-color: #1e2124 !important;
	margin: 6px 0 4px 0;
	padding: 18px;
	border-radius: 4px 4px 0 0;
	box-shadow: 0 0 0px #000 !important;
}

header {
	font-size: 1.6em;
	font-weight: bold;
	text-align: center !important;
	text-shadow: 0 0px 0 #fff !important;
	/* display: none; */
}

header a {
	color: #fff !important;
	text-decoration: none;
	margin-left: 2px;
}

#add, #delete {
	background-color: rgba(54,57,62,1) !important;
	padding: 4px 6px;
	width: 10.1em;
	color: #aaa;
	border: 0px solid #eaeaea !important;
	cursor: pointer;
    border-radius: 2px !important;
    box-shadow: 0 3px rgba(47,49,54,1) !important;
    margin-bottom: 8px !important;
}

#add:hover, #delete:hover {
	background-color: rgba(54,57,62,.7) !important;
	padding: 4px 6px;
	width: 10.1em;
	color: #aaa;
	border: 0px solid #eaeaea !important;
	cursor: pointer;
    border-radius: 2px !important;
    box-shadow: 0 3px rgba(47,49,54,.7) !important;
    animation: bounce 1500ms linear both;
    backface-visibility: hidden;
}

#sizeMes {
	background-color: rgba(54,57,62,1) !important;
	padding: 10px 18px 10px 18px;
	margin: 4px 0 0 0 !important;
	box-shadow: 0 0 0px #a9a9a9 !important;
    color: #fff !important;
	border-radius: 5px 5px 0 0 !important;
}

#innerOuter {
	background-color: rgba(47,49,54,1) !important;
	padding: 10px 18px 10px 18px;
	margin: 0 0 8px 0 !important;
	clear: left;
	border-radius: 0 0 5px 5px;
	box-shadow: 0 0 0px #a9a9a9 !important;
    color: #fff !important;
}

#currentSize {
	background-color: rgba(47,49,54,1) !important;
	color: #fff !important;
	font-size: 1.2em;
	font-weight: bold;
	text-shadow: 0 0px 0 #fff !important;
	padding: 8px;
	margin-left: 6px;
	-webkit-border-radius: 19px;
	-moz-border-radius: 19px;
	border-radius: 19px;
}

#IObg {
	width: 64px;
	height: 21px;
	background: url(https://imgur.com/a/QflMhAL) no-repeat;
    cursor: pointer !important;
}

#IObutton {
	width: 29px;
	height: 21px;
    filter:opacity(6%) !important;
    border-radius: 3px;
	left: 0;
	bottom: 21px;
	position: relative;
    cursor: pointer !important;
}

#inner {
	float: left;
	margin: 2px 0 0 0;
    color: #fff !important;
    cursor: pointer !important;
}

#outer {
	float: left;
	margin: 2px 0 0 0;
    color: #fff !important;
    cursor: pointer !important;
}

footer, #domainforsale {
	width: 760px;
	padding: 4px;
	margin: 6px auto 20px auto;
	text-align: right;
	font-size: .8em;
	text-shadow: 0 1px 0 #fff;
	display: none !important;
}

#overlay article {
	width: 280px;
	margin: 80px auto;
	padding: 10px 25px;
	background-color: rgba(47,49,54,1) !important;
	border-radius: 4px;
    color: #fff !important;
	box-shadow: 0 0 14px #4f4f4f;
}

input {
	font-size: 1em;
	font-weight: bold;
	display: block;
	width: 270px;
	height: 1.8em;
	margin: 2px 0 10px 0;
	border: 0px solid silver !important;
    background-color: rgba(54,57,62,1) !important;
	border-radius: 4px;
    color: #fff !important;
}

#overlay p {
	color: #fff !important;
	margin: 0 0 15px 0;
	font-weight: bold;
	font-size: 1em;
}

#overlay #overlayMes {
	color: #fff !important;
	background-color: #ff3b3b !important;
	font-size: .9em;
	display: inline-block;
	padding: 5px;
	border-radius: 2px;
	display: none;
}

fieldset #create {
	font-weight: lighter !important;
	margin: 5px 120px 5px 0;
	text-decoration: none;
	border: 0px solid silver !important;
	border-radius: 2px;
	padding: 5px 12px;
    background-color: transparent !important;
}

fieldset #create:hover {
    background-color: rgba(54,57,62,.7) !important;
}

fieldset a {
	display: inline-block;
	font-size: .9em;
	color: #5e5e5e !important;
	text-decoration: none !important;
}

.highlight {
	display: inline-block;
	color: #fff;
	border-radius: 2px;
	padding: 0 5px 0 5px;
	margin: 0 0 2px 0;
	background-color: rgba(47,49,54,1) !important;
}

#divMes {
	float: left;
	margin-left: 6px;
	padding: 6px;
	width: 470px;
	height: 35px;
	border: 0px dashed silver !important;
	border-radius: 2px;
	color: #717171 !important;
	font-size: .9em;
	font-weight: bold;
	visibility: hidden;
}

