css:
@-moz-document url-prefix("https://app.wire.com") {
 #center-column {
     background-color: #2c2c2b !important;
     color: #e6e6e6 !important;
 }
 .controls-left {
     background-color: #2c2c2b !important;
     color: #e6e6e6 !important;
 }
 .controls-right {
     background-color: #2c2c2b !important;
     color: #e6e6e6 !important;
 }
 .controls-center {
     background-color: #2c2c2b !important;
     color: #e6e6e6 !important;
 }
 span > .context-menu {
     background-color: #2c2c2b !important;
     color: #e6e6e6 !important;
 }
 #conversation-title-bar > * {
     background-color: #2c2c2b !important;
     color: #e6e6e6 !important;
 }
 div > .controls-center {
     background-color: #2c2c2b !important;
     color: #e6e6e6 !important;
 }
 ul.ephemeral-timer {
     background-color: #2c2c2b !important;
     color: #e6e6e6 !important;
 }
 span.context-menu {
     background-color: #2c2c2b !important;
     color: #e6e6e6 !important;
 }
 time {
     background-color: #2c2c2b !important;
     color: #e6e6e6 !important;
 }
 span.message-status {
     background-color: #2c2c2b !important;
     color: #e6e6e6 !important;
 }
 div.detail-view-main {
     background-color: #2c2c2b !important;
     color: #e6e6e6 !important;
 }
 img.detail-view-image {
     max-width: initial !important;
 }
 header.detail-view-header {
     background-color: #2c2c2b !important;
     color: #e6e6e6 !important;
 }
 footer.detail-view-footer {
     background-color: #2c2c2b !important;
     color: #e6e6e6 !important;
 }
 #conversation-input-bar {
     background-color: #2c2c2b !important;
 }
 conversation-list-calling-cell {
     background-color: #2c2c2b !important;
 }
 .call-ui__button {
     background-color: rgba(0, 0, 0, 0.25) !important;
 }
 .call-ui__button svg > path {
     fill: #ffffff !important;
 }
 hangup-icon > svg > path {
     fill: red !important;
 }
 conversation-list-calling-cell .conversation-list-cell-name {
     color: #e6e6e6 !important;
 }
 conversation-list-calling-cell .conversation-list-cell-description {
     color: rgba(255, 255, 255, 0.68) !important;
 }
 .controls-right-button svg path {
     fill: #b7b7b7 !important;
 }
 .conversation-title-bar-icon svg path {
     fill: #b7b7b7 !important;
 }
 .preferences-content {
     color: #e8e8e8 !important;
 }
 .preferences-options-checkbox-label {
     color: #e8e8e8 !important;
 }
 span.preferences-label {
     color: #e8e8e8 !important;
 }
 div.preferences-titlebar {
     background-color: #2c2c2b !important;
 }
 .preferences-av-select {
     color: #757575 !important;
 }
 .content-titlebar {
     background-color: #2c2c2b !important;
 }
}
