css:
@-moz-document domain("mijnschoolnet-elo.somtoday.nl"), domain("somtoday.nl") {
:root{
  --prim-bg: #121212;
  --seco-bg: #232323;
  --thir-bg: #1c1c1c;
  --hover-bg: #474747;
  --prim-text: white;
  --seco-text: #aeb3b4;
}

body, .loaderFade {
  background-color: var(--prim-bg);
}

#header-wrapper, #main-menu a, .dayTitle, .roster-table-header .day .huiswerk-items, .pauze, .uurNummer, #master-panel .date div, .goverview tbody td, .goverview th, .tog-explanation, .type, #master-panel, .m-wrapper.active, .explanation, .tog-level, .gperiod.expand .tog-noperiod, .glevel, .section .grade, .section .weging, .box {
  background-color: var(--seco-bg);
}

#detail-panel-wrapper, div.block.content {
  background-color: var(--thir-bg);
}

a, #main-menu a, .pauze, .uurNummer, #master-panel h2, body, h3, h4, .tog-explanation, .type, .IconFont.type, .afspraakVakNaam, h1, h2, .m-element, .weekitems .weekitemLabel, .toekenning a {
  color: var(--prim-text);
}

.afspraakLocatie, .sub, .section, .goverview th, .explanation, .gperiod .tog-period, .box{
    color: var(--seco-text);
}

#content {
  border: none;
  box-shadow: none;
}

#master-panel {
  color: var(--prim-text) !important;
  padding-top: 0;
}

.m-element {
  border: none;
}

.m-wrapper, #master-panel .panel-header, .load-more, #master-panel~#detail-panel-wrapper, .roster-table-header, .roster-table-content, .hours, .hour, .day, .weekitems, .goverview tbody td, .goverview th, .tog-explanation, .type {
  border: 1px solid var(--prim-bg) !important;
}

.m-wrapper {
  min-height: 0;
}

.left {
  padding-top: 10px;
}

.m-element:hover, .load-more:hover {
  background-color: var(--hover-bg);
}

.dayTitle {
  border-bottom: solid 3px var(--prim-bg);
}

.roster-table-content .hours .hour {
  width: 957px;
}

.m-wrapper.active {
  border-right: none !important;
  margin-right: -1px;
  background-image:none;
  color: red;
}

#master-panel .date div {
  width: 32px;
  height: 44px;
  margin: -1px 0 0 -34px;
}

#master-panel .date {
  border-top-color: var(--prim-bg);
}

.m-element.no-click:hover {
  border-bottom: none;
  background-color: var(--hover-bg);
}

.section .block {
    background-color: var(--thir-bg);
    border: none;
}

.afspraak{
    background-color: transparent;
    margin: 1;
}

.afspraak .toekenning{
    background-color: transparent;
}

.explanation{
  border-left: 1px solid var(--prim-bg);
  border-right: 1px solid var(--prim-bg);
  border-bottom: 1px solid var(--prim-bg);
}

.glevel, .box{
  border: 1px solid var(--prim-bg);
}

.gperiod{
  border-top: 1px solid var(--prim-bg);
}

.gperiod .tog-period{
  border-top: 0;
}

div.block.content{
  font-size: 18px;
}

.button-silver-deluxe span{
  background: none;
  text-shadow: none;
  color: var(--seco-text);
}

.button-silver-deluxe{
  box-shadow: none;
  border: 1px solid var(--prim-bg);
  background-color: var(--seco-bg);
  margin-top: 3px;
  margin-right: 5px;
}
}
