css:
@-moz-document domain("freshservice.com") {
.table-bordered {
    border: 1px solid #e5e5e5;
    border-collapse: separate;
    border-left: 0;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    -ms-border-radius: 4px;
    -o-border-radius: 4px;
    border-radius: 4px;
    background-color: #269adf;
}

.page-wrapper {
    padding: 0;
    position: relative;
    background-color: #269adf;
}

.inner-banner-strip {
    padding-top: 20px;
    padding-bottom: 20px;
    background-color: #0389ad;
    color: white;
    -webkit-box-shadow: 0 4px 9px -2px rgba(0,0,0,0.2);
    -moz-box-shadow: 0 4px 9px -2px rgba(0,0,0,0.2);
    box-shadow: 0 4px 9px -2px rgba(0,0,0,0.2);
    position: relative;
    z-index: 1;
}
.nav-main li a.active+.nav-links {
    display: block;
    height: 100%;
    background-color: #269adf;
}

a {
    color: #b7dce6;
}
a:visited {
    color: #b7dce6;
}
.notifications-list-wrapper
{
    
 height: 600px;
}
#user_notify_header #user-notifications-popover{
    width: 600px;

       -webkit-animation: fade-in .3s cubic-bezier(1, -0.15, 0.18, 1.05) 1, move-down .5s cubic-bezier(0.77, 0.03, 0.2, 1.65) 1;
        -moz-animation: fade-in .3s linear 1, move-up .3s linear 1;
        -animation: fade-out .3s cubic-bezier(1, -0.15, 0.18, 1.05) 1, move-down .5s cubic-bezier(0.77, 0.03, 0.2, 1.65) 1;
        
}

#user_notify_header #user-notifications-popover .notifications-list-wrapper .notifications-list-scroll {
height: 800px;


}

#user_notify_header #user-notifications-popover .notifications-list a .notification-inner .notification-subject{
     padding-bottom: 5px;
    color: #414655;
    font-size: 13px;
    white-space: unset;
    text-overflow: ellipsis;
    overflow: visible;
    word-wrap: break-word;
    word-break: normal;
    width: 500px;

    
}

#user_notify_header #user-notifications-popover .notifications-list-wrapper .notification-list-filter {
  /* font-size: 13px;
    width: 50px;
    height: 70px;*/
    display: block;
text-align: center;

    
}
div[class^="notification-type"]{
    color: #414655;
    
}

#user_notify_header #user-notifications-popover header {
    
    display: none;

}

@-webkit-keyframes fade-in {
	from   { opacity: 0; }
	to { opacity: 1; }
}
@-webkit-keyframes fade-out {
	from   { opacity: 1; }
	to { opacity: 0; }
}
@-moz-keyframes fade-in {
	from   { opacity: 0; }
	to { opacity: 1; }
}

@-webkit-keyframes move-up {
	from   { bottom: 30px; }
	to { bottom: 42px; }
}
@-moz-keyframes move-up {
	from   { bottom: 30px; }
	to { bottom: 42px; }
}


}
