css:
@-moz-document url-prefix("https://myanimelist.net/anime.php?id="), url-prefix("https://myanimelist.net/manga.php?id="), regexp("https://myanimelist.net/anime/(?!season|producer|genre).*"), regexp("https://myanimelist.net/manga/(?!genre|magazine).*") {
body {
    background: url(https://i.imgur.com/7iEZcCx.png) repeat rgba(var(--colorLightGray)) !important; }

/* Colours */
:root {
    --colorBlue: 112,200,237;
    --colorPink: 244,101,146;
    --colorOrange: 255,196,108;
    --colorGreen: 112,208,182;
    --colorPurple: 140,125,208; }

/* Undo lowercase */
body, div[id*="raArea"] + div + div[class*="bgColor"] > .spaceit_pad:nth-child(2),
div[id*="simaid"] div[class*="bgColor"] .spaceit_pad:nth-child(2),
div.spaceit.borderClass div.lightLink.spaceit,
tr:first-child > td[valign="top"]:first-child h2,
tr:first-child > td[valign="top"][width="225"]:first-child,
tr:first-child > td[valign="top"][align="right"]:first-child,
td[style^="pad"] #horiznav_nav, td[style^="pad"] .normal_header,
td[style^="pad"] div .normal_header, td[style^="pad"] table, td[style^="pad"] h2,
td[style^="pad"] a, td[style^="pad"] .spaceit, .news-container .title, #searchBar #topSearchValue,
td[style^="pad"] small, .incrementalSearchResultList .list.separator {text-transform: none !important;}
.header-list {left: 552px !important;} /* Move 'List' */

#myanimelist {width: 1234px !important;}

#horiznav_nav ul {font-family: Segoe UI !important;}
#horiznav_nav ul li {background: none !important;}

#horiznav_nav {
    position: absolute !important;
    background: linear-gradient(to right, #8c7dd0, #f46592) !important;
    box-shadow: 0 2px 20px rgba(50,55,65,.1);
    border-radius: 10px !important;
    border: none !important;
    left: 0 !important;
    top: -84px !important;
    padding: 10px !important;
    width: 97.9% !important; }
td[width="225"] + td #horiznav_nav {margin: 5px 0 10px 0 !important;width: 97.9% !important;} /* A/M/Char/People */ /* Mod */

#contentWrapper {background: none !important;margin-top: 30px !important;}
#contentWrapper > div:nth-child(1) {position: relative !important;margin-top: -30px !important;margin-bottom: 109px !important;}

#content, #contentHome {background: none !important;border: none !important;padding: 0 !important;}

/* Upper navigation */
#menu {border-radius: 10px 10px 0 0 !important;width: 1234px !important;}

/* Notifications & Messages (Buttons) */
.header-notification {left: 1197px;}
.header-message {left: 1167px !important;}

/* Notifications (Inner) */
.header-notification-item a, .header-notification-item .time,
.header-notification-item .message, .header-notification-item .category,
.header-notification-item-content, .header-notification-view-all a,
.header-notification-dropdown-inner h3 .mark-all {
    color: white !important; }
.header-notification-dropdown-inner h3 .mark-all {position: relative; top: -3px;}
.header-notification-dropdown .arrow_box {
    background: linear-gradient(to bottom right, #8c7dd0, #70c8ed, #70c8ed, #8c7dd0) !important;
    box-shadow: none !important;
    color: white !important; }
.header-notification-dropdown .arrow_box h3 {
    background: linear-gradient(to left,white 0%,white 100%) left bottom transparent no-repeat !important;
    background-size: 100% 3px !important; }
.header-notification-view-all {
    background: linear-gradient(to left,white 0%,white 100%) left top transparent no-repeat !important;
    background-size: 100% 3px !important;
    border-top: none !important;
    padding-top: 3px; }

/* Width fix */
.notification-item-list .notification-item {width: 98.3% !important;}
.notification-item-layout .notification-item-content {width: 44em !important;max-width: 44em !important;}
.notification-item-layout.common .notification-item-content.with-actions {width: 60% !important;}
.notification-item-layout.common .notification-item-content {width: 75% !important;}

/* Sign Up */
.header-menu-login .btn-signup {left: 1166px !important;}

#horiznav_nav ul {display: block !important;margin: 0 !important;padding: 0 !important;text-align: center;width: 100% !important;}

#horiznav_nav ul li a, #horiznav_nav ul li a:hover, #horiznav_nav ul li a.horiznav_active {
    background: none !important;
    border: none !important;
    box-shadow: none !important;
    font-style: normal !important;
    color: rgba(var(--colorText)) !important;
    font-family: Segoe UI Semibold !important;
    border-radius: 3px !important;
    padding: 2px 4px !important;
    transition: 0s !important;
    font-size: 13px !important; }

#horiznav_nav ul li a.horiznav_active {font-size: 16px !important;}

#horiznav_nav ul li a:hover, #horiznav_nav ul li a.horiznav_active:hover, #horiznav_nav ul li a.horiznav_active {
    background: none !important;
    border: none !important;
    box-shadow: none !important;
    color: white !important;
    font-family: Segoe UI Semibold !important; }

#horiznav_nav ul li a:after {content: "," !important;}
#horiznav_nav ul li:last-child a:after {content: "" !important;}

/* Headers */
.normal_header, h2, .header, body.news .menu-category, .navi-seasonal .horiznav_nav, 
.news-container .title, .menu-category-tags, .normal_header.pt8,
body.featured-news h3.header3.header3-underline,
.menu-tags-group, .user-profile h4, h5,
#advancedSearch .fs12.fw-b.mt24.mb8,
.video-menu-category, .widget .widget-header,
.widget .ranking-header, .border_solid {
    border-bottom: none !important;
    background: linear-gradient(to left,#f46592 0%,#8c7dd0 100%) left bottom transparent no-repeat !important;
    background-size: 100% 3px !important;
    padding: 3px 0 6px 2px !important; }

.h2_overwrite {background: none !important;} /* Fix for double headers */

/* Lower navigation */
#contentWrapper > div .h1, .for-pc + div #contentWrapper > .h1 {
    border: none !important; border-radius: 0 0 10px 10px !important;
    padding: 12px 20px 14px !important;
    max-height: 20px !important; }

/* Right aligned text */
.header-right {margin: 13px 15px 0 0 !important;} /* Mod */

/* 'Edit ??? Information' */
.h1.edit-info .h1-title, .for-pc.h1.edit-info .h1-title {width: 1044px !important;} /* Mod */
#contentWrapper > div .h1.edit-info {padding-right: 20px !important;} /* Mod */

/* Rec boxes */
td[valign="top"] + td[valign="top"] > div + .borderClass.bgColor1:hover {margin-left: 387px !important;} /* Mod */
.spaceit a.js-similar-recommendations-button {right: 28px !important;} /* Mod */
.detail-user-recs-text {max-width: 1000px !important;} /* Expanded recs */

/* Sub buttons, below Anime img */
#profileRows, .profileRows.pb0 {margin: 0 !important;padding: 10px 0 !important;width: 233px !important;}
.profileRows.pb0 {margin-bottom: -18px !important;}
.profileRows.pb0 + #addtolist {margin: 18px 11px 18px 14px !important;}
h2.mt8 + #addtolist {margin: 15px 11px 16px 14px !important;}
#profileRows a:first-of-type {border-top: none !important;}
#addtolist + #profileRows {padding-top: 0 !important;}

#profileRows a, .profileRows.pb0 a, .js-favorite-button + div.fn-grey5 {
    color: white !important;
    padding: 7px 0 9px !important;
    text-align: center;
    letter-spacing: 1px;
    border-radius: 5px !important;
    box-shadow: none !important;
    background: linear-gradient(270deg, #f46592, #ffc46c, #f46592, #ffc46c) !important;
    background-size: 400% 400% !important;
    animation: gradient 5s ease infinite;
    font-style: normal !important;
    border-bottom: none !important; }

#profileRows a:hover, .profileRows.pb0 a:hover, .js-favorite-button + div.fn-grey5 {
    background: linear-gradient(270deg, #ffc46c, #f46592, #ffc46c, #f46592) !important;
    background-size: 400% 400% !important;
    color: white !important;
    box-shadow: none !important;
    border-bottom: none !important; }

@keyframes gradient {
    0%{background-position:0% 50%}
    50%{background-position:100% 50%}
    100%{background-position:0% 50%}}

/* Left frame of Profile/Anime/People/Char page */
td.profile_leftcell, tr td:first-child.borderClass[width="225"] {
    background: white !important;
    border: none !important;
    border-radius: 10px !important;
    margin: -79px 0 0 0 !important;
    display: block !important; }

/* Right frame of Profile/Anime/People/Char page */
td.profile_leftcell + td.pl8 {padding-left: 5px !important;width: 100% !important;}
tr td:first-child.borderClass[width="225"] + td[style^="padding"] {
    box-shadow: 0 2px 20px rgba(50,55,65,.1);
    padding: 15px 25px !important;
    background: white;
    border-radius: 10px;
    display: table-cell;
    left: 0 !important;
    width: 877px !important;
    max-width: 877px !important;
    position: relative; }
tr td:first-child.borderClass[width="225"] + td[style^="padding"] td.pb24 {padding-bottom: 0 !important;} /* Mod */

/* Adds left frame padding (non-Profile) */
tr td:first-child[width="225"][style="border-width: 0 1px 0 0;"] {
    padding: 17px 20px 22px 17px !important;
    width: 230px !important;
    box-shadow: 0 2px 20px rgba(50,55,65,.1); }

/* Right frame */
.anime-slide-block[style="width: 800px;"], .anime-slide-block[style="width: 702px;"] {width: 877px !important;}

/* Anime cover image */
td[width="225"] div[style*="center"] img:first-of-type {
    background: linear-gradient(to right, #70c8ed, #f46592, #8c7dd0, #ffc46c, #70d0b6) !important;
    background-size: 400% 400% !important;
    border: none !important;
    box-shadow: none !important;
    animation: gradient 5s ease infinite; }

/* Cover image opacity */
td[width="225"] div[style*="center"] img:first-of-type, a.js-picture-gallery img {opacity: 1 !important;}
td[width="225"] div[style*="center"] img:first-of-type:active, a.js-picture-gallery img:active {opacity: 0.8 !important;}

/* Edit Info Popup */
#editdiv {right: 9px !important;top: 55px !important;}

/* Add to list box */
#addtolist {
    background: linear-gradient(to bottom right, #8c7dd0, #70c8ed, #70c8ed, #8c7dd0) !important;
    box-shadow: none !important;
    padding: 16px 10px !important;
    color: white !important; }

#addtolist a {color: white !important;}

#addtolist .inputButton, .inputButton + #addtolistresult a, h2.mt8 + #addtolist .inputButton + small a {
    background: none !important;
    border-bottom: 2px dotted white !important;
    border-radius: 2px !important;
    box-shadow: none !important;
    color: white !important;
    font-family: Segoe UI !important;
    font-size: 10px !important;
    padding: 1px 3px 3px !important; }
.inputButton + #addtolistresult a, h2.mt8 + #addtolist .inputButton + small a {height: 12px !important;line-height: 1em !important;}
#addtolist .inputButton:hover,
.inputButton + #addtolistresult a:hover,
h2.mt8 + #addtolist .inputButton + small a:hover {border-bottom: 2px solid white !important;color: white !important;}

/* Reviews ('??? people found this review helpful') */
.borderDark > .spaceit > .mb8 + div[style="float: left;"] tr .lightLink.spaceit {min-width: 342px !important;max-width: 342px !important;} /* Mod */
.pb24 .borderDark > .spaceit > .mb8 + div[style="float: left;"] tr .lightLink.spaceit {min-width: 362px !important;max-width: 362px !important;} /* Mod */

/* Reviews (Preliminary tag and text) */
.borderDark > .spaceit > .mb8 + div[style="float: left;"] tr .lightLink.spaceit + div {left: 490px !important;} /* Mod */
.pb24 .borderDark > .spaceit > .mb8 + div[style="float: left;"] tr .lightLink.spaceit + div {left: 510px !important;} /* Mod */

/* Recommendations (New) */
.anime-slide-block .btn-anime {width: 98px !important;}
.anime-slide-block .btn-anime a {max-width: 90px !important;max-height: 140px !important;}
.anime-slide-outer {width: 876px !important;}
.anime-slide-block .btn-anime-slide-side.right {right: 1px !important;}
.anime-slide-block .link.bg-center img {max-width: 90px;max-height: 140px;} /* Undo height fix */

/* Recommendations (Titles, 'Users') */
.anime-slide-block .title {max-width: 81px !important;}
.anime-slide-block .users {width: 81px !important;}

/* Replace '?' Images (Coloured circle) */
div.picSurround img[src*="mark_23.gif"] {background: linear-gradient(to bottom right, #f46592 35%, #8c7dd0) !important;}

/* Videos tab */
.breadcrumb + .video-block.episode-video {margin-top: 4px !important;} /* Mod */

/* Video width */
.contents-video-embed {width: 877px !important;}
.video-embed iframe[width="800"][height="535"],
.video-embed embed[width="800"][height="535"] {width: 877px !important;height: 580px !important;}
.contents-video-embed .video-anime-slide-block.hori {width: 821px !important;}
.contents-video-embed .video-anime-slide-outer {width: 819px !important;}

/* 'Stats' Page (Recently Updated By ___) */
.table-recently-updated tr .borderClass:first-child {width: 338px !important;}

/* Padding on subpages ('Related Clubs' header, etc) fix */
#horiznav_nav + .breadcrumb + h2, #horiznav_nav + .breadcrumb + div {margin-top: 4px !important;} /* Mod */

/* Characters/Staff fix */
.detail-characters-list .left-column.fl-l, .detail-characters-list .left-right.fl-r {width: 430px !important;} /* Mod */

/* Theme Song fix */
.di-tc.va-t[style="width:392px;"] {width: 431px !important;} /* Mod */

/* Footer */
#footer-block .footer-link.di-ib {left: 154px;padding: 5px 71px 8px !important; /* Mod */}
}

@-moz-document url-prefix("https://myanimelist.net/anime.php?id="), url-prefix("https://myanimelist.net/manga.php?id="), regexp("https://myanimelist.net/anime/(?!season|genre|producer).*"), regexp("https://myanimelist.net/manga/(?!genre|magazine).*") {
/* Shorten tab names */
tr:first-child > td[valign="top"][width="225"]:first-child + td #horiznav_nav {margin: 5px 0 10px 0 !important;padding: 9px 10px 11px 10px !important;}

/* Characters (& Staff) */
#horiznav_nav ul li a[href$="/characters"] {font-size: 0 !important;line-height: 22px !important;}
#horiznav_nav ul li a[href$="/characters"]:before {content: "Characters,";font-size: 13px !important;}
#horiznav_nav ul li a[href$="/characters"].horiznav_active:before {font-size: 16px !important;}

/* More (Info) */
#horiznav_nav ul li a[href$="/moreinfo"] {font-size: 0 !important;line-height: 22px !important;}
#horiznav_nav ul li a[href$="/moreinfo"]:before {content: "More";font-size: 13px !important;}
#horiznav_nav ul li a[href$="/moreinfo"].horiznav_active:before {font-size: 16px !important;}
}

@-moz-document regexp("https://myanimelist.net/.*/.*/.*/pics") {
/* Fixes for code changes to Anime/Manga pics page */

.for-pc + #wrapper {width: 1234px !important;}
}
