css:
@-moz-document url-prefix("http://imas-cg.net") {
  
  #utilities,
.google-2ad-m,
.titleRss,
.titleRssBottom,
.articleSocial,
.related_articles_more h3,
.related_articles_more + h3,
.snsArticle,
#title,
.popularArticlesWithImage
{
	display: none !important;
}

.titleSub {
 float: left; 
}
.discription {
 font-size: 16px; 
  width: 400px;
  float: left;
  margin: 2em auto;
}

#header {
  width: auto !important;
}

.title {
  display: block;
}

#content
{
  width: 100% !important;
}

body.multi2 div#main {
    box-sizing: border-box;
	width: 100% !important;
    padding: 0 !important;
    margin: 0 auto !important;
}

.entry {
    box-sizing: border-box;
	padding: 0 5em !important;
    max-width: 1000px;
    margin: 0 auto !important;
}

.resMain {
  margin-top: 1em;
}

.resMain br:first-of-type,
.resMain > br:last-of-type {
	display:none;
}

#cm .wrapper {
  width: 100%;
}

#cm .meta {
  width: auto !important;
}

#cm .meta .num {
	margin: .5em 0;
}

#cm .name {
  display: none;
}

#cm .cmArea {
  float: none;
  margin-left: 2em;
  width: 100%;
  padding:0 1em;
  max-width: 600px;
}

}
