css:
@-moz-document domain("desktop.any.do") {
.AppSidebar__newItem__input {
    background-color: #222222;
}

:root {
    --text-color: #ffffff;
    --bg-color: #333333;
    --card-bg-color: #222222;
    --primary-color: #0da5f3;
    --secondary-color: #898989;
    --third-color: #a1a1a1;
    --warning-color: #ffb926;
    --info-color: #38dca7;
    --box-shadow-color: rgba(0, 0, 0, .1);
    --box-shadow-color-strong: rgba(0, 0, 0, .3);
    --secondary-separator-color: #e7e7e7;
    --backdrop-color: rgba(0, 0, 0, .5);
    --error-text-color: #ff001f;
    --confirm-color: #7ed321;
    --action-secondary-color: #9197a3;
    --action-third-color: #000;
    --task-item-checkbox-background: #9b9b9b;
    --task-item-checkbox-color: var(--action-secondary-color);
    --action-color: #0080ff;
    --action-button-disabled-color: var(--action-secondary-color);
    --primary-color-hover: #0874aa;
    --card-bg-color--hover: #f2f2f2;
    --error-text-color-hover: #cc0018;
    --action-color-hover: #0060ff;
    --primary-color-50p: rgba(13, 165, 243, .5);
    --secondary-color-25p: hsla(0, 0%, 54%, .25);
    --text-color--25p: rgba(74, 74, 74, .25);
    --card-bg-color--75p: hsla(0, 0%, 100%, .75);
    --nav-link-bg-color: var(--card-bg-color);
    --nav-link-bg-color--active: var(--bg-color);
    --nav-link-color: var(--secondary-color);
    --nav-link-heading-color: var(--text-color);
    --nav-link--focus-color: var(--primary-color);
    --nav-link--active-color: var(--primary-color);
    --nav-link-button--active: #9b9b9b;
    --textfield-bg-color: var(--card-bg-color);
    --textfield-bg-color--minimal: transparent;
    --textfield-bg-color--minimal--focus: var(--bg-color);
    --textfield-border-color: var(--secondary-color);
    --textfield-border-color--focus: var(--primary-color);
    --textfield-text-color: var(--text-color);
    --textfield-text-color--focus: var(--text-color);
    --textfield-placeholder-color: var(--third-color);
    --file-attachment-icon-color: var(--action-secondary-color);
    --file-attachment-progress-bar-color: #ebebeb;
    --google-login-bg-color: #4385f5;
    --google-login-bg-color--hover: #1365f1;
    --facebook-login-bg-color: #465e97;
    --facebook-login-bg-color--hover: #2d4c96;
    --theme-color-blue: #0da5f3;
    --theme-color-black: #000;
    --theme-color-pink: #f26baa;
    --theme-color-green: #0bd99c;
    --theme-color-teal: #14ccc9;
    --default-label-color: var(--action-third-color);
    --disabled-opacity: 0.65;
    --disabled-strong-opacity: 0.3;
    --viewport-min-width: 1280px;
    --text-font-size: 16px;
    --heading-line-height: 1.1;
    --line-height: 1.4;
    --border-radius: 5px;
    --modal-border-radius: 10px;
    --modal-dialog-width: 400px;
    --bar-height: 60px;
    --radio-button-size: 24px;
    --radio-button-marker-size: 16px;
    --login-form-height: 44px;
    --login-form-bottom-margin: var(--gutter);
    --login-terms-field-width: 300px;
    --separator-height: 1px;
    --box-shadow-focus-ring-size: 0 0 0 6px var(--secondary-color-25p);
    --box-shadow-focus-ring-inside: inset 0 0 0 3px var(--secondary-color-25p);
    --font-weight-light: 300;
    --font-weight-regular: 400;
    --font-weight-medium: 500;
    --font-weight-bold: 600;
    --default-font-family: "Helvetica Neue", Helvetica, Arial, "Roboto", sans-serif;
    --gutter: var(--text-font-size);
    --pane-h-gutter: calc(var(--gutter) * 2.5);
    --app-sidebar-z-index: 3;
    --app-header-z-index: 4;
    --Tooltip-z-index: 1000;
    --BackdropModal-z-index: 1000;
    --BackdropTooltip-z-index: 1000;
    --drag-layer-item-z-index: 9999;
    --app-header-height: 64px;
    --app-sidebar-width: 260px;
    --app-header-bottom-margin: calc(var(--gutter) * 2.5);
    --tasks-toolbar-category-title-line-height: 1.3;
    --tasks-toolbar-height: var(--tasks-toolbar-category-title-height);
    --tabs-bg-color: var(--card-bg-color);
    --tabs-bg-color--focus: #f7f7f7;
    --tabs-label-color: var(--third-color);
    --tabs-label-color--active: var(--primary-color);
    --default-card-inner-padding-h: calc(var(--gutter) * 1.5);
    --default-card-inner-padding-v: calc(var(--gutter) / 2);
    --scrollbar-width: 8px;
    --scrollbar-border-radius: calc(var(--scrollbar-width) / 2);
    --scrollbar-pane-bg-color: transparent;
    --scrollbar-thumb-bg-color: var(--third-color);
    --pane-box-shadow: 0 2px 10px 0 var(--box-shadow-color);
    --pane-box-shadow-strong: 0 2px 10px 0 var(--box-shadow-color-strong);
    --pane-box-shadow-large: 0 2px 15px 0 var(--box-shadow-color);
    --pane-box-shadow-inversed: 0 -2px 10px 0 var(--box-shadow-color);
    --pane-min-width: 335px;
    --pane-max-width: 450px;
    --task-indicator-color: #979797;
    --task-item-box-shadow: 0 2px 5px 0 var(--box-shadow-color);
    --sidebar-category-width: var(--app-sidebar-width);
    --sidebar-category-height: 44px;
    --sidebar-category-padding-end: 30px;
    --sidebar-category-text-padding-start: 15px;
    --sidebar-category-padding: 0 18px 0 var(--sidebar-category-padding-end);
    --sidebar-category-in-out-transition-speed: var(--animation-speed-medium);
    --file-attachment-spacing: calc(var(--gutter) / 2);
    --animation-delay-speed: 100ms;
    --animation-delay-speed-fast: 50ms;
    --animation-delay-slow: 600ms;
    --animation-speed-fast: 200ms;
    --animation-speed-medium: 400ms;
    --animation-speed-slow: 600ms;
    --animation-speed-very-slow: 1000ms;
    --animation-speed-focus-ring: 400ms;
    --animation-focus-transition: 400ms;
    --animation-pane-enter-speed: 300ms;
    --ButtonRounded-height: 34px;
    --ButtonRounded-height--reminders: 30px;
    --ButtonRounded-borderRadius: calc(var(--ButtonRounded-height) / 2);
}
}
