css:
@-moz-document url-prefix("https://app.grammarly.com/")
{
  /* Colour Settings */
:root {
    \--defaultBG: rgba(7, 12, 15, .95);
    \--editor: #f77f00;
    \--title: #00a3dd;
    
    \--scrollbar: #00a3dd ;
    \--scrollbarHover:#00a3dd ;
    \--scrollbarScrolling: #b9f2ff;
    \--scrollbarBG: rgba(0, 163, 221, .4);
    \--scrollbarHoverBG: rgba(0, 163, 221, .4);
    \--scrollbarScrollingBG: rgba(185, 242, 255, .4);
    
    \--assistantText: #b9f2ff;
    \--assistantButton:#00a3dd ;
    \--assistantButtonText: #b9f2ff;
    \--assistantButtonShadow: rgba(185, 242, 255, .15);
}

/** Body */
body { background-color: var(--defaultBG); }

/** Editor Title **/
._f0b953d7-typography-header1 { color: var(--title); }
/** The editor **/
._1cd6b501-typography-paper { color: var(--editor); }

/** Title Bar **/
._b17ed844-editor-toolbarTitleContainer { background: linear-gradient(-180deg,rgba(7, 12, 15, .9) 78%,hsla(0,0%,0%,0)) }
._fd0d2403-editor-toolbarTitle { color: var(--title); }

/** Scroll Bar **/
.ps > .ps__scrollbar-y-rail > .ps__scrollbar-y { background-color: var(--scrollbar); }
.ps.ps-hovered > .ps__scrollbar-y-rail:hover > .ps__scrollbar-y { background-color: var(--scrollbarHover); }
.ps.ps-hovered.ps--in-scrolling.ps--y > .ps__scrollbar-y-rail > .ps__scrollbar-y { background-color: var(--scrollbarScrolling); }
.ps.ps-hovered > .ps__scrollbar-y-rail:hover { background-color: var(--scrollbarHoverBG); }
.ps.ps-hovered.ps--in-scrolling.ps--y > .ps__scrollbar-y-rail { background-color: var(--scrollbarScrollingBG); }

/** Assistant **/
._ecf3ebb7-scroller-scrollContent { background-color: transparent; }
._248ddbc0-base-fullCard { box-shadow: 0 .625rem 3.5rem 0 rgba(0,0,0,0.8); background-color: rgba(0,0,0,0.1); }
/** Assistant text **/
._8d8a7136-typography-p2 { color: var(--assistantText); }
._5f933fee-base-wrapper { color: var(--editor); }
._5f933fee-base-wrapper:hover { background-color: rgba(0,0,0,0.2); box-shadow: 0 0 15px 0px var(--scrollbarHoverBG); }
._9e5ea358-default-critical, ._73910c54-default-advanced { color: var(--title); }
._82d247f5-replacements_labels-itemOriginalContent { color: var(--editor); }
/** Assistant Button **/
._29fd09e5-default-alignButtonHidden { color: var(--assistantButtonText); background-color: var(--assistantButton); box-shadow: 0 0 20px 0 var(--assistantButtonShadow); }

/** Synonyms Popup **/
._34ba9b44-synonyms-wrapper { background-color: var(--defaultBG); }



/** What is this? ** /
._3c071f12-sidebar-nextLensBtn { color: yellow; }
._f0433afe-sidebar-nextSwitch, ._01e9e57b-sidebar-footer { background-color: yellow; }


