css:
@-moz-document domain("sjgossip.freeforums.net") {

/* latin */
@font-face {
  font-family: 'Monoton';
  font-style: normal;
  font-weight: 400;
  src: local('Monoton'), local('Monoton-Regular'), url(https://fonts.gstatic.com/s/monoton/v7/5h1aiZUrOngCibe4TkHLQg.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}

/*globals*/ 
*, a, #navigation-menu>ul li a {
	font-family: SF Pro text!important;
    transition: .2s ease-in-out;
}
body {
	font-size: 12pt;
	max-width: 900px;
	margin-left: auto;
	margin-right: auto;
	border: 3px soli;
	box-shadow: none!important;
    background: #ededed;
}
::selection {
    background: pink;
}

/*remove box shadow*/
.effect8:before, .effect8:after, .mini-profile {
	box-shadow: none!important; 
}

/*hide ads*/
iframe#ad1, div#stinkysfoot, .effect, .control-bar>div.float-left, #top-ad, .effect1 {
	display: none!important;
}

/*header*/ 
h2#banner, header {
	background-image: none;
	height: 59px;
    background: #ededed;
    margin-top: 20px;
    margin-bottom: 20px;
}
a#logo {
	text-transform: lowercase;
	text-shadow: none;
    color: black;
    font-family: monoton,space mono, abril fatface, comfortaa!important;
    font-size: 48px;
}
/*nav menu*/
#navigation-menu, #navigation-menu * {
    background-image: none;
    box-shadow: none;
    font-size: 13px!important;
    
}
div#navigation-menu {
	width: 90%;
	margin-left: auto;
	margin-right: auto;
	border-bottom-right-radius: 10px!important;
	border-bottom-left-radius: 10px!important;
	border: 2px 0px 10px 30px solid #bbb!important;
	border: 2px solid #bbb!important;
	border-top: 0px!important;
}
#uo_dd {
    background: none;
    width: 250px;
    border: none;
}
#user_options {
    margin-right: 0;
    width: 250px;
}
#current_user_menu {
    width: 140px;
}
#noti {
    color: red;
}

/*thread table*/
table#wrapper2 {
	max-width: 750p;
}
#shadowbox, .container>.control-bar {
    background: #ededed!important;
    border: none;
}
.container>.content, .ui-dialog .ui-dialog-content, .ui-dialog .ui-dialog-buttonpane, .container.copy .clone {
    border: none;
}
table.list>thead>tr>th, table.list>tbody>tr>td, .control-bar .ui-pagination {
    padding: 0
}
.action_subject {
    display: none;
}
.control-bar .ui-search {
    padding-right: 0
}
a.reply-button {
	background: #3897f0!important;
	color: white!important;
	border-radius: 3px!important;
}

/*breadcrumbs*/
div#navigation-tree {
	border: none;
	background: inherit;
	font-size: initial;
}

div#navigation-tree a {
	font-weight: 400;
	padding-left: 0;
	margin-left: 0;
}
div#navigation-tree a::before {
	content: '> ';
	padding-right: 5px
}

span.menu_arrow {
	display: none;
}

.recent-threads-button {
    background: transparent;
    border: none;
    font-size: 0;
}
span.new-icon {
	border: none;
	/* background: #f4b52a; */
	/* color: white!important; */
	padding: 4px;
	/*font-size: 10px;*/
	background: transparent;
	padding-right: 0;
    color: #9f9f9f;
    text-transform:  capitalize;
    font-size: 13.6px;
}

span.new-icon::before {
	content: 'view '
}

span.new-icon::after {
	content: ' discussions'
}

a.recent-threads-button.new {
	padding-right: 0;
}



/*thread title*/
.container>.title-bar {
	padding-top: 2px;
    padding-left: 0;
    padding-right: 0;
    padding-bottom: 20px;
	background: none;
}

.container>.title-bar h1, .container>.title-bar h2, .container>.title-bar h3 {
	color: black;
	font-size: 36p;
	font-weight: 900;
	letter-spacing: -.02e;
    font-family: SF Pro Display!important;
}
/*pagination*/
.ui-pagination li>a, .ui-pagination li>div {
	background: #bbb;
	border: none;
	color: initial;
	padding: 6px 10px;
	font-size: 13px;
	margin-right: 3px;
}

.ui-pagination li.state-selected>a {
	padding: 6px 10px;
	background: black;
	color: white;
}

.ui-pagination li.state-disabled>a {
	border: none;
	padding: 6px 10px;
}
.container>.control-bar {
    margin-bottom: 30px;
}

/************************************************/
/*POSTS******************************************/
.posts .post>td, .posts .labels, td.unblocked {
	background: #ededed;
    border: none;
    padding-bottom: 0;
}

/*meta info*/
.info, .info *, .quote_header, a.user-link.js-user-link.user-86.group-0 span, abbr.o-timestamp.time  {
	font-size: 12px!important;
}
/*hide the 'to top' text*/
td > table > tbody > tr:nth-child(1) > td.content > article > div.content-head.ui-helper-clearfix > div.info > span.date > a > font {
	font-size: 0!important;
}
/*make the timestamp and linkes go all on one line
.info {
	min-width: 350px!important;
}
td > table > tbody > tr:nth-child(1) > td.content > article > div.content-head.ui-helper-clearfix > div.info > span:nth-child(3) > div:nth-child(2) {
	float: right;
}

span.date {
	float: left;
}*/

.posts .post .content-head {
    border: none;
    opacity: .3;
    margin-bottom: 0;
    margin-top: 10px;
    text-transform: lowercase;
}
.posts .post .content-head:hover {
    opacity: 1;
}
.posts .post .content-head .controls>a, .button.post-options {
	background: transparent;
	border: none;
	padding-bottom: 0px!important;
	/* vertical-align: bottom; */
}
.posts .post .content-head .info .date {
    margin-right: 0px;
}

.options_menu li, .select_menu_list li, .ui-menu-context li {
	background: white;
	opacity: 1!important;
	color: black!important;
}

.options_menu li>a, .select_menu_list li>a, .smiley-menu li>a, .ui-menu-context li>a {
	color: black;
	font-weight: bold !important;
	background: white;
	opacity: 1!important;
}

.ui-helper-clearfix.ui-selectMenu {
	opacity: 1!important;
	background: white;
}

/*mini user profile (name, avatar) and entire left panel*/
.posts .post .left-panel {
	width: 130px!important;
    background: #ededed;
}
.mini-profile {
	border: 1px solid re;
    background: #ededed!important;
    margin-top: 42px;
    padding-bottom: 0;
    min-height: 0;
}
.mini-profile .avatar, .avatar_size_default img, .avatar_size_default default, .avatar_size_default, .avatar_size_default embed, .avatar_size_default object {
	max-width: 55px;
	max-height: 55px;
	border-radius: 80px!important;
	vertical-align: top;
	/*display: block;/*THIS IS THE PLACE TO ADD IMPORTANT IF YOU WANNA SEE PROF PICS*/
	display: none;
    margin-top: -7x;
}
.mini-profile br {
    display: none;
}
div.mini-profile a {
    display: block!important;
    /*font-weight: 800 !important;*/
    font-size: 14.4px !important;
}
/*hide user stats except on hover*/
.mini-profile.awarded img, .mini-profile .info, .mini-profile .personal-text {
	display: none!important;
    text-transform: none!important;
}
td.left-panel:hover .mini-profile .info {
    display: block!important;
    opacity: 1;
    transition: .3s ease;
}
.mini-profile {
	color: #000000d4!important;
    font-size: 11px;
    font-weight: 300;
    text-transform:  lowercase;
}
td.left-panel:hover .mini-profile {
    color: initial!important;
}

/*post wrapper*/
.posts .post .content {
    background: #ededed;
    padding-left: 20px;
}
/*post itself*/
.posts .post .message {
    background: white;
    padding: 10px;
    border-radius: 10px!important;
    padding-left: 15px !important;
    min-height: 0!important;
}

/*embedded quote*/
.quote {
	margin-left: 0;
}
#content .content .quote div.quote_body {
    background: #faf6ce;
    border-color:#faf6ce;
    border-radius: 10px!important;
}



/*quick reply box at bottom of thread page*/
.container>.content {
    background: inherit;
    padding-right: 0;
}
div.message.ui-resizable div.ui-resizable-s {
    display: none;
}
/*hide "quick reply" overly obtrusive heading*/
#shadowbox > div.container.quick-reply > div.title-bar > h2 {
    color: transparent;
}
.quick-reply .message {
    margin-bottom: -5px;
}
.content.cap-bottom form {
	max-width: 670px;
	margin-left: auto;
}
.quick-reply textarea {
    border-radius: 10px 10px 0px 10px!important;
    padding: 10px;
    border: 1px solid white;
    background: white;
    color: #3897f0;
    font-weight: 600;
}
textarea {
    color: initial;
}
.quick-reply textarea:focus {
    background: white;
    border: 1px solid #3897f0;
    color: #444;
}
/*quick reply button*/
.quick-reply input[type="submit"] {
	float: right;
	background: white;
	color:  #3897f0;
	font-weight: 600;
	border: 1px solid white;
	border-radius: 0px 0px 5px 5px!important;
}
.quick-reply input[type="submit"]:hover {
    border: 1px solid #3897f0;
}
.quick-reply input[type="submit"]:active {
    border: 1px solid #3897f0;
    background: #3897f0;
    color: white;
}
}
