css:
@-moz-document regexp("(http|https)://www\\.furvilla\\.com/(?!(villager/[0-9]*(?:\\?|$)|profile/[0-9]*(?:\\?|$))).*") {
/*====================================================*/
/*                                                    */
/*                  Furvilla  Reworked                */
/*                      Nightmode                     */
/*                                                    */
/*                       v.1.3.7                      */
/*                                                    */
/*====================================================*/

/* [[*** READ ME ***]]

regarding the applies to/ REGEXP for this section:

1. to change every page EXCEPT villager + user profiles, change the 'applies to' value below to:
     (http|https)://www\.furvilla\.com/(?!(villager/[0-9]*(?:\?|$)|profile/[0-9]*(?:\?|$))).*

2. to change every page on the site including villager + user profiles, change the 'applies to' value to this
     (http|https)://www\.furvilla\.com/.*   
  ...& don't forget to disable custom css in your user settings.

*/

body {
    background: linear-gradient(#210b3c, #761870);
    background: -webkit-linear-gradient(#210b3c, #761870);
    background: -webkit-gradient(#210b3c, #761870);
    color:#fff;
}

.pricebox {right:795px;border-color:#000;background:rgba(0,0,0,0.5);}

::-moz-selection { background: #ffb2f2;color:#000; }
::selection { background: #ffb2f2;color:#000; }

/*---- GENERAL ----*/

/*general links*/
a, header .user-panel a, footer a  {
    color: #8adce8;/*#c49fff*/
    -webkit-transition: 0.3s ease-in-out;
    -moz-transition: 0.3s ease-in-out;
    -o-transition: 0.3s ease-in-out;
    transition: 0.3s ease-in-out;
}

/*general links, hover*/
a:hover, a:active, a:focus, header .user-panel a:hover, header .user-panel a:active, footer a:hover {
    color:#ffb2f2;/*#ffb2f2*/
    text-decoration:none;
}

header .user-panel a, .right-column .user-info h4 a, .right-column a[href*="online"], .breadcrumbs a {color:#ffb2f2;}
.right-column .user-info .align-center:last-child {margin-top:-5px;}
header .user-panel a:hover, header .user-panel a:active, header .user-panel a:focus, .right-column .user-info h4 a:hover, .right-column a[href*="online"]:hover, .breadcrumbs a:hover, .breadcrumbs a:focus {color:#8adce8;}

hr, .furcoins tr:nth-of-type(2) {
    color:#211928;
    border-color:#211928;
}
table.furcoins tr:nth-child(2) td.bestseller, table.furcoins tr:nth-child(1) td.bestseller::before {
    color:#000 !important;
}

::-webkit-scrollbar {
    width: 15px;
}
 
::-webkit-scrollbar-track {
    background: #211928;
}
 
::-webkit-scrollbar-thumb {
  background-color: #D7C9E3;
  outline: 1px solid #000;
}

/*firefox scrollbar style*/
html, body {scrollbar-color: #D7C9E3 #211928;}

/*tooltips*/
.tooltipster-base {
	background: url('https://i.postimg.cc/nrRtqYrs/stars.png') #000;
    box-shadow:0px 3px 5px rgba(231, 183, 202, .1), 0px -3px 5px rgba(231, 183, 202, .1), 3px 0px 5px rgba(231, 183, 202, .1), -3px 0px 5px rgba(231, 183, 202, .1);
	color: #f1f1f1;
    border-color:#fff;
}
.tooltipster-base b {color:#deb7f7;}

/*filter to make most imgs, etc easier on eyes*/
.content img, header .search-form, .textarea {
    filter: brightness(60%) contrast(0.85) !important; 
    backface-visibility:hidden;
    -webkit-transition: 0.3s ease-in-out;
    -moz-transition: 0.3s ease-in-out;
    -o-transition: 0.3s ease-in-out;
    transition: 0.3s ease-in-out;
}
/*img hover filter returns images to normal brightness/contrast*/
img:hover, header .search-form:hover, header .search-form:focus-within, .fur-residents li .fur-residents-item-info:hover img, .animal-item-info:hover img, .crafting-list-item-info:hover img, .inventory-item-info:hover img, .item-block:hover img, .modal-content img, .construction-item-info:hover img, .messages img, .right-column .widget:first-of-type img, fieldset[title*="Create villager"] img, .villagers-list-edit li:hover img, img[src*="http://www.furvilla.com/img/furdollars.gif"], img[src*="http://www.furvilla.com/img/furcash.gif"], img[src*="/img/furdollars.gif"], img[src*="/img/furcash.gif"], .forum tr:hover img, .text-center.margin-1em img, .text-center.margin-2em img, .col-sm-3.text-center:hover img, .jobs li:hover img, .table tr:hover img, .panel:hover img, .featured-villager:hover img, img[src*="http://www.furvilla.com/img/furidol.png"], .furcoins img, .textarea:hover, .textarea:focus, .mayor-avatar img, .fur-idol-prize-list div[style*="border: 1px solid #ebebeb; border-radius: 6px; padding: 20px 10px; width: 100%;"]:hover img, .paintie-gallery .paintie:hover img, img[src*="http://www.furvilla.com/img/npcs/oracle.png"], img[src*="http://www.furvilla.com/img/events/dragonsmaw/npcs/witch2.png"], img[src*="http://www.furvilla.com/img/events/dragonsmaw/npcs/cookery2.png"], img[src*="http://www.furvilla.com/img/events/dragonsmaw/npcs/ringmaster2.png"], img[src*="http://www.furvilla.com/img/events/dragonsmaw/npcs/applebob2.png"], .inventory .crafting-list-item-info:hover img, .fur-idol-prize:hover img, .villager-data-wrapper .villager-data-avatar img, .user-desc:hover img, .user-desc:focus-within img, .villager-description:hover img, .villager-description:focus-within img, .villager-career:hover img, .villager-career:focus-within img, #map-img {
        filter: brightness(100%)  contrast(1) !important;
}

.villager-avatar img {border-radius:100%;}

/*position home link to top left corner*/
#logo {
    height:1px !important;
    width:1px !important;
    position:fixed;
    top:18px;
    right:305px;
    left:unset;
}
#logo a {
    position:absolute;
    top:-7px;
    background:transparent !important;
    height:20px;
    width:22px;
}
#logo::before {
    content: '\f015';
    position: absolute;
    top:-7px;
    z-index:-1;
    color:#D3D9EB !important; /*#E4E9FA*/
    font-family: 'FontAwesome';
    line-height:20px;
    font-size:24px;
    height:19px;
    -webkit-transition: 0.3s ease-in-out;
    -moz-transition: 0.3s ease-in-out;
    -o-transition: 0.3s ease-in-out;
    transition: 0.3s ease-in-out;
}
#logo:hover::before {color: #ffb2f2 !important;}

/*changes green for the paw representing equipped animals on the villager page*/
.green, i[style*="color:#88cc00;"], span[style*="color:#21a845"] {color:#43ecb9 !important;}

/*changing the shade of red that's on warning links, etc*/
.red, i[style*="color:#b30000;"], i[style*="color:#ba1c28"], .report a, font[color*="red"] {color:#D22E5F !important;opacity:1;}

.blue, span[style*="color: rgba(21, 113, 152, 1)"], span[style*="color: rgba(14, 108, 176, 1)"] {color:#8BE2FF !important;}

/*change admin colors from black to a readable color*/
a[style*="color:#000000;"] {color:#FFEE74 !important;}

/*change purple mod color to be lighter*/
a[style*="color:#7E33A1;"] {color:#d26fff !important;}

/*change red mod color to be lighter*/
a[style*="color:#cc0000;"] {color:#FF7B7B !important;}

/*change geen verified check to lighter green*/
a[style*="color:#00AD5A"] {color:#3CF59C !important;}

/*change owner color to be lighter*/
a[style*="color:#0086a6"] {color:#00ceff !important;}

/*fix report button color*/
a[style*="color: #a94442"] {color:#D22E5F !important;}

/*fun w/ labels*/
label, .fur-idol-winner-votes small {color:#deb7f7;}
.text-warning {color:#FFEE74;}
.text-success {color:#43ecb9;}

.label-danger {
    border:1px solid rgba(0,0,0,0.5);
    background-color:#EC9D9B;
    color:#000;
    text-transform:uppercase;
    text-shadow: 0 0 2px rgba(0, 0, 0, .1), 0 0 2px rgba(0, 0, 0, .1), 0 0 2px rgba(0, 0, 0, .1);
}
.label-warning {
    border:1px solid rgba(0,0,0,0.5);
    background-color:#FCD479;
    color:#000;
    text-transform:uppercase;
    text-shadow: 0 0 2px rgba(0, 0, 0, .1), 0 0 2px rgba(0, 0, 0, .1), 0 0 2px rgba(0, 0, 0, .1);
}
.label-success {
    border:1px solid rgba(0,0,0,0.5);
    background-color:#98EBC4;
    color:#000;
    text-transform:uppercase;
    text-shadow: 0 0 2px rgba(0, 0, 0, .1), 0 0 2px rgba(0, 0, 0, .1), 0 0 2px rgba(0, 0, 0, .1);
}

.label-default {
    border:1px solid rgba(0,0,0,0.5);
    background-color:#a2a2a2;
    color:#000;
    text-transform:uppercase;
    text-shadow: 0 0 2px rgba(0, 0, 0, .1), 0 0 2px rgba(0, 0, 0, .1), 0 0 2px rgba(0, 0, 0, .1);
}

.label-primary {
    border:1px solid rgba(0,0,0,0.5);
    background: linear-gradient(#210b3c, #761870) !important;
    background: -webkit-linear-gradient(#210b3c, #761870) !important;
    background: -webkit-gradient(#210b3c, #761870) !important;
    color:#f1f1f1 !important;
    text-transform:capitalize;
    text-shadow: 0 0 2px rgba(0, 0, 0, .1), 0 0 2px rgba(0, 0, 0, .1), 0 0 2px rgba(0, 0, 0, .1);
}

.label-primary:hover, .label-primary:focus, .label-primary:active {
    border:1px solid rgba(0,0,0,0.5) !important;
    background: linear-gradient(#c64386, #c392e2) !important;
    background: -webkit-linear-gradient(#c64386, #c392e2) !important;
    background: -webkit-gradient(#c64386, #c392e2) !important;
    color:#f1f1f1 !important;
    text-transform:capitalize !important;
    text-shadow: 0 0 2px rgba(0, 0, 0, .1), 0 0 2px rgba(0, 0, 0, .1), 0 0 2px rgba(0, 0, 0, .1) !important;
}

/*pinky purple headers*/
.content h1, .content h2, .user-info h4, .fur-residents-item-info .status, .animal-item-info .status, .status, .help-block, .furcoins tr:nth-of-type(2) td {
    color:#deb7f7 !important;
}

.content h1 small, .furcoins, .mayor-announcements .footer {
    color:#f1f1f1;
}

.content h1 small {
    margin-bottom:5px;
}

.registration-well label, .status b { color:#ffaedc !important;}
.content h3 { color:#c2a7f2 !important;} /*a7dff2*/

/*tables in stall search, notifications, etc*/
.table tr {border-bottom: 1px solid #211928;}

.table > thead > tr > th {border-bottom: 0px !important;}

.content .widget-content .table tr:nth-child(odd), .registration-well .table tr:nth-child(odd) {background:rgba(0,0,0,0.2);}

.content .widget-content .table tr:nth-child(even), .registration-well .table tr:nth-child(even) {background:rgba(0,0,0,0.4);}

/*alert error*/
.alert, .alert.alert, .alert.error, .alert-danger {
    color:#000 !important;
    text-shadow: 0 0 2px rgba(0, 0, 0, .2), 0 0 2px rgba(0, 0, 0, .2), 0 0 2px rgba(0, 0, 0, .2);
    background: linear-gradient(to right, #FF799B, #EBA6C8);
    background: -webkit-linear-gradient(to right, #FF799B, #EBA6C8);
    background: -webkit-gradient(to right, #FF799B, #EBA6C8);
}

/*alert success*/
.alert.success {
    color:#000;
    text-shadow: 0 0 2px rgba(0, 0, 0, .2), 0 0 2px rgba(0, 0, 0, .2), 0 0 2px rgba(0, 0, 0, .2);
    background: linear-gradient(to right, #4ddd7d, #43ecb9);
    background: -webkit-linear-gradient(to right, #4ddd7d, #43ecb9);
    background: -webkit-gradient(to right, #4ddd7d, #43ecb9);
}

/*login alert*/
.alert.info {
    text-shadow: 0 0 2px rgba(0, 0, 0, .2), 0 0 2px rgba(0, 0, 0, .2), 0 0 2px rgba(0, 0, 0, .2);
    background: linear-gradient(#210b3c, #761870);
    background: -webkit-linear-gradient(#210b3c, #761870);
    background: -webkit-gradient(#210b3c, #761870);
}

/*widget background, job descriptions, herbalist instruction divs*/
.content .widget-content, .job-description, .registration-well, .well.text-center.brew-div, div[style*="background-color: #bfe7b7"], div[style*="background-color: #e7f3cd"], div[style*="background-color: #effdff"] {
    background:rgba(75, 49, 88, .5) !important;
    border-color: #000;
    border-radius:5px;
}
div[style*="background-color: #effdff"] {
    border: 0px !important;
}

/*center pagination*/
.pagination {
    display:block;
    margin-left:auto;
    margin-right:auto;
}

/*pagination colors*/
.pagination > li > a, .pagination > li > span, .pagination > .disabled > span, .pagination > .disabled > span:hover, table.furcoins tr:nth-child(2) td {
    background-color:rgba(75, 49, 88, .5);
    border-color:#000;
    color:#c8c8c8;
}
.pagination > li > a:hover, .pagination > li > a:focus, .pagination > li > span:hover, table.furcoins td {
    background-color:rgba(142, 71, 134, .5);
    border-color:#000;
    color:#f1f1f1;
}
table.furcoins td:nth-child(1) {
    border-right-color:#9ac9b6 !important;
}
.pagination > .active > span, .pagination > .active > span:hover {
    background-color:rgba(162, 106, 209, .5);
    color:#a7dff2;
    border-color:#000;
}

/*toolbar box thing that says how much inventory space u have, etc*/
.header-right-toolbar-box {
    background:rgba(50, 35, 70, .5);
    border-color:#000;
    color:#f1f1f1;
}

/*change any text to off white*/
.job-description p, .stats p, .modal p, .widget .forum-posts p {color:#f1f1f1 !important;}

/*change any border color to black*/
.villagers-list-edit li {border-color: #000;}

code {background-color:#2f3136;color:#bfbfc1;}

.panel-default {
    background:rgba(50, 35, 70, .5);
    border-color: #000;
    -webkit-transition: 0.3s ease-in-out;
    -moz-transition: 0.3s ease-in-out;
    -o-transition: 0.3s ease-in-out;
    transition: 0.3s ease-in-out;
}

.panel-default:hover, .panel-default:focus-within {
    background:rgba(75, 49, 88, .5);
    border-color: #000;
}


/*======================== villagers page, affects some inventory stuff too =============================*/

/*styling villagers n stuff*/
.fur-residents li {width:244px;border:1px solid #000;margin:0px 5px 10px 5px}
.fur-residents li .fur-residents-item-info, .inventory-item-info, .inventory .crafting-list-item-info, .item-block, .modal .item-block, .modal .inventory-item-info, .construction-item-info, .fur-idol-prize, .jobs li a, .villager-data-minipet, .villager-data-info-box, .villager-data-info-wide {
    background:rgba(50, 35, 70, .5);
    border-color: #000;
    -webkit-transition: 0.3s ease-in-out;
    -moz-transition: 0.3s ease-in-out;
    -o-transition: 0.3s ease-in-out;
    transition: 0.3s ease-in-out;
    padding:5px;
    box-sizing:border-box;
}
.fur-residents li .fur-residents-item-info:hover, .inventory-item-info:hover, .inventory .crafting-list-item-info:hover, .item-block:hover, .modal .item-block:hover, .modal .inventory-item-info:hover, .construction-item-info:hover, .jobs li a:hover, .villager-data-minipet:hover, .villager-data-info-box:hover, .villager-data-info-wide:hover, .fur-residents li .fur-residents-item-info:focus-within, .inventory-item-info:focus-within, .inventory .crafting-list-item-info:focus-within, .item-block:focus-within, .modal .item-block:focus-within, .modal .inventory-item-info:focus-within, .construction-item-info:focus-within, .jobs li a:focus-within, .villager-data-minipet:focus-within, .villager-data-info-box:focus-within, .villager-data-info-wide:focus-within {
    background:rgba(75, 49, 88, .5);
    border-color: #000;
}
.fur-residents li .fur-residents-item-info {border:0px;}
.fur-residents li .durability {
    width: 100%;
    background:rgba(0,0,0, .5) !important;
    border-color: #000;
    left:0px;bottom:0px;
}
.fur-residents-item-actions {left:0px;right:0px;border-radius:0px;}
.fur-residents-item-info {margin:0px;}

.fur-residents-item-actions, .animal-item-actions {
    background:rgba(29, 18, 53, 0.85);
}
.fur-residents-item-actions a, .animal-item-actions a {
    color: #a7dff2;
}
.fur-residents-item-actions a:hover, .animal-item-actions a:hover {
    background:rgba(217, 169, 255, .6);
    color: #FFCAF3;
}
.crafting-template .crafting-list {
    display:flex;
    justify-content:center;
}

/*add compatibility for villager + user pages when disabling custom css*/
.villager-data-wrapper {background:none;}
.villager-data-minipet, .villager-data-info-box, .villager-data-info-wide {padding:10px;}
.user-data .village-shield.col-xs-3 + .row.col-xs-9 {padding-right:0px;}
.col-xs-5.trophies img.col-xs-6 {padding:5px 0px;width:33%;}
.gallery .panel {
    background:rgba(50, 35, 70, .5);
    border-color: #000;
    -webkit-transition: 0.3s ease-in-out;
    -moz-transition: 0.3s ease-in-out;
    -o-transition: 0.3s ease-in-out;
    transition: 0.3s ease-in-out;
}
.gallery .panel:hover, .gallery panel:focus-within {
    background:rgba(75, 49, 88, .5);
    border-color: #000;
}
.buffbox {color:#000;}
.villager-illness-DMM-box img {filter:invert(100%) !important;}
.villagers-list .villagers-icons .villager-info > a[style*="color:black"] { color: #f1f1f1 !important; }

/*append gavel to workers*/
.fur-residents-item-info::before {
    content: '\f0e3';
    position: absolute;
    font-family: 'FontAwesome';
    font-size:20px;
    display:block;
    top: 3px;
    left: 10px;
    z-index:2;
}
/*remove gavel from non-workers + add a villager option*/
.fur-residents-item-info.mute::before, .fur-residents-item-add .fur-residents-item-info::before {
     content: ' ' !important;
}

/*fixes to make non-workers better*/
.fur-residents-item-info.mute, .fur-residents-item-add .fur-residents-item-info {
    opacity:0.5;
    background:rgba(179, 82, 112, .1) !important;
}
.fur-residents-item-info.mute:hover, .fur-residents-item-add .fur-residents-item-info:hover {
    opacity:1;
}

/*====================================== general item styles for multiple careers =======================================*/

.crafting-list-item-info {
    background:rgba(0,0,0,0.2);
    border-color:#000;
    -webkit-transition: 0.3s ease-in-out;
    -moz-transition: 0.3s ease-in-out;
    -o-transition: 0.3s ease-in-out;
    transition: 0.3s ease-in-out;
}

.crafting-list-item-info:hover {
    background:rgba(0,0,0,0.4);
    border-color:#000;
}

.progress-bar {
    background: linear-gradient(#210b3c, #761870);
    background: -webkit-linear-gradient(#210b3c, #761870);
    background: -webkit-gradient(#210b3c, #761870);
    border-color:#000;
}
.progress-bar .progress-text {
    color:#dfdfdf;
    font-weight:bold;
    text-shadow: 0 0 2px #000, 0 0 2px #000, 0 0 2px #000;
}
.progress-bar .progress {
    background: linear-gradient(to right, #4ddd7d, #43ecb9);
    background: -webkit-linear-gradient(to right, #4ddd7d, #43ecb9);
    background: -webkit-gradient(to right, #4ddd7d, #43ecb9);
}

/*==================================================== herbalist ========================================================*/

.animal-item-info .durability {
    width:182px;left:5px;
    background: rgba(0,0,0,0.6);
    border-color:#000;
}

.animal-item-info {
    background:rgba(50, 35, 70, .5);
    border-color: #000;
    -webkit-transition: 0.3s ease-in-out;
    -moz-transition: 0.3s ease-in-out;
    -o-transition: 0.3s ease-in-out;
    transition: 0.3s ease-in-out;
}
.animal-item-info:hover {
    border-color:#000;
    background:rgba(75, 49, 88, .5);
}

.animal-item-info .name, .construction-item-info .name {
    text-decoration:none !important;
}

/*animal husbandry*/
.stables-list-edit tr, .table > thead > tr > th, .table > thead > tr > td, .table > tbody > tr > th, .table > tbody > tr > td, .table > tfoot > tr > th, .table > tfoot > tr > td {
    border-color:#211928;
}

.table thead, .table tr:hover .table thead, .thread-user-post-middle table td.head {
    background: linear-gradient(#210b3c, #761870) !important;
    background: -webkit-linear-gradient(#210b3c, #761870) !important;
    background: -webkit-gradient(#210b3c, #761870) !important;
}

.thread-user-post-middle table td.head font[color*="#37317e"] {
    color: transparent !important;
}

.thread-user-post-middle table {
    margin: 10px auto 10px auto;
}

/*========= carpenter ==========*/

input[value*="Update Prices"] {
    display:block;margin:auto;
}

/*================================== NAV DROP DOWN MENUS / SELECTION MENUS / BUTTONS ===================================*/
/*navigation drop down menu*/
.dropdown-menu {
    background: url('https://i.postimg.cc/nrRtqYrs/stars.png') rgba(0,0,0,0.9);
    overflow:hidden;
}

/*checkbox*/
.css-label {
    background-image:url('https://i.postimg.cc/qRGrvddx/checkboxd.png');
}

/*text inputs + textareas*/
.col-sm-8 .form-control, .input, .input:disabled, .item-select, input {
    color:rgba(242, 213, 232, 0.9);
    background:rgba(0,0,0,0.5);
    border-color:#000;
    -webkit-transition: 0.3s ease-in-out;
    -moz-transition: 0.3s ease-in-out;
    -o-transition: 0.3s ease-in-out;
    transition: 0.3s ease-in-out;
}

.textarea, .select2-search__field, .input, input[name*="furcash"], input[name*="furdollars"] {
    /*background:rgba(242, 213, 232, 0.9);*/
    background:rgba(50, 35, 70, .5);
    border-color:rgba(113, 83, 153, .8);
    color:#000;
    color:rgb(245, 223, 237);
    font-size:16px;
}

.textarea:hover, .textarea:focus, .select2-search__field:hover, .select2-search__field:focus, .input:hover, .input:focus, input[name*="furcash"]:hover, input[name*="furdollars"]:hover, input[name*="furcash"]:focus, input[name*="furdollars"]:focus {
