@-moz-document domain("wolfkodi.dyndns.org") {
/* i really want this to be global */

input#lightswitch:checked+div .toggle-slider {
    background-color: #f3b121
}

input#lightswitch:checked+div .toggle-slider:before {
    transform: translateX(23px);
    background-color: #f3b121;
    box-shadow: 0 0 3px 2px rgba(157, 116, 34, .9)
}

body.night-mode {
    color: #cacaca;
    background: #0B0D0E
}

body.night-mode .logo {
    background-image: url(https://i.imgur.com/R6c3xoW.png)
}

body.night-mode .cmt-att,body.night-mode .context-menu-icon:before,body.night-mode .msg-att,body.night-mode .ui {
    background-image: url(https://i.imgur.com/D9Br9hu.png)
}

body.night-mode #loginwrapper,body.night-mode #mainwrapper,body.night-mode #registerwrapper,body.night-mode #roomwrapper {
    background: #131719;
    border: 1px solid #f3b121
}

body.night-mode i.erasing,i.typing {
    height: 12px;
    font-style: normal;
    vertical-align: middle;
    display: inline-block
}

body.night-mode i.typing {
    width: 12px;
    background: url(https://i.imgur.com/D9Br9hu.png) -10px -191px no-repeat;
    -webkit-animation: typing .3s steps(2) infinite;
    -moz-animation: typing .3s steps(2) infinite;
    -o-animation: typing .3s steps(2) infinite;
    animation: typing .3s steps(2) infinite
}

body.night-mode #userhowl {
    background-color: #d9ab46;
    color: #000;
    border-color: #000
}

body.night-mode #userhowl.disabled {
    background-color: #f00;
    opacity: .5;
    cursor: default
}

body.night-mode .selector {
    background: #2c2c2c
}

body.night-mode .selector:hover {
    background: #373737
}

body.night-mode .selector .bar-icon {
    background: #f3b121
}

body.night-mode .selector.mod .bar-icon {
    background: #ffadad
}

body.night-mode .acc-menu-arrow-up,body.night-mode .menuhdl-up .acc-menu-arrow-up {
    border-bottom-color: #1b1b1b;
    border-top-color: #1b1b1b
}

body.night-mode .acc-menu {
    background: #1b1b1b
}

body.night-mode .acc-menu-item:hover {
    background: #f3b12185
}

body.night-mode .cmt-opt-menu,body.night-mode .optMenu {
    background: #3e3e3e
}

body.night-mode .cmt-opt-menu-item:hover,body.night-mode .optMenuItem:hover {
    background: #2e3138
}

body.night-mode .announcement {
    background-color: #430000
}

body.night-mode .noti-count, .noti-dot {
    position: absolute;
    background-color: #f3b121;
    background-image: linear-gradient(#f3b121,#996f13);
}


body.night-mode .info {
    color: #4babff
}

body.night-mode .info.box {
    background: #494949;
    border-color: #97aac4
}

body.night-mode .highlight-section {
    background-color: #262626
}

body.night-mode .slideshow-wrapper {
    width: 300px;
    text-align: center;
    margin: 0 auto;
    display: inline-block
}

body.night-mode .slide-tip-a,.slide-tip-b {
    content: '';
    display: block;
    width: 0;
    border-style: solid;
    border-width: 0 20px 15px 0;
    position: absolute
}

body.night-mode .slide-tip-a {
    border-color: transparent #f3b121
}

body.night-mode .slide-tip-b {
    top: 1px;
    left: 3px;
    border-color: transparent #0f0f0f
}

body.night-mode .slideshow {
    width: 300px;
    height: 145px;
    background-color: #0f0f0f;
    border-radius: 5px;
    border: 1px solid #f3b121;
    overflow: hidden;
    box-shadow: 0 2px 5px 0 rgba(0,0,0,.1)
}

body.night-mode .slider {
    width: 15000px
}

body.night-mode .slide {
    width: 300px;
    float: left;
    display: table;
    height: 135px
}

body.night-mode .slide-vc {
    display: table-cell;
    vertical-align: middle;
    color: #e7e7e7;
    font: 700 15px sans-serif;
    padding: 15px
}

body.night-mode .tab {
    background: #f00;
    width: 10px;
    text-align: center;
    padding: 5px 0;
    display: inline-block;
    border-radius: 25%;
    border: 1px solid #f3b121;
    -webkit-transition: all .5s ease;
    -moz-transition: all .5s ease;
    -o-transition: all .5s ease;
    transition: all .5s ease
}

body.night-mode .tab-active {
    background: #ffaf00;
    color: #fff
}

body.night-mode .roomcount {
