    color: #7c7c7c
}

body.night-mode .button:not(.toggle):not(.apr):not(.rej):not(.active),body.night-mode input[type=button],body.night-mode input[type=submit] {
    background: #262626;
    color: #fff;
    border-color: #000
}

body.night-mode .button {
    border-color: #8d8d8d
}

body.night-mode .button:hover,body.night-mode input:hover,body.night-mode select:hover,body.night-mode textarea:hover {
    border-color: #bbb
}

body.night-mode .button:active {
    background-color: #ae7f17!important
}

body.night-mode .button.toggle {
    cursor: pointer;
    color: #000;
    position: relative;
    border-left: solid 1px #b28218;
    background-color: #f3b121;
    border-color: #7b5a11;
    background-image: linear-gradient(bottom,#f3b121 0,#f6d17e 100%);
    background-image: -o-linear-gradient(bottom,#f3b121 0,#f6d17e 100%);
    background-image: -ms-linear-gradient(bottom,#f3b121 0,#f6d17e 100%);
    background-image: -moz-linear-gradient(bottom,#f3b121 0,#f6d17e 100%);
    background-image: -webkit-linear-gradient(bottom,#f3b121 0,#f6d17e 100%);
    -moz-box-shadow: inset 0 1px 0 #b4851c,0 3px 0 0 #7b5b13,0 6px 3px #000;
    -o-box-shadow: inset 0 1px 0 #b4851c,0 3px 0 0 #7b5b13,0 6px 3px #000;
    box-shadow: inset 0 1px 0 #b4851c,0 3px 0 0 #7b5b13,0 6px 3px #000
}

body.night-mode .button.toggle.active,.button.toggle:active {
    color: #000;
    font-weight: 100;
    border-left: solid 1px #b28218;
    background-color: #f3b121;
    background-image: linear-gradient(top,#f3b121 0,#f6d17e 100%);
    background-image: -o-linear-gradient(top,#f3b121 0,#f6d17e 100%);
    background-image: -ms-linear-gradient(top,#f3b121 0,#f6d17e 100%);
    background-image: -moz-linear-gradient(top,#f3b121 0,#f6d17e 100%);
    background-image: -webkit-linear-gradient(top,#f3b121 0,#f6d17e 100%);
    -moz-box-shadow: inset 0 1px 0 #b4851c,0 2px 0 0 #7b5b13,0 4px 2px #000;
    -o-box-shadow: inset 0 1px 0 #b4851c,0 2px 0 0 #7b5b13,0 4px 2px #000;
    box-shadow: inset 0 1px 0 #b4851c,0 2px 0 0 #7b5b13,0 4px 2px #000
}

body.night-mode .content-title,.content-title * {
    color: #ecc268;
    font: 700 25px sans-serif
}

body.night-mode .cmt-btn {
    -webkit-border-radius: 2px;
