css:
@-moz-document regexp(".*anilist.co.*")
{
.site-theme-dark {
	--color-background: 0, 0, 0;
	--color-text: 230, 230, 230;
	--color-text-light: 179, 190, 203;
	--color-text-lighter: 183, 200, 215;
	--color-foreground: 31, 35, 45;
	--color-footer: 17, 22, 29;
}
.el-dropdown-menu {
	background-color: rgba(var(--color-foreground));
}
.el-dropdown-menu, .el-dropdown-menu__item {
	color: rgba(var(--color-text));
}
.el-dropdown-menu__item--divided::before {
	background-color: rgba(var(--color-foreground));
}
.el-select-dropdown {
	background-color: rgba(var(--color-background));
}
.el-select-dropdown__list {
    background: rgba(var(--color-background));
}
.el-select-dropdown__item.hover, .el-select-dropdown__item:hover {
	background-color: rgba(var(--color-foreground));
	color: rgba(var(--color-text));
}
.el-input, .el-textarea {
	background: rgba(var(--color-foreground));
	border-radius: 4px;
	color: rgba(var(--color-text));
}
.quick-search .el-select .el-input .el-input__inner, .quick-search .el-select .el-input.is-focus .el-input__inner {
	background: rgba(var(--color-foreground));
}
.forum-thread .wrap .editor,
.forum-thread .preview {
    padding-top: 10px;
    border: black 3px solid;
}
.footer[data-v-5a740dfa] {
	background-color: rgba(var(--color-background));
	text-align: left;
}
.name[data-v-619ce2e5] {
	font-weight: bold;
	text-shadow: 2px 2px 2px rgba(0,0,0,1);
}
.history-day[data-v-3b963d1b] {
	background: rgba(var(--color-background));
}
}
