css:
@-moz-document domain("lua-users.org") {
body {
    font-size: 1em;
    line-height: 1.5em;
    font-family:"Segoe UI", Verdana, Arial;
    color: #bbb !important;
    width: 800px;
    margin: 2em auto;
    background-color: #282828;
}
h1, h3 {
    color: #ddd;
}
h1 {
    font-size: 2em;
}
h3 {
    font-size: 1.5em;
    border-top: 1px solid #777;
    margin-top: 2em;
    padding-top:0.5em;
}
h1 > a:link, h1 > a:visited {
    color: #b97f29;
}
a:link {
    color: #00a5dc;
    text-decoration: none;
}
a:visited {
    color: #9d51bd;
}
a:hover, a:active {
    text-decoration: underline;
}
td:nth-child(2) table {
    display: none;
}
td:nth-child(2) > form {
    color: #fff;
    position: absolute;
    width: 12px;
    height: 12px;
    border: solid 1px currentColor;
    border-radius: 100%;
    -webkit-transform: rotate(-45deg) scale(1.5) translateY(-20px);
    transform: rotate(-45deg) scale(1.5) translateY(-20px);
}
td:nth-child(2) > form:before {
    content:'';
    position: absolute;
    top: 12px;
    left: 5px;
    height: 6px;
    width: 1px;
    background-color: currentColor;
}
td:nth-child(2) > form > input[name="string"] {
    -webkit-transform: rotate(45deg) translate3d(-160px, 55px, 0);
    transform: rotate(45deg) translate3d(-160px, 55px, 0);
    border: none;
    height: 1.5em;
}
td:nth-child(2) > form > input[name=".submit"] {
    position: absolute;
    top: -5px;
    left: -7px;
    width: 25px;
    height: 25px;
    color: #282828;
    background-color: #282828;
    border: none;
    z-index: 0;
    opacity: 0;
    border: 1px solid #333;
    box-sizing: border-box;
    transform: rotate(45deg);
}
td:nth-child(2) > form > input[name=".submit"]:hover {
    cursor: pointer;
    border: 1px solid #fff;
    opacity: 0.2;
    border-radius: 5px;
}
body pre {
    border-left: 2px solid dimgray;
    padding-left: 1em;
    color: #aaa;
}
body pre.code {
    color: #ccc;
    border-left: 1px dotted dimgray;
}
pre {
    font-size: 1.2em;
}
span.keyword {
    color: #6495ed;
    font-weight: normal;
}
span.string {
    color: #b8860b;
}
span.library {
    color: #20b2aa;
}
span.comment {
    color: #6b8e23;
}
}
