css:
@-moz-document domain("science20.com") {
body {
	color: #b5b5b5;
	background-color: #0a0a0a;
}

#page-wrapper {
	border-left: 1px solid #555;
	border-right: 1px solid #555;
}

#main-wrapper {
	background: #000;
	border-left: 1px solid #555;
	border-right: 1px solid #555;
}

a:link, a:visited {
	color: #ccc;
}

li a.active {
	color: #fff;
}

/*
ul li, ul.menu li, .item-list ul li, li.leaf {
	background-image: filter(url(/sites/all/themes/sbv2/images/bullet-blue.png),invert(100%));
}
*/

div#content {
	border-right: 1px solid #555;
}

#cat-wrapper {
	border-right: 1px solid #555;
}
#blog-wrapper {
	background: #000;
}
.bb-zebra {
	background: #2e2a1f;
}
#blog-header a, #blog-header link {
	color: #a6a6a6;
}


#sidebar-right {
	background: #000;
}
#sidebar-right .sb-r-block {
	color: #fff;
}

ul.quicktabs_tabs.quicktabs-style-basic {
	border-bottom: 1px solid #555;
}
ul.quicktabs_tabs.quicktabs-style-basic li {
	border: 1px solid #1a1a1a;
}
ul.quicktabs_tabs.quicktabs-style-basic li:hover {
	border: 1px solid #333;
}
ul.quicktabs_tabs.quicktabs-style-basic li.active {
	border: 1px solid #555;
	border-bottom: 1px solid #555;
	background-color: #000;
}
ul.quicktabs_tabs.quicktabs-style-basic li.active a {
	color: #ffffff;
	border-bottom: 1px solid #000;
}
.quicktabs_main.quicktabs-style-basic {
	background-color: #000;
	border: 1px solid #555;
}




.friend-user-block-tools, .online-user-block-tools, .online-user-block-title, .friend-user-block-title {
	background: #000;
}


#content-page {
	border-right: 1px solid #555;
}
.node-content img {
	background-color: #111111;
	border: 1px solid #555555;
}
.node-meta img {
	background-color: #111111;
	border: 1px solid #555555;
}
.teaser {
	border-bottom: 5px solid #111111;
}

div#new-node-profile {
	border: 1px solid #555555;
	background-color: #000000;
	background-image: none;
}


.node blockquote {
	background-color: #000;
	border-left: 5px solid #222;
	color: #8497ae;
}

div#new-sb-column-sidebar {
	border: 1px solid #555555;
	background-color: #101018;
	background-image: none;
}

