@-moz-document domain("urlex.org") {
body {
    background: #424140;
    color: #fff;
}
body,
textarea,
input {
    font-family: Arial, sans-serif;
    font-size: 18px;
}
body.pro {
    margin: 3em 1em 1em;
    text-align: center;
}
input,
input:hover,
textarea {
    background: #ac8;
    border: 2px outset #bf8;
    border-radius: 7px;
    box-shadow: #222 1px 2px 5px;
    color: #231;
}
textarea {
    background: #efd;
    border: 4px solid #ac8;
    color: #000;
}
textarea.d {
    color: #bca;
}
#w input {
    width: 500px;
    letter-spacing: .3em;
    text-transform: uppercase;
}
input:active {
    padding: 6px 4px 4px 6px;
    border-style: inset;
    background: #8a6;
    color: #120;
}
textarea:focus,
input:hover,
input:focus {
    border-color: #efb;
    box-shadow: #cf5 0 0 10px;
}
h1,
h2,
p,
a {
    color: #dec;
}
h1,
h2 {
    color: #fff;
}
.c {
    background-image: none;
    padding-top: 0;
}
#e {
    text-shadow: #111 0 1px 2px;
    margin: 60px 0 30px 0;
}
#f {
    opacity: .4;
}
#r {
    background: #333;
}
#r a {
    color: #f53;
}
#r td {
    border-bottom: 2px solid #424140;
    text-shadow: #000 0 1px 1px;
}
a > u {
    color: #df0;
}
a:hover,
h1[onclick]:hover,
h2[onclick]:hover {
    text-shadow: none;
}
body.pro h1 {
    background-image: none;
    padding-top: 0;
}
.f.g,
.g {
    display: none !important;
}
.f.c {
    float: initial
}
}

@-moz-document url-prefix("https://userstyles.org/styles/163234/u") {
/* Self-promo */
div[class*="recommended-styles_recommendedStylesWrapper_"]:not(#krystian3w),
div.recommendations:not(#krystian3w) {
  display: none !important;
  visibility: hidden !important;
  opacity: 0 !important;
  filter: opacity(0) !important;
  user-select: none !important;
  pointer-events: none !important;
  width: 0 !important;
  height: 0 !important;
}
div[class*="recommended-styles_recommendedStylesWrapper_"]:not(#krystian3w) * {
  width: 0 !important;
  height: 0 !important;
}
}
