css:
@-moz-document url("https://catwar.su/cw3/")
{
  div#history_block div
  {
    background-color: #a147a6;
  }

  div#thdey
  {
    margin-left: 10px;
  }

  #history div
  {
    background-image: url('http://d.zaix.ru/8bvC.jpeg');
    margin-top: 10px;
    margin-left: 5px;
    color: transparent;
  }

  span#location
  {
    color: #a147a6 !important;
    position: fixed;
    width: 170px;
    top: 243px;
    left: 25px;
    background-color: #b69ab8;
    border: 0.5px solid #b69ab8;
    border-radius: 10px;
    text-align: center;
    padding: 5px;
    font-weight: bold;
  }

  a#history_clean
  {
    margin-left: 5px;
  }

  span#ist
  {
    margin-left: 5px;
    color: #a147a6 !important;
  }

  div#cages_div
  {
    opacity: 1 !important;
  }

  select
  {
    background-color: #522d54;
    color: #a147a6;
  }

  body
  {
    background-color: #473647;
    color: #a147a6;
  }

  .small
  {
    font-size: 14px;
    margin-left: 42%;
  }

  div#tir
  {
    margin-left: 10px;
    margin-top: 10px;
  }

  #akten
  {
    margin-left: 5px;
  }

  a
  {
    color: #a78da8;
  }

  input
  {
    background-color: #522d54;
    color: #a147a6;
    border: 0.5px solid #b69ab8;
    border-radius: 5px;
  }

  hr
  {
    border: 0.5px solid #b69ab8;
  }

  .ui-slider
  {
    background: #522d54;
    border: 0.5px solid #b69ab8;
    margin-right: 50px;
  }

  .ui-slider .ui-slider-handle
  {
    background: #7e5c80;
    border: 0.5px solid #b69ab8;
  }

  .move_name
  {
    color: #1e101f;
  }

  .nick
  {
    color: #a78da8;
  }

  .adopt-id
  {
    color: #fff;
  }

  .hotkey
  {
    background-color: #522d54;
    color: #a147a6;
    border: 0.5px solid #b69ab8;
    border-radius: 5px;
  }

  .ui-scroll
  {
    background-color: #b18db3;
  }

  #fightPanel
  {
    background-color: #1e101f;
    border: 0.5px solid #423842;
  }

  #block_mess
  {
    margin-left: 10px;
  }

  #tr_chat
  {
    background-color: #423842;
    padding: 0px;
  }

  #chat_msg
  {
    background-color: #423842;
    border: 0px;
  }

  #tr_actions
  {
    background-color: #412242;
    border: 0px;
  }

  #block_deys
  {
    margin-top: 5px;
  }

  #mit
  {
    background-color: #522d54;
    color: #a147a6;
    border: 0.5px solid #b69ab8;
    border-radius: 5px;
  }

  #mitok
  {
    margin-top: 2px;
    background-color: #522d54;
    color: #a147a6;
    border: 0.5px solid #b69ab8;
    border-radius: 5px;
  }

  #main_table
  {
    border: 0px;
    margin-top: 10px;
  }

  #tr_tos
  {
    display: none;
  }

  #tr_mouth
  {
    background-color: #412242;
  }

  #info_main
  {
    background-color: #412242;
  }

  #family
  {
    background-color: #412242;
  }

  a#history.toggle
  {
    color: #a147a6;
    font-size: 16px;
    margin-left: 10px;
    margin-bottom: 5px;
  }

  #history
  {
    background-color: #412242;
    width: 100%;
    margin-left: 10px;
  }

  #parameter
  {
    position: fixed;
    width: 185px;
    top: 20px;
    left: 20px;
    border-radius: 10px;
    text-align: left;
    padding: 3px;
  }

  a#parameters.toggle
  {
    display: none;
  }

  #block_mess
  {
    margin-top: 5px;
    margin-bottom: 5px;
  }

  #family
  {
    display: none;
  }

  #cages
  {
    background-color: transparent;
  }

  #text
  {
    color: #fff;
  }

  #might_table
  {
    margin-bottom: 21px;
  }

  p#error
  {
    top: 270px;
    left: 25px;
    background-color: #b69ab8;
    border: 0.5px solid #b69ab8;
  }

  #items
  {
    padding-left: 17px;
    padding-top: 5px;
    padding-bottom: 5px;
  }

  input:focus
  {
    background-color: #7e5c80;
  }

  #newchat
  {
    background-color: #b18db3;
    color: #473647;
  }

  #newls
  {
    background-color: #b18db3;
    color: #473647;
  }

  a:hover
  {
    color: #b18db3;
  }

  .myname
  {
    background-color: #b18db3;
    color: black;
  }

  .title
  {
    display: none;
  }

  #tr_sky
  {
    display: none;
  }
}
