css:
@-moz-document regexp("(http|https)://www\\.furvilla\\.com/(?!(villager/[0-9]*(?:\\?|$)|profile/[0-9]*(?:\\?|$))).*") {
/*======================================================================================================================*/
/*                                                                                                                      */
/*                                         F U R V I L L A      R E W O R K E D                                         */
/*                                                       vanilla                                                        */
/*                                                                                                                      */
/*                                                      v.1.1.8                                                         */
/*                                                   written by a                                                       */
/*                                                   sneaky fellow                                                      */
/*                                                                                                                      */
/*                                                                                                                      */
/*                                                                                                                      */
/*                                  based heavily on and borrows code/ideas from:                                       */
/*                       FV Battling by msjanny (https://userstyles.org/styles/142352/fv-battling)                      */
/*         FV Nightmode Redesigned by CuteHornyUnicorn (https://userstyles.org/styles/131007/fv-nightmode-redesigned)   */
/*                                                                                                                      */
/*======================================================================================================================*/

body {
    background: linear-gradient(#d7f9ff, #52B8E4, #5A80D9, #222B70) !important;
    background: -webkit-linear-gradient(#d7f9ff,#52B8E4, #5A80D9, #222B70) !important;
    background: -webkit-gradient(#d7f9ff,#52B8E4, #5A80D9, #222B70) !important;
    background-attachment:fixed !important;
}

/*---- GENERAL ----*/

::-webkit-scrollbar {
    width: 15px;
}
 
::-webkit-scrollbar-track {
    background: #E9F1F7;
}
 
::-webkit-scrollbar-thumb {
  background-color: #0e6cb0;
  outline: 1px solid #0e6cb0;
}

.content h1 small {
    margin-bottom:5px;
}

/*------------------------------------------------ BG ANIMATIONS ---------------------------------------------------------------------*/

/*clouds*/
header:before {
    content: ' ';
    position: fixed;
    display:block;
    top: 200px;
    left: 0px;
    z-index: -2;
    background:transparent url(https://i.imgur.com/SF1y1QU.png) repeat top center;
    min-height: 15000px;
    height: 100%;
    width: 100%;
    animation:clouds 3600s linear infinite;
}
/*more clouds*/
header:after {
    content: ' ';
    position: fixed;
    display:block;
    top: -200px;
    left: 0px;
    z-index: -2;
    background:transparent url(https://i.imgur.com/SF1y1QU.png) repeat top center;
    min-height: 15000px;
    height: 100%;
    width: 100%;
    opacity: .5;
    animation:clouds 1200s linear infinite;
}

/* bg animations defined */
@keyframes clouds {
    from {
        background-position:0 0;
        
    }
    to {
        background-position:50000px 0;
        
    }
}

/*header*/
header nav {
  background-image: -webkit-linear-gradient(#2e498f, #37317e);
  background-image: -webkit-gradient(linear, left top, left bottom, from(#2e498f), to(#37317e));
  background-image: linear-gradient(#2e498f, #37317e);
}

/*header links*/
header nav a {
  color: #fff !important;
  text-shadow: 1px 1.732px 2px rgba(0, 0, 0, 0.25);
}

/*user panel*/
header .user-panel {
    background:#04b1e7;
}

.btn {
  -webkit-border-radius: 16px;
          border-radius: 16px;
  background-image: -webkit-linear-gradient(#2ecbff, #0088c8);
  background-image: -webkit-gradient(linear, left top, left bottom, from(#2ecbff), to(#0088c8));
  background-image: linear-gradient(#2ecbff, #0088c8);
  color: #fff !important; }

.btn:hover {
  background-image: -webkit-linear-gradient(#2e488f, #37317e);
  background-image: -webkit-gradient(linear, left top, left bottom, from(#2e488f), to(#37317e));
  background-image: linear-gradient(#2e488f, #37317e); }

.btn-link {
  color: #0e6cb0 !important; }

.btn-link:hover {
  color: #004e86 !important;}

header nav .dropdown-menu a {
  color: #004e86 !important;
}

header nav .dropdown-menu a:hover {
  color: #23527c !important; }

.multi-column-dropdown li a {
      color: #333; }
    .multi-column-dropdown li a:hover {
      color: #262626;
      background-color: #28c2f8; }

header .btn:hover {
  background-image: -webkit-linear-gradient(#2ecbff, #0088c8);
  background-image: -webkit-gradient(linear, left top, left bottom, from(#2ecbff), to(#0088c8));
  background-image: linear-gradient(#2ecbff, #0088c8); }


/*footer links colors*/
footer a {
    color: #0e6cb0 !important;
}
footer a.red {
    color:#ed1c24 !important;
}

/*headers*/
.widget-header h3 {
  -webkit-border-radius: 8px 8px 0 0;
          border-radius: 8px 8px 0 0;
  background-image: -webkit-linear-gradient(#2ecbff, #0088c8);
  background-image: -webkit-gradient(linear, left top, left bottom, from(#2ecbff), to(#0088c8));
  background-image: linear-gradient(#2ecbff, #0088c8);
  color: #fff; }

.widget-header {
    border-color: #007fbb !important;
}


/*tables in stall search, notifications, etc*/
.table tr {
    border-bottom: 1px solid #C6C6C6;
}

.table > thead > tr > th {
    border-bottom: 0px !important;
}

.content .widget-content .table tr:nth-child(odd), .registration-well .table tr:nth-child(odd) {
    background:rgba(104, 179, 255, .1);
}

.content .widget-content .table tr:nth-child(even), .registration-well .table tr:nth-child(even) {
    background:rgba(104, 179, 255, .3);
}

/*widget background, job descriptions, herbalist instruction divs*/
.content .widget-content, .job-description, .registration-well {
    border-radius:5px;
}

/*======================== villagers page, affects some inventory stuff too =============================*/

.fur-residents li {
    width:310px;
}

.fur-residents li .fur-residents-item-info, .inventory-item-info, .item-block, .modal .item-block, .modal .inventory-item-info, .construction-item-info, .fur-idol-prize, .jobs li a {
    -webkit-transition: 0.3s ease-in-out;
    -moz-transition: 0.3s ease-in-out;
    -o-transition: 0.3s ease-in-out;
    transition: 0.3s ease-in-out;
}

.fur-residents li .fur-residents-item-info:hover, .inventory-item-info:hover, .item-block:hover, .modal .item-block:hover, .modal .inventory-item-info:hover, .construction-item-info:hover, .jobs li a:hover {
    background:#DBEAFF;
}

.fur-residents li .durability {
    width: 298px;
    background:#B1C6E2 !important;
}

/*append gavel to workers*/
.fur-residents-item-info::before {
    color:#0e6cb0;
    content: '\f0e3';
    position: absolute;
    font-family: 'FontAwesome';
    font-size:20px;
    display:block;
    top: 3px;
    left: 10px;
    z-index:2;
}
/*remove gavel from non-workers + add a villager option*/
.fur-residents-item-info.mute::before, .fur-residents-item-add .fur-residents-item-info::before {
     content: ' ' !important;
}

/*fixes to make non-workers better*/
.fur-residents-item-info.mute, .fur-residents-item-add .fur-residents-item-info {
    opacity:0.5;
}
.fur-residents-item-info.mute:hover, .fur-residents-item-add .fur-residents-item-info:hover {
    opacity:1;
}

/*====================================== general item styles for multiple careers =======================================*/

.crafting-list-item-info {
    -webkit-transition: 0.3s ease-in-out;
    -moz-transition: 0.3s ease-in-out;
    -o-transition: 0.3s ease-in-out;
    transition: 0.3s ease-in-out;
}

.crafting-list-item-info:hover {
    background:#DBEAFF;
}

/*==================================================== herbalist ========================================================*/

.animal-item-info .durability {
    width:225px;
}

.animal-item-info {
    -webkit-transition: 0.3s ease-in-out;
    -moz-transition: 0.3s ease-in-out;
    -o-transition: 0.3s ease-in-out;
    transition: 0.3s ease-in-out;
}
.animal-item-info:hover {
    background:#DBEAFF;
}

.table thead, .table tr:hover .table thead, .thread-user-post-middle table td.head {
    background: linear-gradient(#2ecbff, #0088c8) !important;
    background: -webkit-linear-gradient(#2ecbff, #0088c8) !important;
    background: -webkit-gradient(#2ecbff, #0088c8) !important;
}

.thread-user-post-middle table td.head font[color*="#37317e"] {
    color: transparent !important;
}

.thread-user-post-middle table {
    margin: 10px auto 10px auto;
}

/*========= carpenter ==========*/

input[value*="Update Prices"] {
    margin-left:400px;
}


/*=================================*/

/* shrinks + positions the header */
header {
    /*position:fixed;*/
    height: 78px;
    left: -125px; /*31*/
    /*z-index: 99;*/
}

/*positions content */
.content {
    position: relative;
    top: 0px; /*78*/
    left:-125px;
    border-radius: 0px 0px 10px 10px;
    min-height:400px !important;
    background: linear-gradient(#E4F2FF, #FFF, #FFF, #FFF) !important;
    background: -webkit-linear-gradient(#E4F2FF, #FFF, #FFF, #FFF) !important;
    background: -webkit-gradient(#E4F2FF, #FFF, #FFF, #FFF) !important;
}

.content:after {
    display: none !important;
}

/* moves the search bar to above right-column widgets */
header .search-form {
    position:fixed;
    z-index:1000;
    top: 6px;
    right: 47px; /*6px, 30px*/
}

header .search, input[placeholder*="Search..."] {
    background:#DBEAFF url(https://i.imgur.com/X2cwkwM.png) no-repeat 100% 50%;
}


/* hiding unnecessary content - logo + ads*/
#logo, div[style*="width: 728px; height: 90px;"] {
    display: none;
}


/*------------------------------------------------------ NAVIGATION ------------------------------------------------------------------*/
/* shrinks the header (moving top part of navigation to proper position) */
header nav {
    top: 0;
    border-radius: 0;
}

/*moves nav farther to left*/
header nav ul {
    margin-left:170px;
}

/*---- LEFT COLUMN ----*/
/*repositions left column*/
.left-column {
    width:950px !important;
    float: none !important;
}
.header-left {
    float: none;
}

/*---------------------------------------------------------- RIGHT COLUMN ------------------------------------------------------------*/
/*repositions right column*/
.right-column {
    position: fixed;
    right: 25px; /*10px*/
    top: 0px;
    overflow-y: auto !important;
    height: 105%;
    width: 230px !important;
    visibility: hidden;
    z-index: 999;
    padding-bottom:20px;
}

.right-column .text-right {
    padding-bottom:15px;
}
.content .widget {
    visibility: visible;
    margin-bottom: 0px !important;
}
.right-column .widget:first-of-type {
    padding-top:0px; /*10px;*/
}
.right-column .widget:first-of-type .text-center {
    padding:10px;
}

.right-column .widget:first-of-type .widget-content {
    padding-top:50px !important; /*10px;*/
    padding-bottom:10px !important; /*10px;*/
}

.right-column .widget-content {
    padding: 10px 0 0 0 !important;
    background: #fff !important;
    border-radius:0;
}

.right-column .widget:first-of-type .widget-header {
    display:none !important;
}

.right-column .widget-header {
    background: linear-gradient(#2ecbff, #0088c8);
    background: -webkit-linear-gradient(#2ecbff, #0088c8);
    background: -webkit-gradient(#2ecbff, #0088c8);
    border: none !important;
}

.right-column .widget-header h3 {
    background: none !important;
    border: none !important;
    color:#fff !important;
}

/*recent posts widget*/

.right-column .widget .forum-posts {
    padding-bottom:0;
    margin-top:0;
}

.right-column .text-right {
    background:#fff;
    padding-top:5px;
}

.right-column .widget-content:last-of-type {
    padding-bottom:25px !important;
}

/*---------------------------------------------------------- NOTIFICATIONS -----------------------------------------------------------*/
/*changes bg color of notifications to be less transparent*/
/*also allows notifications to scroll instead of strech down the page, just in case u get a lot*/
.notifications-widget .widget-content {
    background:#fff !important;
    padding:0;
    margin:0;
    height:130px;
    overflow:auto;
}

.notifications-widget .widget-content .table tr:nth-child(odd) {
    background:rgba(104, 179, 255, .1);
}
.notifications-widget .widget-content .table tr:nth-child(even) {
    background:rgba(104, 179, 255, .3);
}

.notifications-widget .close {
    opacity:1;
}

/*shrinks notifications widget*/
.notifications-widget {
    height: 32px;
    width: 125px; /*116*/
    overflow: hidden;
    position: absolute;
    left: -80px; /*74*/
    top: 54px;
    -ms-transform: rotate(270deg);
    -webkit-transform: rotate(270deg);
    transform: rotate(270deg);
    z-index: 999;
}

/*expands notifications widget on mouseover*/
.notifications-widget:hover {
    height: auto;
    width: auto;
    left: -42px; /*-42*/
    overflow: visible;
    top: 0;
    z-index: 999;
    -ms-transform: rotate(0deg);
    -webkit-transform: rotate(0deg);
    transform: rotate(0deg);
    padding: 10px 10px 0 42px;
}

.notifications-widget:hover .widget-content {
    box-shadow: 0px 0px 50px rgba(189, 144, 252, .2);
}

/*keeps notifications header still on mouseover*/
.notifications-widget:hover .widget-header {
    -ms-transform: rotate(270deg);
    -webkit-transform: rotate(270deg);
    transform: rotate(270deg);
    position: absolute;
    z-index: 1000;
    left: -48px; /*42*/
    top: 54px;
    border: 0;
}
/*animates notifications label to make it more noticeable */
.notifications-widget h3 {
    color:#0e6cb0 !important;
    text-transform:uppercase;
    -webkit-animation: bouncyX 1s infinite  alternate;
    animation: bouncyX 1s infinite  alternate;
    -ms-transform: rotate(270deg);
    -webkit-transform: rotate(270deg);
    transform: rotate(270deg);
    background:none !important;
}
/*stops notif label's animation on hover*/
.notifications-widget:hover h3
{
    -webkit-animation-play-state: paused;
    -moz-animation-play-state: paused;
    -o-animation-play-state: paused;
     animation-play-state: paused;
    margin-top:8px;
}
.notifications-widget .table {
    margin:0 !important;
}

/*keyframes for bouncing animation*/
@-webkit-keyframes bouncyX {
    0% { transform: translateX(0); }
    100% { transform: translateX(-10px); }
}
@keyframes bouncyX {
    0% { transform: translateX(0); }
    100% { transform: translateX(-10px); }
}

/*---- TABLES ----*/
.table tr td {
    background: none !important;
    border: none !important;
    text-align: left;
}

.table-striped > tbody > tr:nth-of-type(odd), .table-striped > tbody > tr:nth-of-type(even) {
    background: none;
}


.table tr:hover td {
    background: none !important;
}

/*---- MODAL ----*/
/*fixes padding*/
.modal-content {
    top:10px;
    padding: 30px !important;
}
/*hides logo*/
.modal-content .logo {
    display:none;
}
/*x button*/
.modal-header .close {
    opacity:0.8;
}
.modal-header .close:hover {
    opacity:1;
}
/*lil image fixes*/
.explore-item, .well {
    background:transparent;
    border-color:transparent;
}
.counter, .inventory-item-info .counter, .item-block .counter, .inventory-item-info:hover .counter, .item-block:hover .counter, .widget-header .counter {
    background: linear-gradient(#2ecbff, #0088c8);
    background: -webkit-linear-gradient(#2ecbff, #0088c8);
    background: -webkit-gradient(#2ecbff, #0088c8);
    color:#fff; /*#68ebff*/
    border-color:#000;
    font-weight:bold;
    -webkit-transition: 0.3s ease-in-out;
    -moz-transition: 0.3s ease-in-out;
    -o-transition: 0.3s ease-in-out;
    transition: 0.3s ease-in-out;
}

/*========= edit profile ==============*/
.villagers-list-edit li:hover {
    background:#DBEAFF;
}

/*========= house page ==========*/
#house {
    display:block;
    margin-left:auto;
    margin-right:auto;
}


/*================== footer =================*/
footer {
    width: 550px;
    background: #fff;
    padding:5px;
    border-radius: 10px;
    position:relative;
    left:-125px;
    margin-bottom:30px;
    margin-top:-40px;
}
footer .pets, footer .logo-small {
    display:none;
}

footer .social {
    height:50px;
    top: 30px;
    right: 200px;
    display:block;
    margin-left: auto;
    margin-right:auto;
    margin-bottom:0px;
    line-height:100%;
}

footer .top {
    top:0px;
    height:100px;
}
}

@-moz-document url("http://www.furvilla.com/townhall") {
/*==================================== town hall ========================================================*/

#map-img {
    display:none;
}

.left-column .widget.recent-posts .text-right {
    position:absolute;
    left:40%;
    top:210px;
}

.widget.recent-posts {
    min-height:280px;
}

.left-column .widget.recent-posts .widget-content:nth-of-type(2) {
    background: none !important;
}

.mayor-announcements {
    float:left;
    margin-left:10px;
}


.mayor-avatar {
    margin-left:80px;
}

.toolbar-title {
    border: none !important;
}

.toolbar-title h2:before {
    display: none !important;
}

.toolbar-title h2, .left-column .widget.recent-posts .widget-header h3 {
    color:#fff !important;
    text-transform:uppercase;
    letter-spacing:2px;
    font-weight:bold !important;
}

.left-column .widget.recent-posts .widget-header  {
    text-align:center;
    width:60%;
    display:block;
    padding:5px;
    margin-left:auto;
    margin-right:auto;
    border: none;
    border-radius:10px;
    background: linear-gradient(#2ecbff, #0088c8) !important;
    background: -webkit-linear-gradient(#2ecbff, #0088c8) !important;
    background: -webkit-gradient(#2ecbff, #0088c8) !important;
    text-shadow: 0 0 2px rgba(0, 0, 0, .2), 0 0 2px rgba(0, 0, 0, .2), 0 0 2px rgba(0, 0, 0, .2);
}

.left-column .widget.recent-posts .widget-header h3 {
    background: transparent !important;
}

.toolbar-title {
    text-align:center;
    width:60%;
    display:block;
    padding:5px;
    margin-left:auto;
    margin-right:auto;
    margin-top:25px;
    margin-bottom:0;
    border: 1px solid #0e6cb0;
    border-radius:10px 10px 0 0;
    background: linear-gradient(#2ecbff, #0088c8) !important;
    background: -webkit-linear-gradient(#2ecbff, #0088c8) !important;
    background: -webkit-gradient(#2ecbff, #0088c8) !important;
    text-shadow: 0 0 2px rgba(0, 0, 0, .2), 0 0 2px rgba(0, 0, 0, .2), 0 0 2px rgba(0, 0, 0, .2);
}

.toolbar-shops .text-center {
    background:#DBEAFF;
    border-color: #0e6cb0;
    width:60%;
    display:block;
    padding-top:20px;
    padding-bottom: 20px;
    border-radius:0 0 10px 10px;
    margin-left:auto;
    margin-right:auto;
    -webkit-transition: 0.3s ease-in-out;
    -moz-transition: 0.3s ease-in-out;
    -o-transition: 0.3s ease-in-out;
    transition: 0.3s ease-in-out;
}

.toolbar-shops .text-center:hover {
    background:#EAF2FF;
    border-color: #0e6cb0;
}

/*== event speechbubble ===*/

.widget.mayor.clearfix .widget-content {
    background:transparent !important;
    padding:0px 0 50px 0;
}

.user-comments .user-comment {
    background: #fff !important;
    padding:15px 15px 15px 15px;
}

.recent-posts .btn.btn-link, .recent-posts .btn.btn-link:hover {
    color:#fff !important;
    text-decoration:none !important;
}
.user-comments .user-avatar-wrapper {
    margin-bottom:10px;
    margin-left:117px;
    margin-right:10px;
}
}

@-moz-document url-prefix("http://www.furvilla.com/") {
/*============= small fixes for entire site =================*/

/*hide ads div lol*/
.content .text-center.margin-10px:first-of-type {
    display: none;
}
}

@-moz-document url-prefix("http://www.furvilla.com/career/warrior/battle") {
/*===============================================================*/
/*               BATTLING CODE SECTION 1                         */
/*===============================================================*/

/* fixes width of battle area for compatibility */
.content .left-column {
    width:790px !important;
    margin-left: 190px;
}

/*moves the monster's stats to the right + minor aesthetic changes*/
.enemy-panel .stat-panel {
    position:absolute;
    left:-590px;
    top:0;
    display: block !important;
    border: #eee 1px solid;
    width: 200px;
    margin: 0;
    cursor: pointer !important;
    z-index: 89;
}

.warrior-panel .stat-panel {
    background:#DBEAFF;
}

/*hides monster's image in monster stat panel*/
.stat-panel .text-center:first-of-type {
    display: none;
}

/*shrinks the monster's description*/
.stat-panel .text-center {
    font-size: 12px;
    line-height: 1.5em;
    min-height: 75px;
    overflow: auto;
}

/*shrinks margins*/
.stat-panel hr {
    margin: 6px 0;
}

/*turns the stats into a single column*/
.stat-panel > .row > .col-sm-6 {
    width: 100%;
}

/*condenses things down to five abilities per row*/
.equipment-slot {
    margin: 0 11px 20px 0;
    border: 0;
    width: 130px;
    height: 180px;
    vertical-align: top;
    padding: 6px;
    position: relative;
}

/*prevents weird word wrapping*/
.equipment-slot .label {
    display: block;
    padding: 0;
    position: absolute;
    bottom: 10px;
    width: 117px;
    overflow: hidden;
}

/*spacing equipment slots*/
.equipment-wrap .row {
    padding: 0;
    margin: 0;
}

/*spacing equipment slots*/
.equipment-slot:last-child {
    margin-right: 0;
}

/*moves counter to top left*/
.equipment-slot .counter {
    position: absolute;
    top: 6px;
    left: 6px;
    padding: 0 5px;
}

/*aesthetic changes*/
.enemy-panel .stat-panel,
.equipment-slot,
.pass-span,
.enemy-panel,
.warrior-panel,
.equipment-slot:hover,
.pass-span:hover,
.enemy-panel:hover,
.warrior-panel:hover,
.battle-log {
    background: rgba(255, 255, 255, .7);
}

/*aesthetic changes*/
.enemy-panel-wrap:hover, .warrior-panel-wrap:hover {
    background: none;
}

/*condenses loot*/
.battle-loot {
    border: 0;
    display: inline-block;
    max-width: 130px;
}

/*continue battling*/
.battle-complete > p > .btn:nth-child(2) {
    position: relative;
    z-index: 2050; /*2050*/
}


/*use chocolate candycorn*/
.battle-end-stats .row.text-center a[data-url^="/use/candycorn/1894"] {
    position: absolute;
    top: 528px;
    height: 32px;
    width: 252px;
    background: url(http://i.imgur.com/kiFFCVw.png) no-repeat;
    background-size:cover;
    left: 290px;
    -webkit-transition: 0s ease-in-out;
