css:
@-moz-document domain("yandex.ru") {
/*стандартный шрифт яндекса*/
.typo {
    font-family: Arial,Helvetica,sans-serif !important;
    
}
/*выключает "плюс" кнопку*/
div.yaplus.yaplus_available_yes.i-bem.yaplus_js_inited {
    display: none;
}
a.home-link.desk-notif-card_login-plus.home-link_intense-blue_yes {
    display: none;
}
/*выключает гомно снизу поиска под цифрами перелистывания страниц*/
.serp-footer__main {
    display: none;
}
/*раздражающий колокольчик типа с 3 оповещениями убирает*/
.notifier.notifier_header.notifier_no-login_yes.notifier_size_m.notifier_fade_yes.serp-header__notifier.i-bem.notifier_has-unread_yes.notifier_js_inited {
    display: none;
}
}
