css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("rapidshare.de"),domain("rapidshare.com") {

/*

*/
img[src*="/img2/premium_vorteile_de.jpg"],

img[src*="/img2/dl_langsam.gif"],

img[src*="/img2/dl_schnell.gif"],

img[src*="/img2/premium_vorteile_en.jpg"],

img[src*="/img2/lottery_teaser.jpg"],

input[value*="Premium user"],

input[value*="Create Premium Account"],

div[style*="text-align: right; padding-top: 10px;"],

div.klappbox p#small, h2,

div.klappbox p:not([class]),

div.klappbox table tbody tr td img,

div.klappbox table tbody tr td img+br,

div.klappbox table tbody tr td input[type="image"],

div.klappbox table tbody tr td input[type="image"]+br,

div.klappbox img+table+a+h2+p,

div.klappbox img+table+a+h2+p+h2+p,

div.klappbox img+table+a+h2+p+h2+p+p,

div.klappbox img+table+a+h2+p+h2+p+p+h2+p,

div.klappbox img+table+a+h2+p+h2+p+p+h2+p+p,

div.klappbox img+table+a+h2+p+h2+p+p+h2+p+p+h2+hr,

div.klappbox p+table+a+h2+p,

div.klappbox p+table+a+h2+p+h2+p,

div.klappbox p+table+a+h2+p+h2+p+p,

div.klappbox p+table+a+h2+p+h2+p+p+h2+p,

div.klappbox p+table+a+h2+p+h2+p+p+h2+p+p,

table.klapp

{

   display:none !important;

}

div.klappbox p.downloadlink,div.klappbox>p

{

   display:block !important;

}

td[width*="300"]{

   width:0 !important;

}

input[value*="Free user"]{

   padding:10px 25px !important;

   color: #001A49 !important;

   font-size: 20px !important;

   font-weight: bold !important;

   text-transform: uppercase !important;

   -moz-appearance: none !important;

   -moz-border-radius: 24px !important;

   border: 1px solid #777 !important;

   background: #999 !important;

}

input[value*="Free user"]:hover{

   color: #fff !important;

   background: #001A49 !important;

}

img[alt*="logo"]{

   display:block !important;

   height:107px !important;

   width:150px !important;

}

}
