css:
@-moz-document domain("git-scm.com") {
:root {
    --body-bg-color: #151515;
    --body-color: #aaa;
    --center-bg-color: #202020;
    --nav-color: #aaa;
    --nav-hover-color: #0388a6;
    --command-bg-color: #222;
    --code-color: #b8860b;
    --border-radius: 2px;
    --option-color: #8b4513;
    --link-color: #0388a6;
    --link-hover-color: #d2691e;
    --border-color: #333;
    --font:"Segoe UI", Verdana;
    --code-font: monospace;
    --highlight-color: #555;
}
/************************************************************
*	General
************************************************************/
 .windows.chrome body, .windows.ie8 body, body {
    font-family: var(--font) !important;
    background: none;
    background-color: var(--body-bg-color);
    color: var(--body-color);
}
footer {
    border-top: 1px solid var(--border-color);
}
a, footer a {
    color: var(--link-color);
}
a:hover, ol.full-search-results a.url:hover, footer a:hover {
    color: var(--link-hover-color);
}
div.dropdown-panel, a.dropdown-trigger.active {
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    background-color: var(--body-bg-color);
    border: solid 2px var(--border-color);
}
a.dropdown-trigger.active {
    border-bottom: none;
}
/***********************************************************************
*	Docs Page
***********************************************************************/
 aside.sidebar nav ul li a {
    color: var(--nav-color);
}
aside.sidebar nav ul li a:hover {
    color: var(--nav-hover-color);
}
div#main {
    background-color: inherit;
    text-rendering: optimizeLegibility;
    border: none;
    padding: 0 1em;
}
pre, #main div.callout, aside.sidebar .callout {
    background-color: var(--command-bg-color);
    color: var(--code-color);
    border: none;
    font-family: monospace;
}
code {
    background-color: inherit;
    border: solid 1px var(--border-color);
    color: var(--code-color);
    font-family: var(--code-font);
}
h1, h2, h3 {
    color: var(--code-color);
}
dd {
    margin-bottom: 1em;
    border-bottom: 1px solid var(--border-color);
    padding-bottom: 1em;
}
div#reference-version {
    border: 1px solid var(--border-color);
    border-radius: var(--border-radius);
    background-color: inherit;
}
form#search {
    background-color: inherit !important;
    border: 1px solid var(--border-color);
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
}
input#search-text {
    font-family: var(--font) !important;
    padding: 0.3em 0.1em;
    line-height: normal;
    margin: 0;
}
dt.hdlist1 {
    color: var(--option-color);
}
/************************************************************
*	Home Page
************************************************************/
 div#masthead {
    background: none;
    background-color: var(--command-bg-color);
}
#front-nav a {
    color: var(--body-color);
}
/************************************************************
*	Search Page
************************************************************/
 span.highlight {
    background-color: var(--highlight-color);
}
}
