css:
@-moz-document domain("bitchute.com"), domain("disqus.com") {
/* Bitchute - Compact top of pages & some color adjustments
version: 0.0.2

This userstyle is intented for Bitchute to be in NIGHT MODE
You can switch to NIGHT MODE by clicking the Moon icon on the top right */

/* Remove the distracting logo on top */
#nav-top-menu {
    background-image: none !important;
}

/* Remove unnecessary border */
#page-bar,
#carousel,
.policy-detail {
    border-bottom: 0px !important;
}

/* Rmove the Carrousel on the -Subscribed- page */
#carousel {
    display: none !important;
}

/* Decrease Page Title font size from 30px to 25px */
#page-bar .page-title {
    font-size: 25px !important;
}

/* Overall page background from dark grey to black */
.btn-group-vertical > .btn-group::after,
.btn-group-vertical > .btn-group::before,
.btn-toolbar::after,
.btn-toolbar::before,
.clearfix::after,
.clearfix::before,
.container-fluid::after,
.container-fluid::before,
.container::after,
.container::before,
.dl-horizontal dd::after,
.dl-horizontal dd::before,
.form-horizontal .form-group::after,
.form-horizontal .form-group::before,
.modal-footer::after,
.modal-footer::before,
.modal-header::after,
.modal-header::before,
.nav::after,
.nav::before,
.navbar-collapse::after,
.navbar-collapse::before,
.navbar-header::after,
.navbar-header::before,
.navbar::after,
.navbar::before,
.pager::after,
.pager::before,
.panel-body::after,
.panel-body::before,
.row::after,
.row::before {
    /* display: table; */
    content: " ";
}

/* Color watch counts, like counts, and dislike counts a little brighter */
.video-info-bar ul.list-inline.list-unstyled.info-ul li {
    color: #FFFFFF !important;
}
.info-btn.video-like {
    color: #09ef37 !important;
}

/* Color the "Subscribe" button green when subscribed */
.btn.btn-default {
    background-color: #7CF27C !important;
}

/* Subscriber Count */
.text-uppercase [data-icon="users"],
#subscriber_count {
    color: #F0E357 !important;
}

/* Color the video author yellow */
.video-author a {
    color: #F0E357 !important;
}

/* Color the background of sidebar topic grey instead of red */
.sidebar-heading {
    background-color: #45573d !important;
}
.switch .slider {
    background-color: #45573d !important;
}
.switch input:checked + .slider {
    background-color: #7cf27c !important;
}

/* Make video title text more readable on "Subscribed" overview page */

/* color the channel name cyan */

/* color the viewcount white */
.video-card-text a {
    /*	color: #9AD81A !important;*/
    color: #FFF !important;
}
.video-card-text a[href*="channel"] {
    color: #33B873 !important;
}
.video-card-text p span {
    color: white !important;
}

/* color the comments whitegrey */
.post-message-container,
.post-message,
.post-content {
    color: #bdbcbc !important;
}
}
