@-moz-document domain("shazoo.ru") {
/*-- ИНФОРМАЦИЯ И ПОЯСНЕНИЯ -*/

/*-- Версия стиля 0.1.5 -*/

/*-- ИНФОРМАЦИЯ И ПОЯСНЕНИЯ -*/

/*
1. Что бы обновить стиль, в расширении stylus нажмите кнопку "менеджер" и рядом с названием сайта нажмите кнопку проверить обновления. 
Если вы вносили какие то изменения в основной код, то предварительно скопируйте свои правки и после обновления вставьте их обратно.
*/

/* ----------------------------------------------------------- */

.homeTopSection {
    height: 625px;
}
.homeTopSection.noFeature {
    height: 310px;
}
body {
    background: #272a2d !important
}
.v4 .headerContainer:before {
    background: #171819;
}
.footerContainer {
    background: #171819;
}
.footerContainer footer {
    background: #171819;
}
.homeTopSection.wide:before {
    background: none;
}
.homeTopSection:not(.wide) {
    background: #34383c;
}
.v4 #globalNav .nav > li > a {
    color: #b7b7b7;
}
.v4 #globalNav .nav > li > a:active,
.v4 #globalNav .nav > li > a:hover {
    color: #eee;
    background: #5c646d;
}
.contextPaneContainer .contextPaneContent > ul > li > a {
    color: #aaa;
}
input[type=text] {
    background: #373c42;
    color: #fff;
}
.lengthCounter {
    background: #1fb248;
}
.contextPaneContainer .contextNav > li:hover,
.contextPaneContainer .contextPaneContent > ul > li.selected,
.contextPaneContainer .contextPaneContent > ul > li.selected a {
    background: #5c646d;
}
.v4 .menuContent li:hover .contextPaneButton {
    background: #5c646d;
}
.contextPaneContainer .contextPane {
    background: rgb(31, 33, 35);
}
.v4 #globalNav .navLinks > li.active > a {
    background: #373c42;
}
.main #contentWrapper {
    background: #373c42;
}
#home .homeMain .homeLeft {
    background: #282b2f;
}
.homeMain {
    background: #1f2123;
}
.homeMid {
    background: #1f2123;
}
.homeMidSection {
    background: #34383c;
}
.homeLeft .entryTags li a {
    background: #1f2123;
}
.homeMidSection .tags > a {
    color: #bbab89;
    font-weight: 600;
    font-size: 18px;
}
.homeMidSection .tags > a:hover {
    color: #1da1f3;
}
#content p a {
    color: #6d95c3;
}
#content p a:hover {
    color: #1da1f3;
}
a:active, a:hover {
    color: #1da1f3;
}
h1.entryTitle a,
h2.entryTitle a,
h3.entryTitle a,
h4.entryTitle a,
h5.entryTitle a,
h6.entryTitle a {
    color: #c3c3c3 !important;
}
h1.entryTitle a:hover,
h2.entryTitle a:hover,
h3.entryTitle a:hover,
h4.entryTitle a:hover,
h5.entryTitle a:hover,
h6.entryTitle a:hover {
    color: #eee !important;
}
.homeTopSection .entryTitle a:hover {
    color: #eee !important;
}
.homeLeft .entryAuthor {
    color: #b49e72 !important;
}
.homeLeft .entryAuthor:hover {
    color: #1da1f3 !important;
}
.homeMain .sectionHeader h2 {
    color: #aaa;
}
@media only screen and (min-width: 801px) {
    .homeLeft .sectionHeader {
        margin: 5px -20px 20px -10px;
    }
}
.entryList .entry .entryByline {
    color: rgba(255, 255, 255, 0.49);
}
.entryList .entry .entryComments {
    color: rgba(255, 255, 255, 0.4);
}
.entryList .entry .entryByline a:hover:hover {
    color: #bebebe;
}
.entryByline .entryAuthor:hover {
    color: #1da1f3 !important;
}
#home .homeMain .homeLeft .sectionDate {
    color: rgba(255, 255, 255, 0.72);
}
.homeCenter .pickedEntries .entry {
    background: #282b2f;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #b5b5b5;
}
.homeCenter .entryDetails .entryMeta {
    color: #55adc9;
}
.homeCenter .entry .entryAuthor {
    color: #7d7d7d !important;
}
a {
    color: #56adc9;
    transition: color .2s ease;
}
#home .home-section .primary-tag-entries a {
    color: #b9b9b9;
}
#contextHeader {
    background: #373c42;
}
.v4 .entryContextHeader h1 {
    color: #e6e6e6;
}
.v4 .tagHeaderContainer:not(.game) .tagHeader .desktop li a {
    color: #a2bacc;
}
.v4 .entryContextHeader .byline {
    color: #b3b3b3;
    border-top: 1px solid #525252;
    border-bottom: 1px solid #525252;
}
body {
    color: #dadada;
}
.latestComments {
    background: #282b2f;
}
#scroll .arrow.bottom {
    border-top-color: #fff !important;
}
.headerContainer .js-scroll .arrow.bottom {
    border-top-color: #fff;
}
.headerContainer .js-scroll .arrow.top {
    border-bottom-color: #fff;
}
.topicList .topic {
    background: #282b2f;
}
a {
    color: #c3c3c3;
}
.topic .topicStatus span {
    background: #373c42;
}
.subNav {
    background: #1f2123;
}
#globalNav .subNav li a {
    color: #a7a7a7;
}
.icon,
[class^=icon-] {
    opacity: 1;
}
.userName {
    color: #b5b5b5;
}
#home .homeSection .tabsHeader li {
    color: #cccccc;
}
#home .homeSection .tabsHeader li.active {
    color: #cccccc;
}
#home .home-section .secondary-tags a {
    color: #cccccc;
}
.entryFeature .main #contentContainer {
    background: rgba(40, 43, 47, 0.62);
}
.commentsList .comment {
    background: rgba(40, 43, 47, 0.58);
}
.entryFeature #entry .meta {
    color: rgb(183, 183, 183);
}
.entryFeature #entry .byline .entryAuthor a {
    color: rgb(183, 183, 183) !important;
}
section.body strong,
section.lead strong {
    color: #e8e8e8;
}
.commentCreate.commentFormWrapper {
    background: #2e3237;
}
.topicNav {
    background: #1f2123;
}
.pager li.active a, .pagination li.active a {
    color: #fafafa;
    background-color: #5c636b;
}
.pager li a, .pagination li a {
    background: #272a2d;
    color: #ddd;
}
section.body blockquote {
    color: #86a6bb;
}
.body .reference {
    background: rgb(31, 33, 35);
}
.body .reference:hover {
    background: rgb(46, 50, 55);
}
section.brief {
    background: #282b2f;
    border: 1px solid #1f2123;
}
.userAvatar img {
    background: none;
}
#comments strong {
    color: #538cc3;
}
.entryComments .icon {
    opacity: 0.5;
    filter: invert(75%);
}
.entryList .entry .entryByline a:hover .icon {
    opacity: 0.5;
    filter: invert(100%);
}
.iconSmallArrowRight {
    filter: invert(45%);
}
del {
    text-decoration: none;
    color: #6d7073;
    background: #6d7073;
    padding: 0 10px;
}
.modal .modalFooter {
    background: #333;
}
.modal .modalContent {
    background: #333;
}
.modal .modalHeader {
    background: #333;
}
.mineralShop .tabsHeader li {
    color: rgb(142, 142, 142);
}
.mineralShop .tabsHeader li.active {
    color: rgb(212, 212, 212);
}
.homeRight .comment .commentDate {
    color: #6d95c3;
}
.homeRight .commentText {
    color: rgba(255, 255, 255, 0.5)
}
.homeRight .itemTitle h6 a {
    font-weight: 600;
    color: #bbb !important;
}
#userHome .locationImageContainer {
    opacity: 1;
    background-color: #282b2f;
}
#userHeader .userNav {
    background: #1f2123;
}
#userMineralsActivity .comment {
    background: rgb(40, 43, 47);
}
.calendarMonth .game {
    background: #282b2f;
}
.calendarMonth .gameReleaseDate {
    color: #b1b1b1;
}
.calendarSoon .gameReleaseDate {
    background: #282b2f;
}
.navLogo a {
    filter: invert(50%);
}
.iconSearch.large {
    filter: invert(70%);
}
.iconEnvelope.large {
    filter: invert(70%);
}
.iconEdit.large {
    filter: invert(70%);
}
#replyDialog .comment {
    background: #272a2d;
}
#searchDialog.overlay {
    background: rgba(21, 21, 21, 0.97);
}
.commentMineralBg.none:not(.editing) .commentContent {
    background: #222427;
    box-shadow: none;
    color: #ddd;
}
.entryFeature:not(.entryCards) .main #contentContainer {
    background: #373c42;
}
.entryContextHeader .byline .authorInfo a {
    color: #cecece;
}
}
