css:
@-moz-document domain("www.codewars.com") {
/******* dashboard ********/
.dark-theme {
    background-color: #acd1df !important;
    color: black !important;
}
.dark-theme h1, .dark-theme h2, .dark-theme h3, .dark-theme h4, .dark-theme h5, .dark-theme h6 {
    color: black !important;
}
/* top left block with "your next challenge" */
.dark-theme .is-darkened {
    background-color: white !important;
}
.dropdown:hover{
    background-color: #bbbaba !important;
}

/* under-under top right block with "Earn extra honor and gain..." */
.dark-theme .panel {
    color: black !important;
}
.btn.is-dark {
    color: black !important;
    background-color: white !important;
}
.btn.is-dark:hover {
    color: #888 !important;
    background-color: #e3e3e3 !important;
}

/* under top right block with "Allies..." */
.is-notice-text {
    color: #0534bf !important;
}

/* other sections */
.ide-editors
{
    filter: invert(100%);
}

#description, #sidenav {
       background-color: white;
    color: black;
}



.lang-python {
    background-color: white;
    color: black;
}

pre {
    background-color: white;
}
pre .hljs-string {
    color: #0f4;
}

.five, .coloumns {
    background-color: white;
    color: black;
}

span.mrn, span.has-tip, a.has-tip {
    color: black;
}


/* top right block with kata description */
.personal-trainer .trainer-info {
    background-color: #d9d9d9;
    color: black;
    /*filter: invert(100%);*/
}
.is-white-text {
    color: black !important; 
}
.markdown {
    color: black;
    background-color: #ddd;
}

/* bottom comment section */
.comment-markdown {
    color: black !important;
}
.dark-theme .is-gray-text {
    color: #443b3b !important;
}
a {
    color: #1a53e5 !important;
}
a:hover {
    color: blue !important;
}

pre code {
    background-color: white !important;
    color: black !important;
}
/* inside comment: replying */
.markdown-editor.is-compact .cm-s-cw-dark {
    background-color: #cecaca !important;
    color: black !important;
}

/* left side-panel */
#sidenav li a{
    color: black !important;
}
#sidenav li a:hover{
    color: #868686 !important;
}

/* top right hover block */
#main_header {
    background-color: white !important;
}
header.main .menu .menu-body {
    background-color: #c3c2c2 !important;
}
#main_header a{
    color: black !important;
}
#main_header a:hover{
    color: #6c6c6c !important;
}
/****** login *******/
.is-red-text {
    color: white !important;
}

/***** code full screen *****/
.has-full-screen-control #code_challenges.full-viewport {
    filter: invert(100%);
}
}
