css:
@-moz-document domain("skribbl.io") {
@font-face {
    font-family: 'Orbitron';
    font-style: normal;
    font-weight: 400;
    src: local('Orbitron Regular'), local('Orbitron-Regular'), url(https://fonts.gstatic.com/s/orbitron/v9/yMJRMIlzdpvBhQQL_Qq7dy0.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
@font-face {
    font-family: 'Montserrat';
    font-style: normal;
    font-weight: 400;
    src: local('Montserrat Regular'), local('Montserrat-Regular'), url(https://fonts.gstatic.com/s/montserrat/v12/JTUSjIg1_i6t8kCHKm459Wlhyw.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
* {
    font-family: 'Montserrat';
}
::-webkit-scrollbar {
    width: 1rem;
}
::-webkit-scrollbar-thumb {
    border: 1px solid #181c25;
    background: #232936;
    -webkit-box-shadow: 0 5px 15px rgba(0, 0, 0, 0.5);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.5);
}
::-webkit-scrollbar-track {
    background: #181c25;
    -webkit-box-shadow: inset 0 0 5px #12171c;
    box-shadow: inset 0 0 5px #12171c;
}
body {
    color: rgba(255, 255, 255, 0.75);
    background: linear-gradient( rgba(18, 23, 28, 0.8), rgba(18, 23, 28, 0.8)), url(https://skribbl.io/res/background.png) center fixed;
    background-size: 256px;
}
.loginPanelTitle,
.lobbySettings .lobbyName,
#boxMessages p:nth-child(even),
#overlay .content .wordContainer .word,
.modal-header {
    background-color: #212833;
    border-width: 0;
}
.loginPanelContent,
.lobbySettings .lobbyContent,
#boxChat,
#containerChat,
.modal-content,
.modal-footer,
#screenGame .gameHeader,
#overlay .content .wordContainer .word:hover {
    background: #181c25 none repeat scroll 0 0;
    border-width: 0;
}
a,
.lobbySettings .lobbyName {
    color: rgba(255, 255, 255, 0.95)!important;
}
a:hover {
    color: rgba(255, 255, 255, 0.75)!important;
}
.updateInfo {
    background: rgba(205, 220, 255, 0.07) none repeat scroll 0 0;
    border-width: 0px;
    border-left: 2px solid rgba(245, 245, 245, 0.1);
    font-size: 13px;
    padding-left: 10px;
    border-radius: 0px;
    color: #fff;
}
.form-control,
#invite {
    -moz-appearance: none;
    -webkit-appearance: none;
    -webkit-border-radius: 0;
    border-radius: 0;
    -webkit-box-shadow: none;
    box-shadow: none;
    background: rgb(42, 51, 62) none repeat scroll 0 0;
    border: 1px solid rgba(97, 119, 147, 0);
    color: #f5f5f5;
    font-size: 12px;
}
#loginLanguage {
    cursor: pointer;
}
#loginAvatarCustomizeContainer {
    background: rgb(42, 51, 62) none repeat scroll 0 0;
    border-color: #3a3e47;
}
.containerSocial .socialItem img {
    filter: brightness(3);
}
.logoBig,
#logoAvatarContainer {
    opacity: .8;
}
#audio,
.modal-header .close {
    filter: invert(80%);
    opacity: .2;
}
#audio:hover {
    opacity: .4;
}
#audio {
    background-image: url("https://cdn.rawgit.com/eXo-Necro/Files/595648fc/skribbl.io/volume-up-solid.svg")!important;
    background-position: center;
    background-repeat: no-repeat;
}
#audio[style*="audio_off"] {
    background-image: url("https://cdn.rawgit.com/eXo-Necro/Files/595648fc/skribbl.io/volume-off-solid.svg")!important;
    background-position: center;
    background-repeat: no-repeat;
    margin-left: -12px;
}
#containerGamePlayers .player:nth-child(odd) {
    background: rgba(205, 220, 255, 0.07);
}
#containerGamePlayers .player:nth-child(even) {
    background: rgba(205, 220, 255, 0.15) none repeat scroll 0 0;
}
#containerGamePlayers .guessedWord:nth-child(odd) {
    background: #568c42!important;
}
#containerGamePlayers .guessedWord:nth-child(even) {
    background: #446d36!important;
}
#containerGamePlayers .player .info,
#containerGamePlayers .player .rank {
    color: rgba(255, 255, 255, 0.75);
}
#containerGamePlayers .player .info .name[style="color: rgb(0, 0, 255);"] {
    color: rgb(242, 193, 97)!important;
}
#boxMessages p {
    color: rgba(255, 255, 255, 0.75)!important;
}
#boxMessages p[style*="color: rgb(206, 79, 10);"] {
    color: #c05d5d!important;
}
#boxMessages p[style*="color: rgb(86, 206, 39);"] {
    color: #5dc283!important;
}
#boxMessages p[style*="color: rgb(57, 117, 206);"] {
    color: #5289bb!important;
}
#boxMessages p[style*="color: rgb(204, 204, 0);"] {
    color: #c49d42!important;
}
#boxMessages p[style*="color: rgb(125, 173, 63);"] {
    color: #3af2d3!important;
}
#containerGamePlayers .player .message .content {
    color: rgba(205, 220, 255, 0.07)!important;
    background-color: rgba(34, 39, 47, .85);
    border: 2px solid rgba(84, 94, 111, .85);
    border-radius: 2px;
}
#containerGamePlayers .player .message .arrow {
    border-right-color: rgba(84, 94, 111, .85);
}
#containerGamePlayers .player .message .content .text {
    color: rgba(205, 220, 255, 0.8);
}
#timer {
    background: #262b38;
    border: 2px solid #3c4558;
    width: 70px;
    height: 30px;
    line-height: 28px;
    border-radius: 2px;
    color: #e74c3c;
    text-shadow: 0 0 15px rgba(231, 76, 60, 0.4);
    font-family: 'Orbitron';
    margin-top: 10px;
    position: relative;
    left: 10px;
    top: 0px;
    font-weight: 100;
}
#round {
    margin-left: 40px;
}
canvas {
    filter: brightness(80%);
}
.btn-warning {
    background-color: #c3934d;
    border-color: #ad8041;
}
#containerGamePlayers .player .info {
    line-height: 19px;
}
}
