css:
@-moz-document domain("wolfkodi.dyndns.org") {
    /*CSS3 Â©Wolfing. All Rights Reserved.*/
    iframe,
    img.center {
        display: block
    }
    
    .acc-menu-item,
    .selector,
    .tab {
        cursor: pointer
    }
    
    #map *,
    .char div img {
        -webkit-user-select: none
    }
    
    body {
        font: 12px Arial, sans-serif;
        color: #222;
        margin: 0;
        padding: 0;
        background: #f4f4f4
    }
    
    img.center {
        margin: auto
    }
    
    
    @-webkit-keyframes logoplay {
        from {
            background-position: 0 0
        }
    
        to {
            background-position: -1620px 0
        }
    }
    
    @-moz-keyframes logoplay {
        from {
            background-position: 0
        }
    
        to {
            background-position: -1620px
        }
    }
    
    @-o-keyframes logoplay {
        from {
            background-position: 0
        }
    
        to {
            background-position: -1620px
        }
    }
    
    @keyframes logoplay {
        from {
            background-position: 0
        }
    
        to {
            background-position: -1620px
        }
    }
    
    .selector {
        position: relative;
        display: inline-block;
        height: 30px;
        width: 30px;
        background: #EEE;
        border-radius: 3px
    }
    
    .selector:hover {
        background: #CDF
    }
    
    .selector .bar-icon-wrapper {
        position: absolute;
        top: 50%;
        left: 50%;
        margin-left: -10px;
        margin-top: -10px;
        -o-transition: transform .4s ease-in-out;
        -ms-transition: transform .4s ease-in-out;
        -moz-transition: transform .4s ease-in-out;
        transition: transform .4s ease-in-out
    }
    
    .selector.active .bar-icon-wrapper {
        -o-transform: rotate(-450deg);
        -ms-transform: rotate(-450deg);
        -moz-transform: rotate(-450deg);
        transform: rotate(-450deg)
    }
    
    .menuhdl-up .selector.active .bar-icon-wrapper {
        -o-transform: rotate(-270deg);
        -ms-transform: rotate(-270deg);
        -moz-transform: rotate(-270deg);
        transform: rotate(-270deg)
    }
    
    .selector .bar-icon {
        height: 3px;
        width: 20px;
        background: rgb(99, 81, 95);
        border-radius: 3px;
        display: block;
        opacity: 1;
        margin: 3px 0;
        -o-transition: width .6s ease-in-out, opacity .6s ease-in-out, transform .6s ease-in-out;
        -ms-transition: width .6s ease-in-out, opacity .6s ease-in-out, transform .6s ease-in-out;
        -moz-transition: width .6s ease-in-out, opacity .6s ease-in-out, transform .6s ease-in-out;
        transition: width .6s ease-in-out, opacity .6s ease-in-out, transform .6s ease-in-out
    }
    
    .acc-menu,
    .acc-menu-arrow-up {
        display: none;
        position: absolute
    }
    
    .selector.active .bar-icon:nth-child(1) {
        -o-transform: rotate(-35deg) translate3d(-3px, 1px, 0);
        -ms-transform: rotate(-35deg) translate3d(-3px, 1px, 0);
        -moz-transform: rotate(-35deg) translate3d(-3px, 1px, 0);
        transform: rotate(-35deg) translate3d(-3px, 1px, 0);
        width: 15px
    }
    
    .selector.active .bar-icon:nth-child(2) {
        opacity: 0
    }
    
    .selector.active .bar-icon:nth-child(3) {
        -o-transform: rotate(35deg) translate3d(-3px, -1px, 0);
        -ms-transform: rotate(35deg) translate3d(-3px, -1px, 0);
        -moz-transform: rotate(35deg) translate3d(-3px, -1px, 0);
        transform: rotate(35deg) translate3d(-3px, -1px, 0);
        width: 15px
    }
    
    .acc-menu-arrow-up {
        z-index: 101;
        bottom: -1px;
        right: 9px;
        width: 0;
        height: 0;
        border-left: 6px solid transparent;
        border-right: 6px solid transparent;
        border-bottom: 8px solid #fff
    }
    
    .menuhdl-up .acc-menu-arrow-up {
        bottom: initial;
        border-top: 8px solid #fff;
        top: -5px;
        border-bottom: none
    }
    
    .acc-menu {
        right: -3px;
        z-index: 100;
        border-radius: 3px;
        transition: opacity .218s;
        background: #fff;
        border: 1px solid #ccc;
        border: 1px solid rgba(0, 0, 0, .2);
        font-size: 12px;
        outline: 0;
        box-shadow: 1px 1px 5px rgba(0, 0, 0, .8)
    }
    
    .menuhdl-up .acc-menu {
        bottom: 37px
    }
    
    .acc-menu-title {
        margin: 2px;
        padding: 4px 6px 0;
        color: #cacaca
    }
    
    .acc-menu-title::after {
        content: '';
        position: absolute;
        left: 0;
        display: block;
        width: 100%;
        border-bottom: 2px solid
    }
    
    .acc-menu-item {
        position: relative;
        color: #888;
        list-style: none;
        margin: 0;
        padding: 6px 20px;
        white-space: nowrap
    }
    
    .acc-menu-item:hover {
        background: #CDF
    }
    
    .acc-menu a {
        color: inherit
    }
    
    .acc-menu a:hover {
        text-decoration: none
    }
    
    .slideshow-wrapper {
        width: 300px;
        text-align: center;
        margin: 0 auto;
        display: inline-block
    }
    
    .slide-tip-a,
    .slide-tip-b {
        content: '';
        display: block;
        width: 0;
        border-style: solid;
        border-width: 0 20px 15px 0;
        position: absolute
    }
    
    .slide-tip-a {
        border-color: transparent #FFF
    }
    
    .slide-tip-b {
        top: 1px;
        left: 3px;
        border-color: transparent #FFF
    }
    
    .slideshow {
        width: 300px;
        height: 145px;
        background-color: #fff;
        border-radius: 10px;
        border: 1px solid #FFF;
        overflow: hidden;
        box-shadow: 0 2px 5px 0 rgba(0, 0, 0, .1)
    }
    
    .slider {
        width: 15000px
    }
    
    .slide {
        width: 300px;
        float: left;
        display: table;
        height: 135px
    }
    
    .slide-vc {
        display: table-cell;
        vertical-align: middle;
        color: #cacaca;
        font: 700 15px sans-serif;
        padding: 15px
    }
    
    .tab {
        background: #fff;
        width: 10px;
        text-align: block;
        padding: 5px 0;
        display: inline-block;
        border-radius: 25%;
        border: 1px solid #c5c5c5;
        -webkit-transition: all .5s ease;
        -moz-transition: all .5s ease;
        -o-transition: all .5s ease;
        transition: all .5s ease
    }
    
    .tab-active {
        background: #FFF;
        color: #fff
    }
    
    .line,
    .line-small {
        background-color: #d3d3d3
    }
    
    .hvcenter,
    .hvcflexcnt {
        margin: auto;
        text-align: center;
        color: #cacaca
    }
    
    .hvcenter {
        position: absolute;
        top: 0;
        left: 0;
        bottom: 0;
        right: 0;
        height: 0;
        padding: 2px 5px;
        font: 700 30px sans-serif
    }
    
    .hvcenter.autoh {
        top: 50%;
        bottom: auto;
        height: auto;
        -webkit-transform: translateY(-50%);
        -ms-transform: translateY(-50%);
        -o-transform: translateY(-50%);
        transform: translateY(-50%);
        line-height: 100%
    }
    
    .hvcflexbox {
        display: -webkit-inline-flex;
        display: -ms-inline-flex;
        display: inline-flex;
        height: 100%;
        width: 100%
    }
    
    .hvcflexcnt {
        height: auto;
        width: auto;
        padding: 30px;
        font: 700 30px sans-serif
    }
    
    .borderdashbig {
        border: 6px dashed;
        border-radius: 6px;
        box-shadow: inset 0 0 25px rgba(0, 0, 0, .5)
    }
    
    .heading {
        color: rgb(107, 85, 119);
        font: 700 22px sans-serif;
        margin-bottom: 20px
    }
    
    .heading-small {
        color: rgb(112, 85, 119);
        font: 700 15px sans-serif;
        margin-bottom: 15px
    }
    
    .heading-tiny {
        color: rgb(108, 85, 119);
        font: 700 11px sans-serif;
        margin-bottom: 5px
    }
    
    .heading-description {
        position: relative;
        top: -20px;
        color: rgb(184, 170, 187);
        font: 700 16px sans-serif
    }
    
    .heading-small-description {
        position: relative;
        top: -15px;
        color: rgb(170, 153, 169);
        font: 700 11px sans-serif
    }
    
    .h30 {
        font: 700 30px sans-serif
    }
    
    .h15 {
        font: 700 15px sans-serif
    }
    
    .h12 {
        font: 700 12px sans-serif
    }
    
    .title-small {
        color: #567;
        font: 700 15px sans-serif;
        margin-top: 15px
    }
    
    .content-title,
    .content-title * {
        color: #cacaca;
        font: 700 25px sans-serif
    }
    
    .content-title.large {
        font-size: 30px;
        margin-bottom: 25px
    }
    
    .content-view-count {
        margin-left: 20px;
        font-size: 15px;
        opacity: .25
    }
    
    .content-text {
        margin: 5px 0 20px;
        color: #666;
        font-size: 13px;
        line-height: 20px
    }
    
    .content-text-small {
        margin: 4px 0 8px;
        color: #666;
        font-size: 11px;
        line-height: 15px
    }
    
    .content-image-container {
        height: 135px;
        width: 220px;
        margin: 5px 22px 5px 0;
        overflow: hidden;
        display: block;
        float: left
    }
    
    .modal-dialog {
        max-width: 600px
    }
    
    .modal-title {
        font: 26px sans-serif;
        color: #967a81
    }
    
    .modal-content {
        font: 15px sans-serif;
        color: #333
    }
    
    .modal-label {
        font-weight: 700;
        color: #4a434d
    }
    
    .modal-input {
        width: 100%;
        box-sizing: border-box
    }
    
    .modal-textarea {
        width: 100%
    }
    
    .list {
        display: block;
        line-height: normal;
        padding: 15px 0 15px 125px
    }
    
    .list-bullet {
        float: left;
        margin-left: -115px;
        font: 700 12px sans-serif
    }
    
    .list-small {
        display: block;
        line-height: normal;
        padding: 1px 0 1px 80px
    }
    
    .list-bullet-small {
        float: left;
        margin-left: -35px;
        font: 700 12px sans-serif
    }
    
    .cmt-btn,
    .cmt-header {
        font-weight: 700
    }
    
    .profile-moderate {
        text-align: right
    }
    
    .profile-moderate a {
        margin-left: 10px;
        font-size: 10px
    }
    
    .line {
        height: 2px
    }
    
    .line-small {
        height: .1em
    }
    
    .line-space {
        display: block;
        height: 10px
    }
    
    .cmt-avt {
        float: left
    }
    
    img[src*="/wolfing/img/wolfing_character.png"] {
        background-image: url("https://i.imgur.com/vlmiitf.gif");
        width: 201px;
        display: inline-block;
        padding: 190px 0 0 0;
        height: 0px
    }
    
    .cmt-tip {
        position: relative;
        left: 54px;
        top: 17px
    }
    
    .cmt-tip-r {
        position: relative;
        left: 34px;
        top: 12px
    }
    
    .cmt-tip-a {
        border: 7px solid #FFF;
        left: 2px;
        top: 1px;
        z-index: 1
    }
    
    .cmt-tip-b {
        border: 6px solid #d5d5d5;
        left: 0;
        top: 0
    }
    
    .cmt-tip-a,
    .cmt-tip-b {
        border-bottom-color: transparent;
        border-left-color: transparent;
        position: absolute
    }
    
    .cmt-att,
    .cmt-clr {
        height: 24px;
        border: 0;
        margin: 0;
        padding: 0;
        position: absolute;
        float: right;
        cursor: pointer
    }
    
    .cmt-clr {
        background: url(https://i.imgur.com/2H0JXKK.png) 0 -144px no-repeat;
        width: 24px;
        right: 2px;
        top: 2px;
        display: none
    }
    
    .cmt-clr.cmt-att-clr {
        right: 10px;
        top: 10px
    }
    
    .cmt-att {
        background: url(https://i.imgur.com/2H0JXKK.png) 0 -205px no-repeat;
        width: 12px;
        right: 26px;
        top: 14px
    }
    
    .cmt-box,
    .cmt-box-att {
        background-color: #fff;
        margin-left: 65px
    }
    
    .cmt-box {
        min-height: 40px;
        border: 1px solid #ddd;
        border-top: 1px solid #d5d5d5;
        border-radius: 2px;
        padding: 5px 42px 5px 10px;
        font-size: 13px;
        white-space: pre-wrap
    }
    
    .cmt-box-att {
        border: 1px solid #ddd;
        border-top: 1px solid #d5d5d5;
        border-radius: 2px;
        padding: 10px;
        display: none;
        text-align: center
    }
    
    .cmt-img {
        max-width: 100%;
        max-height: 600px;
        border-radius: 5px;
        margin-top: 5px
    }
    
    .ui-autocomplete,
    span.bubble {
        max-height: 300px;
        overflow-x: hidden
    }
    
    [contenteditable=true]:empty:not(:focus):before,
    [name=guestname]:empty:not(:focus):before {
        content: attr(data-ph);
        color: silver
    }
    
    .cmt-disabled {
        color: silver
    }
    
    .cmt-btn {
        -webkit-border-radius: 2px;
        border-radius: 2px;
        cursor: pointer;
        font-size: 12px;
        text-align: center;
        white-space: nowrap;
        min-width: 54px;
        height: 28px;
        line-height: 28px;
        outline: 0;
        padding: 0 8px;
        background-color: #97889b;
        color: #fff;
        float: right;
        display: none
    }
    
    .cmt-btn:hover {
        background-color: #cec0c5
    }
    
    .cmt,
    .cmt-reply,
    .cmt-reply-reply {
        padding-bottom: 20px
    }
    
    .cmt-reply,
    .cmt-reply-reply {
        margin-left: 60px
    }
    
    .cmt-content,
    .cmt-content-att,
    .cmt-footer,
    .cmt-header {
        margin-left: 60px;
        font-size: 13px;
        line-height: 1.31;
        color: #555
    }
    
    .cmt-footer,
    .cmt-time {
        font-size: 11px;
        color: #999
    }
    
    .cmt-time {
        margin-left: 10px;
        font-weight: 400
    }
    
    .cmt-content,
    .cmt-content-att {
        overflow: hidden;
        white-space: pre-wrap
    }
    
    .cmt-footer {
        font-weight: 700
    }
    
    .cmt-opt {
        background: url(https://i.imgur.com/D69khtm.png) 0 -120px no-repeat;
        float: right;
        width: 14px;
        height: 14px;
        cursor: pointer;
        opacity: .3
    }
    
    .cmt-like-count {
        font-weight: 400;
        margin-left: 15px;
        color: #92868b
    }
    
    .cmt-like-count-neg {
        color: red
    }
    
    .cmt-dislike,
    .cmt-like {
        background: url(https://i.imgur.com/D69khtm.png) no-repeat;
        margin-left: 15px;
        display: inline-block;
        width: 17px;
        height: 18px;
        cursor: pointer;
        position: relative;
        top: 5px
    }
    
    .cmt-like {
        background-position: 0 -40px;
        top: 4px;
        opacity: .5
    }
    
    .cmt-dislike {
        background-position: 0 -60px;
        top: 6px;
        margin-left: 8px;
        opacity: .5
    }
    
    .cmt-like:hover {
        background-position: 0 -20px
    }
    
    .cmt-dislike:hover {
        background-position: 0 -80px
    }
    
    .cmt-like-s,
    .cmt-like-s:hover {
        background-position: 0 0;
        opacity: 1
    }
    
    .cmt-dislike-s,
    .cmt-dislike-s:hover {
        background-position: 0 -100px;
        opacity: 1
    }
    
    .cmt-like-info {
        margin-left: 10px;
        color: #deb0d3;
        font-weight: 400
    }
    
    .cmt-loadmorecomments,
    .cmt-sh {
        font-weight: 700;
        cursor: pointer
    }
    
    .cmt-like-info:hover {
        color: #997796
    }
    
    .clear:focus .cmt-opt,
    .clear:hover .cmt-opt {
        opacity: .7
    }
    
    .clear:focus .cmt-opt:focus,
    .clear:hover .cmt-dislike,
    .clear:hover .cmt-like,
    .clear:hover .cmt-opt:hover {
        opacity: 1
    }
    
    a.cmt-r-link {
        color: #999;
        cursor: pointer
    }
    
    a.cmt-r-link:hover {
        color: #cacaca
    }
    
    a.cmt-eh {
        color: #999;
        cursor: pointer
    }
    
    .cmt-readmore,
    a.cmt-eh:hover,
    div[onclick="cmtSH(this)"] {
        color: #cacaca
    }
    
    .cmt-readmore:hover,
    div[onclick="cmtSH(this)"]:hover {
        text-decoration: underline
    }
    
    #chatbox div div a:hover,
    .a-no-u:hover,
    .a-opt,
    .a-opt:hover,
    .cmt-loadmorecomments:hover,
    .icon-bg,
    .sidebar-link-current:hover,
    .sidebar-link:hover,
    .ui-datepicker a,
    .ui-menu .ui-menu-item a,
    a {
        text-decoration: none
    }
    
    .cmt-sh {
        margin-left: 60px;
        font-size: 13px;
        margin-bottom: 10px
    }
    
    .cmt-sh-icon {
        background: url(https://i.imgur.com/D69khtm.png) 0 -134px no-repeat;
        margin-left: 10px;
        display: inline-block;
        width: 12px;
        height: 8px
    }
    
    .cmt-sh-icon-v {
        background-position: 0 -142px
    }
    
    .cmt-readmore {
        margin-left: 60px;
        cursor: pointer;
        font-size: 13px
    }
    
    .cmt-r {
        margin-left: 45px
    }
    
    label[for=guestname] {
        float: left;
        margin-top: 5px;
        font-size: 16px
    }
    
    .cmt-guestname {
        display: block;
        min-height: 16px;
        margin-left: 65px
    }
    
    .cmt-opt-menu,
    .optMenu {
        position: absolute;
        top: 13px;
        right: 0;
        z-index: 100;
        display: none;
        -webkit-border-radius: 3px;
        border-radius: 3px;
        -webkit-transition: opacity .218s;
        transition: opacity .218s;
        background: #fff;
        border: 1px solid #ccc;
        border: 1px solid rgba(0, 0, 0, .2);
        font-size: 13px;
        outline: 0;
        box-shadow: 1px 1px 5px rgba(0, 0, 0, .8)
    }
    
    .optMenu {
        top: 33px;
        z-index: 10000
    }
    
    .cmt-opt-menu-item,
    .optMenuItem {
        position: relative;
        color: #333;
        cursor: pointer;
        list-style: none;
        margin: 0;
        padding: 6px 60px 6px 30px;
        white-space: nowrap
    }
    
    .cmt-opt-menu-item:hover,
    .optMenuItem:hover {
        background-color: #f5f5f5
    }
    
    .mod {
        color: brown
    }
    
    .cmt-loadmorecomments {
        background-color: #f8f8f8;
        background-image: -webkit-linear-gradient(top, #fcfcfc 0, #f8f8f8 100%);
        background-image: linear-gradient(top, #fcfcfc 0, #f8f8f8 100%);
        border: 1px solid #d3d3d3;
        color: #333;
        font-size: 12px;
        display: block;
        padding: 2px 0;
        text-align: center
    }
    
    .cmt-loadmorecomments:hover {
        background-color: #f0f0f0;
        background-image: -webkit-linear-gradient(bottom, #f0f0f0 0, #f8f8f8 100%);
        background-image: linear-gradient(bottom, #f0f0f0 0, #f8f8f8 100%);
        border-color: #c6c6c6
    }
    
    .clear:after {
        content: ".";
        display: block;
        clear: both;
        visibility: hidden;
        line-height: 0;
        height: 0
    }
    
    .clear {
        position: relative;
        zoom: 1
    }
    
    .cmt-deleted {
        opacity: .3
    }
    
    .cmt-deleted:hover {
        opacity: 1
    }
    
    .cmt-private {
        background-color: #e8e8e8
    }
    
    .cmt-pending-approval {
        background-color: #e8d8c8
    }
    
    .cmt-mod-rmv {
        background-color: #fdc
    }
    
    .container-video {
        max-width: 600px;
        margin: 10px
    }
    
    .container-widescreen {
        position: relative;
        padding-bottom: 56.25%;
        height: 0
    }
    
    .container-content {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%
    }
    
    textarea {
        font: 12px Arial, sans-serif
    }
    
    table {
        border: 0;
        border-collapse: collapse;
        width: 100%
    }
    
    div.d-cm,
    table.t-cm {
        text-align: center;
        vertical-align: middle
    }
    
    table.t-list {
        font-size: 12px;
        width: 1%;
        white-space: nowrap
    }
    
    .user-about,
    span.bubble {
        white-space: pre-wrap;
        word-break: break-word
    }
    
    table.t-list th {
        font-size: 10px;
        padding: 0 5px;
        color: #fff;
        background: #999
    }
    
    table.t-list tr:hover {
        background: #e8e8e8
    }
    
    table.t-list td {
        border: .1em solid #dcdcdc
    }
    
    form,
    p,
    span {
        margin: 0;
        padding: 0
