    .user-about {
        padding: 25px 0
    }
    
    .user-about img {
        max-width: 100%
    }
    
    .spoiler {
        border: 1px dotted #d3d3d3;
        color: grey;
        padding: 2px;
        background: #e0e3e8
    }
    
    .spoiler:not(.expnded)::before {
        content: '>';
        padding: 0 8px
    }
    
    .spoiler:not(.expnded)::after {
        content: '  //show'
    }
    
    .spoiler.expnded::before {
        content: 'v';
        padding: 0 8px
    }
    
    .spoiler.expnded::after {
        content: '  //hide'
    }
    
    .spoiler-cnt {
        display: none;
        border-left: 1px solid #d3d3d3;
        border-bottom: 1px solid #d3d3d3;
        border-right: 1px solid #d3d3d3;
        padding: 2px
    }
    
    .spoiler-cnt::after {
        content: ".";
        display: block;
        clear: both;
        visibility: hidden;
        line-height: 0;
        height: 0
    }
    
    i.pinned,
    i.sniff {
        width: 16px;
        height: 16px
    }
    
    img.shadow {
        box-shadow: 1px 1px 5px grey
    }
    
    img.fl {
        float: left;
        margin: 5px 22px 22px 0
    }
    
    #login,
    #register,
    img.fr {
        float: right
    }
    
    img.fr {
        margin: 5px 0 22px 22px
    }
    
    img.fl.s {
        margin: 5px 5px 5px 0
    }
    
    img.fr.s {
        margin: 5px 0 5px 5px
    }
    
    i.sniff {
        background: url(https://i.imgur.com/2H0JXKK.png) -30px -58px no-repeat;
        display: inline-block
    }
    
    i.pinned {
        background: url(https://i.imgur.com/2H0JXKK.png) -30px -76px no-repeat;
        display: inline-block;
        font-style: normal;
        vertical-align: middle
    }
    
    i.erasing,
    i.typing {
        height: 12px;
        font-style: normal;
        vertical-align: middle;
        display: inline-block
    }
    
    i.typing {
        width: 12px;
        background: url(https://i.imgur.com/2H0JXKK.png) -10px -191px no-repeat;
        -webkit-animation: typing .3s steps(2) infinite;
        -moz-animation: typing .3s steps(2) infinite;
        -o-animation: typing .3s steps(2) infinite;
        animation: typing .3s steps(2) infinite
    }
    
    @-webkit-keyframes typing {
        from {
            background-position: -10px -191px
        }
    
        to {
            background-position: -38px -191px
        }
    }
    
    @-moz-keyframes typing {
        from {
            background-position: -10px -191px
        }
    
        to {
            background-position: -38px -191px
        }
    }
    
    @-o-keyframes typing {
        from {
            background-position: -10px -191px
        }
    
        to {
            background-position: -38px -191px
        }
    }
    
    @keyframes typing {
        from {
            background-position: -10px -191px
        }
    
        to {
            background-position: -38px -191px
        }
    }
    
    i.erasing {
        width: 17px;
        background: url(https://i.imgur.com/2H0JXKK.png) -62px -156px no-repeat;
        -webkit-animation: erasing .3s steps(2) infinite;
        -moz-animation: erasing .3s steps(2) infinite;
        -o-animation: erasing .3s steps(2) infinite;
        animation: erasing .3s steps(2) infinite
    }
    
    .ui.cross,
    .ui.tick,
    .ui.white-tick {
        width: 19px;
        height: 19px
    }
    
    @-webkit-keyframes erasing {
