@-moz-document url("https://wolfkodi.dyndns.org/wolfing/r/Northern-Lights"), url("https://wolfkodi.dyndns.org/wolfing/r/Howling-Peaks") {
#input div.inputfield {
    margin-right: 120px;
}
}

@-moz-document domain("wolfkodi.dyndns.org") {
.noti-count,
.noti-dot {
    position: absolute;
    background-color: #ecbcd8;
    background-image: linear-gradient(#e18ca7, #4f374c);
}
.noti-count {
    top: -5px;
    right: -3px;
    min-height: 12px;
    padding: 1px 3px;
    font: 700 10px sans-serif;
    color: #fff;
    text-align: center;
    border: 1px solid #fff;
    border-radius: 2px;
    animation: glow 1s infinite alternate;
    box-shadow: 0 1px 1px rgba(0, 0, 0, .7);
    text-shadow: 0 -1px 0 rgba(0, 0, 0, .4);
}
@keyframes glow {
    from {
        box-shadow: 0 0 3px -3px #cb4437;
    }
    to {
        box-shadow: 0 0 3px 3px #fff;
    }
}
.status.big {
    width: 12px;
    height: 12px;
    box-shadow: 0 0 0 3px #242424
}
.status.good {
    background-color: #228b22;
    animation: glowy 1s infinite alternate;
}
@keyframes glowy {
    from {
        box-shadow: 0 0 3px -3px #28a028;
    }
    to {
        box-shadow: 0 0 3px 3px #28a028;
    }
}

.status.warn {
    background-color: #d2691e;
    animation: glower 1s infinite alternate;
}
@keyframes glower {
    from {
        box-shadow: 0 0 3px -3px #e67321;
    }
    to {
        box-shadow: 0 0 3px 3px #e67321;
    }
}

.status.bad {
    background-color: #b22222;
    animation: glowhom 1s infinite alternate;
}
@keyframes glowhom {
    from {
        box-shadow: 0 0 3px -3px #db2929;
    }
    to {
        box-shadow: 0 0 3px 3px #db2929;
    }
}
}

@-moz-document url("https://wolfkodi.dyndns.org/wolfing/") {
body.night-mode .heading:after {
    content: ' BETAv.06';
}
}
