}
#forum-fr {
    padding: 3px;
    border-collapse: separate;
    border-spacing: 3px;
    margin-left: 3px !important;
    width: 730 !important
}
#forum-general {
    padding: 3px;
    border-collapse: separate;
    border-spacing: 3px;
    margin-left: 3px !important;
    width: 730 !important
}
.news {
